INSERT INTO term(term_name, term_description, term_source) VALUES
('Абаши (абаш)', 'Лиственная порода, произрастающая в западной части экваториальной Африки. Цвет древесины: кремово-белый переходящий к бледно-желтому. Низкие теплопроводность и теплоемкость, высокая водостойкость, отсутствие сучков и выделяемой смолы, легкость, пластичность, прочность, способность длительное время сохранять первоначальный внешний вид способствуют широкому применению древесины абаши для изготовления контактирующих с кожей человека деталей интерьера саун.', 'stroit.ru'),
('Абразивный инструмент', 'Абразивный инструмент - инструмент, изготовленный из абразивных материалов. Абразивный инструмент выпускается в виде шлифовальных кругов, брусков, лент и т. д.', 'stroit.ru'),
('Абразивы', 'Вещества, обладающие высокой твердостью (алмаз, корунд, наждак, карбид кремния и др.). Абразивы используются для механической обработки (шлифования, полировки) поверхностей различных материалов. Абразивы имеют разную твердость, форму, размеры зерен и абразивную способность.', 'stroit.ru'),
('Автобетононасос', 'Бетононасос (машина для транспортирования бетонной смеси по трубопроводам (шлангам) к месту ее укладки), смонтированный на автомобильном шасси.', 'stroit.ru'),
('Автогрейдер', 'Самоходная колесная машина. Основной рабочий орган – приводимый в действие от двигателя полноповоротный отвал криволинейного профиля с механическим или гидравлическим управлением. Может оснащаться сменным оборудованием бульдозера, погрузчика, снегоочистителя и других машин. Автогрейдеры применяются для профилирования земляных насыпей, перемещения и разравнивания грунтов и строительных материалов при сооружении, реконструкции и ремонте автомобильных дорог, аэродромных покрытий, мелиоративных сооружений и т. д.', 'stroit.ru'),
('Автоклав', 'Аппарат для проведения различных процессов при нагреве под давлением выше атмосферного (как правило, до 500 град. Цельсия и 1500 кгс/кв. см), благодаря чему достигается ускорение реакции и увеличение выхода продукта. Представляет собой сосуд емкостью от нескольких десятков куб. см до сотен метров кубических. Автоклавы бывают вращающимися, качающимися, горизонтальными, вертикальными, колонными. Применяются в химической, резиновой (для вулканизация технических изделий), пищевой промышленности; в гидрометаллургии; производстве стройматериалов (для термовлажностной обработки силикатного кирпича и силикатных изделий из ячеистого бетона.', 'stroit.ru'),
('Автоклав проходной', 'Один из видов автоклавов (наряду с тупиковым). Оборудован двумя открывающимися торцевыми крышками, имеет двухстороннюю загрузку и выгрузку.', 'stroit.ru'),
('Автоклав тупиковый', 'Один из видов автоклавов (наряду с проходным). Оборудован одной торцевой крышкой, имеет одностороннюю загрузку и выгрузку.', 'stroit.ru'),
('Авторский надзор', 'Контроль со стороны авторов проекта за соответствием строящегося объекта проектно-сметной документации. Авторский надзор осуществляется на протяжении всего периода строительства и приемки объекта.', 'stroit.ru'),
('Агломерат', 'В геологии – рыхлые скопления неправильных очертаний обломков горных пород, при цементации образующие брекчии, туфы и т. д. Агломерат лавовый — скопление глыб очень вязкой лавы, образовавшейся при движении лавового потока. В металлургическом производстве – спекшаяся в куски мелкая (часто пылевидная) руда размерами до 100 мм с незначительным содержанием мелочи.', 'stroit.ru'),
('Агломерация', 'В металлургическом производстве: термический процесс окускования мелких материалов, путем их спекания с целью придания формы и свойств (химического состава, структуры), необходимых для плавки. На практике чаще всего осуществляется на колосниковых решетках, с просасыванием воздуха сверху вниз сквозь лежащую на решётке шихту. Впервые промышленное производство агломерата освоено в начале 20 в. Основные исходные материалы: мелкая сырая руда и ее концентрат, а также топливо (коксовая и антрацитовая мелочь до 3 мм), флюс (известняк и доломит). Более 95% агломерата используется в черной металлургии.', 'stroit.ru'),
('Аглопорит', 'Искусственный пористый заполнитель для легких бетонов – продукт дробления шихты, изготовленной методом агломерации (спекания) из глинистых пород или глиносодержащих отходов добывающей промышленности. Алгопорит применяется для производства аглопоритобетона.', 'stroit.ru'),
('Агрегат насосный', 'Агрегат насосный – совокупность устройств, состоящая обычно из насоса, двигателя и передачи.', 'stroit.ru'),
('Агрегат отопительный', 'Агрегат отопительный – устройство для отопления и вентиляции помещений.', 'stroit.ru'),
('Агрессивная среда', 'В строительстве: среда, воздействие которой вызывает коррозию и, в конечном счете, разрушение строительного материала в изделии или конструкции. Может быть жидкой, газообразной, твердой.', 'stroit.ru'),
('Агротекс', 'Торговая марка укрывного сельскохозяйственного полотна, однородная структура которого обеспечивает создание микроклимата с оптимально поддерживаемой температурой, равномерным распределением осадков и постоянной циркуляцией воздуха. Не препятствует естественному росту растений, способствует более раннему созреванию и повышению урожайности овощей и фруктов.', 'stroit.ru'),
('Адгезия', 'Способность сцепления двух разнородных тел на молекулярном уровне.', 'stroit.ru'),
('Адиабатный процесс', 'Процесс, происходящий в физической системе без теплообмена с окружающей средой. Может быть осуществим в системе, окруженной теплоизолирующей (адиабатной) оболочкой, или должен протекать настолько быстро, чтобы за время процесса не произошло теплообмена между системой и окружающей средой.', 'stroit.ru'),
('Адсорбент', 'Адсорбент – вещество, способное присоединять к своему поверхностному слою молекулы газа и жидкости.', 'stroit.ru'),
('Адсорбция', 'Адсорбция – поглощение некоторыми твердыми телами газов, красок, солей и других веществ из растворов.', 'stroit.ru'),
('Азерит', 'Искусственный заполнитель для легких бетонов. Представляет собой шарообразные стекловидные пористые частицы диаметром 5–40 мм. Азерит применяется также в качестве теплоизоляционной засыпки.', 'stroit.ru'),
('Акведук', 'Водовод в виде арочного моста, в котором стенки и днище лотка являются несущими конструкциями.', 'stroit.ru'),
('Акрилаты', 'Соли и эфиры акриловой кислоты. Акриловые эфиры – основная продукция, производимая из акриловой кислоты (в них перерабатывается более половины производимой в мире акриловой кислоты).', 'stroit.ru'),
('Акт приемки скрытых работ', 'Официальный документ, составляемый после приемки представителями заказчика, подрядчика и авторского надзора выполненных работ, скрываемых последующими работами. Составление такого документа дает право на производство последующих работ.', 'stroit.ru'),
('Акустика строительная', 'Раздел акустики, рассматривающий проблемы звукоизоляции ограждающих конструкций зданий и сооружений.', 'stroit.ru'),
('Акустическая штукатурка', 'Штукатурка, которая изготавливается из различных вяжущих и легких заполнителей (керамзит, пемза и т.п.). Акустическая штукатурка применяется в отделочных работах для увеличения звукопоглощения ограждающих конструкций.', 'stroit.ru'),
('Акустические материалы', 'Материалы, применяемые для защиты от шума в конструкциях зданий различного назначения. Акустические материалы подразделяются на звукопоглощающие и звукоизоляционные. Акустические материалы изготавливаются в виде матов, плит, блоков, ваты или сыпучих веществ (керамзит, вспученный перлит).', 'stroit.ru'),
('Алебастр', 'Алебастр - продукт обжига природного гипса при температуре 120–170 град. С. В строительстве алебастр применяют в виде порошка тонкого помола.', 'stroit.ru'),
('Алевролит', 'Обломочная твердая порода, сцементированная, уплотненная, претерпевшая некоторые диагенетические изменения. Состоит преимущественно из зерен размером от 100 до 10 мкм.', 'stroit.ru'),
('Алупласт', 'Профиль для легких перегородок, состоящий из алюминия и пластика.', 'stroit.ru'),
('Альбедо', 'Характеристика отражательных свойств наружных поверхностей зданий и сооружений. Альбедо учитывается при расчете теплопоступления от солнечной радиации внутрь зданий и сооружений.', 'stroit.ru'),
('Альков', 'На арабском – «маленькая комната». Ниша в стене. Может служить спальней, местом расположения внутриквартирной лестницы и т. д.', 'stroit.ru'),
('Алюфом', 'Торговая марка комплексной тепло-, паро- и звукоизоляции с высоким коэффициентом отражения излучаемой тепловой энергии.', 'stroit.ru'),
('Алюцинк', 'Сплав цинка и алюминия (состав алюминий – 55 %, цинк – 43,5 %, кремний – 1,5 %). Впервые получен в 60-х годах XX столетия. Зарекомендовал себя в качестве высоко эффективной защиты от коррозии.', 'stroit.ru'),
('Амортизатор', 'Устройство для защиты от ударных нагрузок и сотрясений в машинах и сооружениях. Располагается между телом, передающим ударную нагрузку, и защищаемым телом. В конструкциях амортизаторов используются упругие свойства твердых тел (рессоры, торсионы, резиновые элементы), жидкостей (т. н. "гидравлические пружины") и газов (пневматические пружины).', 'stroit.ru'),
('Ангидрит', 'Сульфат кальция. В природе ангидрит – минерал или осадочная горная порода, в основном состоящая из этого минерала. Растворимый, нерастворимый и высокотемпературный ангидриты являются компонентами гипсовых и ангидритовых вяжущих веществ (ангидритовый цемент, отделочный ангидритовый цемент, высокообжиговый гипс). Ангидрит используется в качестве поделочного камня.', 'stroit.ru'),
('Ангоб', 'Декоративное керамическое покрытие (тонкий слой из глинистой суспензии), наносимое на поверхность керамического изделия и закрывающее цвет или грубую структуру его материала. Может быть белым или цветным. Применяется в производстве цветного кирпича и облицовочной плитки.', 'stroit.ru'),
('Андезит', 'Вулканическая горная порода, состоящая в основном из плагиоклаза и одного или нескольких цветных минералов. Андезит применяется для изготовления кислотоупорных материалов.', 'stroit.ru'),
('Анемометр', 'Прибор для измерения скорости ветра и газовых потоков по числу оборотов вращающейся под действием ветра вертушки. Регистрирующие анемометры носят название анемографов.', 'stroit.ru'),
('Анемостат', 'Конечный воздухотехнический элемент для распределения (подачи и отвода) воздуха.', 'stroit.ru'),
('Анкер', 'Крепежная деталь механическим или химическим способом способствующая соединению строительных изделий и конструкций и креплению оборудования. Также анкером называют деталь, частично забетонированную в монолитную конструкцию или заложенную в кирпичную кладку.', 'stroit.ru'),
('Анкер (анкерный болт)', 'Анкер - крепежная деталь, предназначенная для соединения различных строительных изделий и конструкций, а также крепления оборудования. Термин анкер применяется также в смысле "промежуточная деталь" (анкерная связь, анкерная плита).', 'stroit.ru'),
('Анкер распорный', 'Анкер с внутренней резьбой. В процессе ввинчивания в него вставного элемента разжимается и надежно закрепляется в гнезде. Используется преимущественно для твердых оснований.', 'stroit.ru'),
('Анод', 'В химических источниках тока (гальванических элементах или электрических аккумуляторах) – положительный электрод. А также: электрод электронного (ионного) прибора, соединяемый с положительным полюсом источника; положительный полюс электролитной ванны электрического тока: положительный электрод электрический дуги. В зависимости от назначения аноды выполняются металлическими (тантал, молибден, никель, медь, железо, вольфрам и др.) или неметаллическими (графит).', 'stroit.ru'),
('Антик', 'Произведения искусства античной эпохи – керамика, вазы, скульптура и т. п.', 'stroit.ru'),
('Антипирены', 'Антипирены - вещества, предохраняющие древесину и другие материалы органического происхождения от воспламенения и самостоятельного горения. Антипирены содержат замедлители горения (фосфаты аммония, бура, хлористый аммоний), сипергисты (вещества, усиливающие действие основного замедлителя) и стабилизаторы, ограничивающие расход замедлителя.', 'stroit.ru'),
('Антисептики', 'Антисептики - вещества, применяющиеся для предохранения строительных материалов органического происхождения от биоповреждений (воздействия бактерий, грибов и др.)', 'stroit.ru'),
('Антифриз', 'Антифриз - охлаждающая жидкость для двигателей внутреннего сгорания. Представляет собой смесь воды с этиленгликолем. В зависимости от марки антифриз имеет температуру замерзания –40 или –65 град. С. Антифризы различных марок отличаются также по цвету.', 'stroit.ru'),
('Античные лаки', 'Античные лаки - разновидность ангобов. При обжиге изделия в окислительной среде отстой белой глины позволяет получить белый цвет, при наличии в глине окислов железа – красный. При восстановительном обжиге оба состава дают черный цвет. Античные лаки используются для вазовой живописи. В античном мире античные лаки широко применялись для росписи и сплошного покрытия керамических изделий.', 'stroit.ru'),
('Антрацит', 'Ископаемый гумусовый уголь высшей степени метаморфизма. Залегает в виде пластов различной мощности, главным образом средней и малой. Используется как высококачественное энергетическое топливо, в литейном и доменном процессах, а также для изготовления электродов и полупроводников.', 'stroit.ru'),
('Антресоль', 'Верхний полуэтаж, встроенный в объем основного этажа. Антресоли характерны для особняков и усадебных домов 18-19 вв. В современном «звучании» – полка под потолком квартиры.', 'stroit.ru'),
('Анфилада', 'Ряд помещений, площадей, дворов, парковых ландшафтных структур, последовательно примыкающих друг к другу по одной прямой и имеющих единую открытую сквозную перспективу. Прием характерный для барокко и классицизма.', 'stroit.ru'),
('Анфиладная планировка', 'См. планировка анфиладная', 'stroit.ru'),
('Арболит', 'Арболит - разновидность легкого бетона, заполнителем в котором являются разной крупности частицы растительного происхождения, а вяжущим – цемент, строительный гипс и т. д. По назначению арболит подразделяют на теплоизоляционный и конструкционно-теплоизоляционный.', 'stroit.ru'),
('Аргон', 'Газ без цвета, запаха и вкуса. Химический элемент VIII гр. периодической системы Менделеева (инертный газ). Впервые получен в 1894 году. В природе присутствует только в свободном виде. В промышленности аргон получают в процессе разделения воздуха при глубоком охлаждении. Аргон широко используется при термической обработке легко окисляющихся металлов (так в защитной атмосфере аргона проводят сварку и резку различных редких и цветных металлов).', 'stroit.ru'),
('Ареометр', 'Прибор для измерений плотности жидкостей и твердых тел, действие которого основано на законе Архимеда. По глубине погружения ареометра (т. е. объему вытесненной им жидкости) и его весу можно определить плотность исследуемой жидкости. На практике применяют ареометры двух типов: постоянного веса и постоянного объема.', 'stroit.ru'),
('Арка', 'Криволинейное перекрытие проемов в стене или пролетов между опорами.', 'stroit.ru'),
('Аркада', 'Ряд одинаковых по форме и размеру арок, опирающихся на колонны или столбы. Чаще всего применяется при устройстве открытых галерей.', 'stroit.ru'),
('Арктилит', 'Арктилит - слоистый пластик, представляющий собой чередование слоев березового шпона, ткани и металлической сетки. Склеивание слоев осуществляется с помощью фенолформальдегидной смолы. Арктилит применяется в судостроении.', 'stroit.ru'),
('Арматура', 'Арматура - составная часть железобетонной конструкции, предназначенная для восприятия растягивающих усилий. Обычно применяют стальную арматуру, в некоторых случаях – неметаллическую арматуру.', 'stroit.ru'),
('Арматура спускная', 'Устройство, приводимое в действие вручную или автоматически, обеспечивающее слив воды. из смывного бачка в унитаз (напольную чашу).', 'stroit.ru'),
('Армоцемент', 'Армоцемент - мелкозернистый бетон, в массе которого равномерно распределены проволочные стальные сетки. Одновременно может присутствовать и стержневая или проволочная арматура. Из армоцемента можно изготавливать тонкостенные элементы (речные и морские суда, дебаркадеры, резервуары, трубы).', 'stroit.ru'),
('Архитектура', 'Архитектура - искусство проектирования и строительства сооружений, решающее эстетические и социальные задачи. Архитектура входит в триаду главных искусств: живопись, скульптура, архитектура.', 'stroit.ru'),
('Архитектура ландшафтная', 'Вид искусства, целью которого является организация пространственной среды с помощью природного материала, приводящее к гармонии пластические особенности существующего рельефа.', 'stroit.ru'),
('Асбест', 'Асбест - минералы класса силикатов легко расщепляющиеся на тонкие прочные волокна. Наибольшее промышленное значение имеет хризотил-асбест. Асбест используется при производстве асбоцементных листов, труб и других строительных материалов.', 'stroit.ru'),
('Асбестоцементная панель', 'Изделие, представляющее собой плоскостной строительный элемент, имеющий асбестоцементные наружные поверхности и внутреннее пространство, заполняемое при необходимости изоляционным материалом, предназначенное для использования в вертикальном положении.', 'stroit.ru'),
('Асбозурит', 'Асбозурит - теплоизоляционный материал, состоящий из диатомита (70–85%) и асбестового волокна (15–30%).', 'stroit.ru'),
('Асбокрошка', 'Обработанное асбестовое волокно, состоящее из смеси волокон различной длины. Применяется при производстве асбестотехнических и асбоцементных изделий', 'stroit.ru'),
('Асботкань', 'Ткань асбестовая – полотно из переплетенных асбестовых нитей, содержащих от 5 до 18 % связующего волокна (вискозы, хлопка, лавсана). Используется при изготовлении для прошива жарозащитной одежды и теплозащитных покрытий. Применяется в качестве теплоизоляционного материала в котельных, различном теплотехническом оборудовании.', 'stroit.ru'),
('Асбоцемент', 'Асбоцемент - материал, состоящий из затвердевшего портландцемента и волокон асбеста, выполняющих функцию дискретной арматуры. Наличие волокон асбеста увеличивает прочность асбоцемента и снижает его теплопроводность.', 'stroit.ru'),
('Аспирация', 'Аспирация - процесс удаления пыли и газов, образующихся в процессе работы технологического оборудования из производственных помещений.', 'stroit.ru'),
('Асфальт', 'Асфальт - искусственное или природное органическое вяжущее. Асфальт применяется главным образом для строительства дорог, а также для устройства полов в промышленных зданиях.', 'stroit.ru'),
('Асфальтобетон', 'Асфальтобетон - строительный материал, получаемый в результате затвердевания уплотненной смеси минеральных заполнителей (щебня, песка, тонкоизмельченного минерального порошка) с органическим вяжущим (битумом или дегтем). Асфальтобетон применяется главным образом для строительства дорог, а также для устройства полов в промышленных зданиях.', 'stroit.ru'),
('Асфальтовая крошка', 'Продукт переработки старых асфальтовых покрытий, представляет из себя крошку с битумными остатками. Применяется в дорожном строительстве.', 'stroit.ru'),
('Атакситовый лед', 'Одна из разновидностей внутригрунтового льда.', 'stroit.ru'),
('Атриум', 'Помещение, высота которого составляет два и более этажей. Может напрямую сообщаться с помещениями, расположенными на этажах здания. Атриумы появились в Древнем Риме, где служили местом для размещения очага.', 'stroit.ru'),
('Аттик', 'Стенка, над венчающим архитектурное сооружение карнизом (часто завершает триумфальные арки). Может быть украшено рельефами или надписями.', 'stroit.ru'),
('АУП', 'Автоматическая установка пожаротушения (АУП). Наряду с установкой пожарной сигнализации (УПС) входит в состав стационарно установленных на объекте и автоматически действующих средств обнаружения и (или) тушения пожара. Предназначена для защиты людей и материальных ценностей от опасных факторов пожара до прибытия подразделений пожарной охраны. Административно-управленческий персонал (АУП) —работники аппарата управления, служащие, входящие в администрацию фирмы, предприятия, организации, конторские работники, дирекция предприятий и учреждений.', 'stroit.ru'),
('Ацетилен', 'Ненасыщенный углеводород. Бесцветный мало растворимый в воде газ. Служит сырьем для синтеза целого ряда ценных промышленных продуктов (например, винилхлорида – исходного вещества для производства пластических материалов). При сжигании ацетилена выделяется большое количество тепла (до 14 000 ккал/куб. м), поэтому ацетилено-кислородное пламя (максимальная температура выше 3000 град. Цельсия) применяют для сварки и резки цветных и черных металлов. Ацетилен хранят и транспортируют в стальных баллонах под давлением в виде ацетонового раствора, поглощенного пористым материалом (например, древесным углем).', 'stroit.ru'),
('Ацэид (ацеид)', 'Асбестоцементные дугостойкие доски (листы) электротехнические. Предназначены для изготовления искрогасительных перегородок, устройства электрораспределительных щитов, оснований электрических машин и аппаратов, облицовки индукционных и тигельных печей. Используется как строительный материал (перекрытия, подоконники. Отличаются чрезвычайно высокой прочностью, устойчивы против загнивания, термостойки, долговечны, стойки к агрессивным средам.', 'stroit.ru'),
('Аэратор', 'Насадок на изливе, образующий аэрированную (водовоздушную) струю.', 'stroit.ru'),
('Аэрированный легкий бетон (АЛБ)', 'Конструктивно-теплоизоляционный бетон, сочетающий в себе свойства легких бетонов на пористых заполнителях и пенобетона. Готовится в скоростном аэросмесителе.', 'stroit.ru'),
('Базальт', 'Базальт - темная вулканическая горная порода, состоящая из плотной или очень мелкозернистой массы, но может содержать порфировые выделения. Базальт хорошо полируется. Базальт используется в качестве бутового камня, наполнителя для бетонов, для мощения улиц, при производстве литых каменных изделий.', 'stroit.ru'),
('Бак расширительный', 'Емкость, находящаяся в системе водяного отопления, предназначенная для приема избытка воды, возникающего при ее нагревании.', 'stroit.ru'),
('Балка', 'Балка - горизонтальная несущая конструкция зданий и сооружений, имеющая опору в двух или более точках. Балка, перекрывающая один пролет и имеющая две опоры, называется разрезной. Балка, перекрывающая несколько пролетов и имеющая несколько опор, называется неразрезной многопролетной.', 'stroit.ru'),
('Балка неразрезная', 'Балка неразрезная – балка, опирающаяся на несколько опор.', 'stroit.ru'),
('Балкон', 'Балкон – консольно выступающая площадка на фасаде здания, огражденная перилами.', 'stroit.ru'),
('Балюстрада', 'Балюстрада - ограждение крыш, лестниц, галерей, балконов в виде перил с невысокими фигурными стойками (балясинами).', 'stroit.ru'),
('Балясины', 'Балясины - элементы ограждающих конструкций лестниц, балконов, террас, поддерживающие перила.', 'stroit.ru'),
('Бардолин', 'Бардолин - битумная черепица, центральный слой которой — упругое стекловолокно. Бардолин пропитан с двух сторон битумом, верхний слой имеет минеральную посыпку, нижний слой — кремниевый песок.', 'stroit.ru'),
('Бачок смывной', 'Безнапорный, аккумулирующий воду резервуар, наполняемый автоматически из водопроводной сети и опорожняемый вручную или автоматически для обеспечения смыва унитаза (напольной чаши).', 'stroit.ru'),
('Бегунец', 'Бегунец – тип кирпичной кладки в виде пояса, образующего на поверхности стены ряд треугольных углублений, попеременно обращенных вершинами вверх и вниз.', 'stroit.ru'),
('Бельведер', 'Бельведер – круглая в плане надстройка над зданием. Беседка или павильон, как правило, на возвышенном месте.', 'stroit.ru'),
('Бельтинг', 'Тяжелая, очень плотная и прочная техническая ткань, получаемая из крученой хлопчатобумажной пряжи, иногда с применением химических волокон. Используется для изготовления конвейерных лент и приводных ремней.', 'stroit.ru'),
('Бензин', 'Продукт перегонки нефти, представляющий собой смесь легких углеводородов с температурой кипения от 30 до 205 град. С. Применяется как топливо для карбюраторных двигателей и как растворитель.', 'stroit.ru'),
('Берма', 'Уступы на откосах гидротехнических сооружений: земляных и каменных плотинах (берма плотины – площадка с незначительным уклоном для отвода вод), каналов, укрепленных берегов и т. д., служащие для придания устойчивости вышележащих частей сооружений, а также улучшения условий их эксплуатации. Бермами также называют уступы в карьерах. Бермы шириной до 15 м, используются для размещения транспортных коммуникаций (например, автомобильных дорог), соединяющих забои с поверхностью, бермы шириной до 10 м служат для периодического удаления осыпающихся горных пород, а предохранительные бермы шириной до 5 м – для предотвращения обрушения бортов карьеров.', 'stroit.ru'),
('Бетон жирный', 'См. «жирный бетон».', 'stroit.ru'),
('Бетон тощий', 'См. «тощий бетон».', 'stroit.ru'),
('Бетон ячеистый', 'Бетон ячеистый – бетон, получаемый в результате затвердевания вспученной при помощи порообразователя (газобетон) или вспененной смеси вяжущего (пенобетон), кремнезистого компонента и воды.', 'stroit.ru'),
('Бетоновоз', 'Специальный автомобиль, оборудованный емкостью для перевозки бетонной смеси.', 'stroit.ru'),
('Бетононасос', 'Машина для транспортирования бетонной смеси по трубопроводам (шлангам) к месту ее укладки. Различают станционарные и автобетонасосы (бетононасосы, смонтированные на автомобильном шасси).', 'stroit.ru'),
('Бетонополимеры', 'Специальные бетоны на минеральном вяжущем, пропитанные монополимерами с их последующим отверждением.', 'stroit.ru'),
('Бетоносмеситель', 'Бетоносмеситель – строительный механизм, предназначенный для приготовления бетонной смеси посредством перемешиванием ее компонентов (вяжущего, заполнителей и воды).', 'stroit.ru'),
('Бетоны', 'Группа строительных материалов, представляющая собой искусственный камень, состоящий из затвердевшей смеси вяжущих веществ (цемент, битум и т. д.), воды, заполнителей (песок, гравий, щебень, шлак) и различных добавок. По назначению бетоны подразделяются на конструкционные и специальные. По средней плотности – на особо тяжелые (свыше 2500 кг/см куб.), тяжелые (1200–2200), легкие (600–1200) и особо легкие (до 500 кг/см куб.). Бетоны подразделяются также по виду вяжущего, структуре, виду заполнителей. По прочности на сжатие выделяют марки бетона: тяжелого – 100, 150, 200, 250, 300, 400, 500, 600, 700, 800; легкого – 25, 35, 50, 75, 100, 150, 200, 300, 400.', 'stroit.ru'),
('Бетоны жаростойкие', 'Специальные бетоны, предназначенные для работы в условиях воздействия температур от 200 до 1800 град. Цельсия', 'stroit.ru'),
('Бетоны конструкционные', 'Бетоны несущих и ограждающих конструкций зданий и сооружений, определяющими требованиями к качеству которых являются требования по физико-механическим характеристикам. Применяются в несущих и ограждающих конструкциях зданий и сооружений. К ним относятся тяжелые бетоны (ср. пл. 2200–2500 кг/см куб.), легкие (1200–2000) и ячеистые (600–1000).', 'stroit.ru'),
('Бетоны напрягающие', 'Специальные бетоны на основе напрягающего цемента, расширяющиеся при твердении и предназначенные для создания предварительного напряжения (самонапряжения) и конструкции при его твердении.', 'stroit.ru'),
('Бетоны плотные', 'Бетоны, у которых пространство между зернами крупного и мелкого или только мелкого заполнителя заполнено затвердевшим вяжущим и порами вовлеченного газа или воздуха, в т. ч. образующихся за счет применения добавок, регулирующих пористость в объеме не более 7 %.', 'stroit.ru'),
('Бетоны радиационно-защитные', 'Специальные бетоны, предназначенные для защиты от воздействия радиационных излучений.', 'stroit.ru'),
('Бетоны силикатные', 'Бетоны на известковых вяжущих автоклавного и неавтоклавного твердения.', 'stroit.ru'),
('Бетоны специальные', 'Теплоизоляционные, жаростойкие, химически стойкие, радиационно-защитные, декоративные и др. Используются в конструкциях, работающих в особых условиях.', 'stroit.ru'),
('Бетоны теплоизоляционные', 'Специальные бетоны, предназначенные для тепловой изоляции конструкций, зданий и сооружений', 'stroit.ru'),
('Бетоны химические стойкие', 'Специальные бетоны, предназначенные для работы в условиях воздействия агрессивных сред.', 'stroit.ru'),
('Биг-бэг (big-bag)', 'Мягкие контейнеры для транспортировки и хранения насыпных грузов, используются для складирования и хранения вторичного сырья и вывоза мусора. Биг-бэги – очень экономичный вид упаковки благодаря своему минимальному весу и способности принимать необходимую форму. Легко адаптируются под любые виды погрузочно-разгрузочного оборудования и установки затаривания и разгрузки.', 'stroit.ru'),
('Бикроэласт', 'Модифицированный битумный кровельный и гидроизоляционный материал. Предназначен для устройства кровель с малым уклоном и гидроизоляции фундаментов зданий и сооружении. Состоит из прочной не гниющей органической основы (стеклоткань, стеклохолст или полиэстер), на которую с двух сторон нанесено высококачественное модифицированное битумное вяжущее. Свободно гнется при температурах до -1 град. C, обладает теплостойкостью +85 град. C.', 'stroit.ru'),
('Биогаз', 'Биогаз - смесь газов растительного и животного происхождения, которые образуются при разложении органических отходов, например, на свалках. Биогаз может быть получен в специальных установках и использован в качестве топлива.', 'stroit.ru'),
('Биологическая коррозия', 'Процессы повреждения строительных материалов, вызванные продуктами жизнедеятельности живых организмов (бактерий, грибов, мхов, лишайников и микроорганизмов), поселяющихся на поверхности строительных конструкций. Биоповреждения минеральных строительных материалов обусловлены нарушением сцепления составляющих компонентов этих материалов в результате воздействия органических кислот микробного происхождения. Биоорганизмы могут находится как в непосредственном контакте с наружной или внутренней (для пористых материалов) поверхностью строительной конструкции, так и будучи непосредственно не связанными со строительной конструкцией, являться продуцентами веществ, агрессивных по отношению к строительному материалу.', 'stroit.ru'),
('Биологическая очистка', 'Очистка сточных вод с использованием способности биологических организмов поглощать и разрушать посторонние примеси.', 'stroit.ru'),
('Биостойкость', 'Способность строительных материалов противостоять действию продуктов жизнедеятельности живых организмов (См «биологическая коррозия»). Степень биостойкости зависит как от химического состава строительного материала, так и условий эксплуатации.', 'stroit.ru'),
('Биотектура', 'Биотектура - раздел архитектуры, основанный на учете особенностей окружающей среды, а также применении местных строительных материалов.', 'stroit.ru'),
('Биотуалет', 'Биотуалет – сантехническое устройство для переработки фекальных отходов в органическое удобрение путем использования процесса биологического окисления.', 'stroit.ru'),
('Битулин', 'Битулин - материал на основе нетканого полиэстера или стекловолокна. С двух сторон на него наплавляются битумные мембраны с добавлением полипропилена. С внешней (верхней) стороны битум покрывается слоем талька, с нижней — прикрывается сгорающей при укладке пленкой.', 'stroit.ru'),
('Битумы', 'Природные или искусственные твердые или жидкие водорастворимые органические вещества, состоящие из смеси высокомолекулярных углеводородов и их производных, содержащих кислород, серу, азот и комплексные соединения металлов. Применяются в дорожном и жилищном строительстве, лакокрасочной и химической промышленности.', 'stroit.ru'),
('Блок бетонный', 'Изделия из бетона массой от десятков килограммов до нескольких тонн. Изготавливаются из тяжелых, облегченных и легких бетонов. По назначению подразделяются на фундаментные, цокольные и стеновые. Для снижения массы и теплопроводности часто выпускаются пустотными или дырчатыми. Наиболее часто пустоты имеют форму щелей, которые расположены вдоль или поперек блока.', 'stroit.ru'),
('Блок керамический', 'Штучные пустотелые керамические изделия плотностью 1100–1300 кг/м куб.. Объем пустот составляет 15–40%, водопоглощение 6–14%, морозостойкость не менее 25 циклов. Выпускаются различных типоразмеров: для кладки стен, перегородок, перекрытий, ограждений и т. д. Основные размеры 250х120х140, 250х220х250 и 320х100х140.', 'stroit.ru'),
('Блок-контейнер', 'Универсальная сборочная единица (модуль) для возведения бытовых городков, офисных и производственных зданий любой планировки и конфигурации в максимально короткие сроки. Может быть использован в качестве жилого, офисного, санитарного, производственного помещения, строительной бытовки, поста охраны, киоска и т. д.', 'stroit.ru'),
('Бойлер', 'Устройство для получения горячей воды с помощью пара, проходящего по трубам, расположенным внутри резервуара.', 'stroit.ru'),
('Больц', 'Больц – стальной крепежный элемент, соединяющий ступени лестницы между собой. Лестница на больцах не имеет каркаса. Его роль выполняют ступени и больцы.', 'stroit.ru'),
('Бордюр', 'Бордюр – в дорожном строительстве - бортовые камни, отделяющие проезжую часть дороги от обочин, тротуаров и т.п.; В ландшафтном дизайне - окаймляющая посадка низких растений по контуру клумбы, вдоль дорожки, газона.', 'stroit.ru'),
('Брандмауэр', 'Брандмауэр - противопожарная стена.', 'stroit.ru'),
('Бревно', 'Часть ствола дерева заданной длины, полученная его поперечным делением.', 'stroit.ru'),
('Бревно оцилиндрованное', 'См. «оцилиндрованное бревно»', 'stroit.ru'),
('Бризол', 'Модифицированный безосновный рулонный материал, предназначенный для антикоррозийной защиты и гидроизоляции подземных стальных трубопроводов и гидроизоляции подземных сооружений. Применяется при температуре окружающего воздуха не ниже минус 20 град. C.', 'stroit.ru'),
('Брус', 'В общем случае тело, размеры поперечного сечения которого малы по сравнению с длиной. В деревообработке – пиленный или тесаный с двух, трех или четырех сторон лесоматериал толщиной и шириной более 100 мм. Брус с двумя противоположными обработанными пластями называется двухкатным; брус, имеющий три продольные обработанные поверхности – трехкатным, четыре – четырехкантным. Используется как строительный материал или для последующей распиловки на обрезные пиломатериалы.', 'stroit.ru'),
('Брус клееный', 'См "клееный брус"', 'stroit.ru'),
('Брусчатка', 'Дорожно-строительный материал из прочных горных пород (гранит, базальт, диабаз и пр.), в виде брусков, имеющих форму, близкую к параллелепипеду. Применяется для устройства покрытий на отдельных участках автомобильных дорог и мощения городских улиц и площадей.', 'stroit.ru'),
('Бульвар', 'Аллея, расположенная посреди широкой улицы со специально высаженными деревьями и кустарниками. Имеет важное значение при озеленении городов.', 'stroit.ru'),
('Бульдозер', 'Бульдозер – самоходная землеройная машина с навесным щитом, расположенным вне базы ходовой части машины.', 'stroit.ru'),
('Бункер', 'Емкость для хранения и перегрузки сыпучих материалов, выполненная из железобетона, стали или древесины.', 'stroit.ru'),
('Бустилат', 'Бустилат – клей на синтетической основе для приклеивания линолеума, ковровых покрытий, поливинилхлоридных плиток для полов и других рулонных и плиточных материалов.', 'stroit.ru'),
('Бут', 'Бут - строительный камень, получаемый при взрывании залежей сплошных пород, таких как гранит, плотный известняк, песчаник и др. Бут применяется для возведения фундаментов, подпорных стенок, оград и т. п.', 'stroit.ru'),
('Бутилкаучук', 'Синтетический каучук, продукт сополимеризации изобутилена и небольшого количества (до 5%) изопрена. Не растворяется в спиртах, эфирах, дихлорэтане, анилине, нитробензоле. Стоек к действию воды. Отличается низкой газопроницаемостью. Важнейшая область применения – производство шин и резиновых изделий, стойких к действию высоких температур и агрессивных сред.', 'stroit.ru'),
('Вагонка', 'Вагонка - погонажное изделие из древесины — фрезерованная тонкая доска (в паз, гребень, в четверть). Вагонка используется для обшивки стен и потолков. Вагонка - экологически чистый материал.', 'stroit.ru'),
('Вакуумметр', 'То же, что и вакуумный манометр или вакуумметр-манометр, – прибор для измерения давления разреженных (давление ниже атмосферного) газов. Совокупность методов измерения давления разреженных газов носит название вакуумметрия. В зависимости от устройства и принципа действия различают жидкостные (гидростатические), механические, компрессионные ионизационные, тепловые, магнитные электроразрядные, вязкостные и др. вакуумметры.', 'stroit.ru'),
('Валик', 'В малярном деле – инструмент для нанесения и равномерного распределения лакокрасочных материалов по окрашиваемой поверхности. Корпуса валиков, как правило, изготавливаются из полиэтилена высокой плотности или алюминиевых сплавов (допускается использовать картон с пропиткой в эпоксидной смоле или бакелитовом лаке); подшипники – из полиэтилена (или иных ударопрочных пластмасс), стали или алюминиевых сплавов. Существуют следующие типы малярных валиков: - ВМ - валики с меховым покрытием, предназначенные для окраски поверхностей лакокрасочным составом; - ВМП - валики с меховым покрытием, предназначенные для окраски полов лакокрасочным составом; - ВМУ - валики с меховым покрытием, предназначенные для окраски вогнутых углов лакокрасочным составом; - ВП - валики с пенополиуретановым покрытием, предназначенные для окраски поверхностей водно-клеевым составом. Кроме того, широкое применение получили валики-насадки (ролики) из полиамида (перлона, нейлона), полиакрила (канекарона, модакрила, мерила), полиэстера, велюра (шерсть и полиакрил), поролона (мольтопрена). Для нанесения структурных красок и штукатурок и придания поверхности фактурного мелкозернистого покрытия применяются структурные валики; для нанесения штукатурок и густых красок с декоративным эффектом – валики рельефные; для разглаживания обоев – валики прижимные; для перфорации гипсокартона и удаления обоев – валики игольчатые; для прокатывания неутвержденных лицевых слоев с целью удаления воздуха (деаэрации) – валики для наливных полов.', 'stroit.ru'),
('Вальма', 'Скат шатровой крыши, имеющий треугольную форму и расположенный с торцовой стороны здания.', 'stroit.ru'),
('Вальмовая или шатровая крыша', 'Крыша прямоугольного здания, имеющая четыре ската, два из которых – трапециевидные (по длинным скатам), два – треугольные (по коротким скатам).', 'stroit.ru'),
('Вальцы', 'Рабочие органы различных машин (дробильных, мукомольных, штамповальных, деревообрабатывающих и т. д.), выполненные в виде гладких или рифленых валков, цилиндров или конусов и служащих для подачи обрабатываемого материала. (', 'stroit.ru'),
('Вантовые конструкции', 'Висячие покрытия, кровли, мосты, и иные конструкции, основанные на сочетании работы жестких опор и растяжении стальных тросов/стержней.', 'stroit.ru'),
('Ванты', 'Стальные тросы, применяемые как растяжки для крепления высоких металлических труб, радиомачт, башен ветродвигателей и т.д.', 'stroit.ru'),
('Вата базальтовая', 'Теплоизоляционный материал, состоящий из тонкого базальтового волокна. Волокна получают в результате расплавления вулканической породы при 1500 град.С с последующим раздувом расплава и добавлением в неё связующих компонентов и водоотталкивающих веществ.', 'stroit.ru'),
('Вата каолиновая', 'См. «каолиновая вата».', 'stroit.ru'),
('Вата минеральная', 'Теплоизоляционный материал в виде слабо уплотненной массы стекловидных волокон. Получают из силикатных расплавов на основе доменных шлаков, а также из смесей осадочных (мергель, доломит, известняк) и изверженных (диабаз, базальт, порфирит и т. д.) горных пород.', 'stroit.ru'),
('Вата стеклянная', 'Рыхлый материал, состоящий из переплетенных между собой тонких стеклянных волокон. Сырьем для получения служат кварцевый песок, известняк, кальцинированная сода. Используется для изготовления теплоизоляционных материалов.', 'stroit.ru'),
('Вата целлюлозная (эковата)', 'Древесный волокнистый материал. Изготовляется из макулатуры. 80% эковаты состоит из газетной бумаги, а 20% эковаты составляют нелетучие, добавки, служащие антисептиками и антипиренами.', 'stroit.ru'),
('Ввод объекта', 'Юридическое оформление заказчиком законченного строительством объекта в органах исполнительной власти.', 'stroit.ru'),
('Венец', 'В деревянном строительстве венец - один ряд горизонтальных бревен или брусьев сруба.', 'stroit.ru'),
('Вентиль', 'Вентиль – клапан для регулирования расхода жидкости, пара или газа.', 'stroit.ru'),
('Вентильная головка', 'Узел водоразборной арматуры, обеспечивающий управление запорным элементом', 'stroit.ru'),
('Вентиляция', 'Вентиляция – регулируемый воздухообмен в помещении, обеспечивающий чистоту, определенный температурный режим, необходимую влажность и подвижность воздуха.', 'stroit.ru'),
('Веранда', 'Веранда - остекленная неотапливаемая пристройка к зданию дачного типа. В большинстве случаев веранда одноэтажная. Иногда встречаются двухэтажные веранды.', 'stroit.ru'),
('Вермикулит', 'Вермикулит - материал из группы гидрослюд, образовавшийся в природе из биотита или флогопита под влиянием гидротермальных процессов в коре выветривания.', 'stroit.ru'),
('Вермикулит вспученный', 'Вермикулит вспученный - материал, получаемый путем измельчения и кратковременного обжига в печах природного вермикулита. Вермикулит вспученный применяют для теплоизоляционных засыпок, при производстве штучных теплоизоляционных изделий, в качестве заполнителя для вермикулитбетонов и добавок в декоративные штукатурные растворы.', 'stroit.ru'),
('Верстак', 'Специальный стол для столярных, слесарных и иных работ. Различаются по назначению (слесарные, столярные), по материалу (металлические, деревянные), по количеству тумб (одно-, двух-, трехтумбовые), по числу рабочих мест (одноместные, двухместные) Обычно стационарный верстак оборудуют передними и задними тисками, а его верстачную доску – приспособлениями для фиксации заготовок.', 'stroit.ru'),
('Вестибюль', 'Помещение между входом и внутренним пространством. Использовался еще в древнеримской архитектуре (например, в зданиях бань). Но особую важность приобрел в странах с холодным климатом, для защиты от теплопотерь. В России применительно к частным домам чаще употреблялись названия «прихожая» или «крытые сени», а слово вестибюль использовалось для общественных зданий.', 'stroit.ru'),
('Ветровое давление на сооружение', 'Давление или разряжение, создающиеся на поверхности сооружения обтекающим его ветром.', 'stroit.ru'),
('Виадук', 'Сооружение мостового типа, предназначенное для преодоления неводных преград – ущелий, оврагов, дорог, железнодорожных путей и т. п.', 'stroit.ru'),
('Вибратор', 'Любая система, служащая для возбуждения механических, электромагнитных или иных колебаний. В радиотехнике – участок проводника, излучающий электромагнитные волны, вследствие прохождения по нему переменного тока. В строительных технологиях – устройство для получения механических колебаний, используемое самостоятельно или как узел вибрационных машин и оборудования. Важнейшая область применения – уплотнение бетонных смесей при проведении бетонных работ или изготовлении бетонных и железобетонных изделий. Кроме того, вибраторы используются для механизации выгрузки материалов из емкостей, уплотнения грунтов и дорожных покрытий, в виброконвейерах, вибростендах для испытания конструкций.', 'stroit.ru'),
('Виброкаток', 'Виброкаток – дорожный каток, один из вальцов которого совершает колебательные движения.', 'stroit.ru'),
('Вибропрессование', 'Способ уплотнения бетонной смеси путем приложения к ней вибрационных нагрузок и статического давления.', 'stroit.ru'),
('Винипласт', 'Термопластичный непрозрачный материал – пластическая масса на основе поливинилхлорида (ПВХ), содержащая стабилизаторы, смазывающие вещества, красители (для получения цветных изделий), наполнители и модификаторы (снижают стоимость, улучшают физико-механические свойства). Впервые производство винипласта было организовано в Германии в 30-х гг. 20 столетия. Винипласт не горит, не имеет запаха, хорошо поддается различным видам механической обработки на обычных станках, легко сваривается с помощью сварочного прутка, хорошо склеивается, является хорошим диэлектриком, устойчив к действию кислот. Используется как коррозионностойкий конструкционный материал для изготовления емкостей, трубопроводов, вентиляционных установок, футеровки стальных, бетонных и деревянных аппаратов, в системах водоснабжения, канализации, ирригации и мелиорации. Также применяется в строительстве (изготовление отделочных материалов и изделий) и как упаковочный материал', 'stroit.ru'),
('Виньетка', 'Виньетка – небольшая лепная орнаментальная или сюжетная композиция на фасаде здания или стене (потолке) помещения.', 'stroit.ru'),
('Висячее крыльцо', 'Висячее крыльцо – крыльцо, опирающееся на консольные выпуски бревен-кронштейнов.', 'stroit.ru'),
('Витая пара', 'Витая пара – как правило, экранированный электрокабель, состоящий из двух свитых вместе проводников.', 'stroit.ru'),
('Витраж', 'Витраж - вставленная в оконный или дверной проем, либо в самостоятельную раму декоративная композиция, выполненная из кусков (в большинстве случаев разноцветного) стекла. В современной архитектуре витраж – обширное остекление фасада крупноразмерными стеклами, закрепленными в металлических рамах.', 'stroit.ru'),
('Витрина', 'Витрина – стеклянное хранилище для выставки на вид и сбережения различных предметов.', 'stroit.ru'),
('Влажность', 'Величина, показывающая относительное (реже абсолютное) содержание влаги в материале, определенное по отношению к массе сухого материала и выраженная в процентах.', 'stroit.ru'),
('Водозабор', 'Гидротехническое сооружение для забора воды в целях водоснабжения, ирригации.', 'stroit.ru'),
('Водонепроницаемость', 'Способность материала не пропускать воду до достижения односторонним гидростатическим давлением определенной величины.', 'stroit.ru'),
('Водоотведение', 'Использование комплекса инженерных сооружений и оборудования с целью удаления сточных, ливневых и талых вод из населенных пунктов и промышленных объектов.', 'stroit.ru'),
('Водоотлив', 'Комплекс мероприятий по отводу и удалению подземных или поверхностных вод. В горном деле – из шахт (рудников), карьеров; в строительстве – из котлованов и траншей. Производится, как с подъемом воды, так и самотеком. Системы водоотвода состоят из дренажных канав, трубчатых коллекторов, водосборников, камер с насосами, нагнетательных трубопроводов и т. д.', 'stroit.ru'),
('Водопроницаемость', 'Водопроницаемость - физическая характеристика. Водопроницаемость измеряется коэффициентом фильтрации, т. е. количеством воды (в м куб.) прошедшим в течение 1 часа через объем равный 1 м куб. при разности давлений, установленной стандартом для данного материала.', 'stroit.ru'),
('Воды грунтовые', 'Подземные воды ближайшего к поверхности земли водоносного горизонта.', 'stroit.ru'),
('Воздуховод', 'Трубопровод для перемещения воздуха в системах вентиляции, отопления и кондиционирования.', 'stroit.ru'),
('Воздушная завеса', 'Вентиляционная установка, расположенная у ворот здания, предотвращающая поступление в него холодного наружного воздуха.', 'stroit.ru'),
('Войлок строительный', 'Войлок строительный - рулонный теплоизоляционный материал, изготавливающийся из низкосортной шерсти, растительных волокон и клея. Войлок строительный используется для теплоизоляции различного оборудования, трубопроводов и т. п.', 'stroit.ru'),
('Волоковое окно', 'Волоковое окно - небольшое окно, вырубленное в двух расположенных друг над другом бревнах деревянного сруба. Волоковое окно изнутри закрывается (заволакивается) тесовой задвижкой, выполненной из доски.', 'stroit.ru'),
('Вольфрам', 'Тугоплавкий тяжелый металл светло-серого цвета, химический элемент VI группы периодической системы Менделеева. Мало распространен в природе. В свободном состоянии не встречается, входит в состав некоторых минералов, из которых промышленное значение имеют вольфрамит (Fe, Mn) WO4 и шеелит CaWO4. Почти столетие не находил промышленного применения. Лишь во второй половине 19 века начал использоваться для улучшения свойств стали. В современной технике широко применяется как в ряде сплавов, так и в виде чистого металла. Входит в состав износоустойчивых и жаропрочных сплавов, используется для изготовления нитей накала электроламп и деталей электровакуумных приборов.', 'stroit.ru'),
('Ворсолин', 'Покрытие для пола или стен, в процессе производства которого петли разрезаются, «подбриваются» до нужной длины и распускаются.', 'stroit.ru'),
('Восьмерик', 'Восьмерик - бревенчатый восьмигранный сруб. В деревянном зодчестве восьмерик – конструктивное решение перехода от кубического объема сооружения к куполу.', 'stroit.ru'),
('Время твердения', 'Время твердения – применительно к бетонной смеси время, через которое бетон теряет пластичность и приобретает необходимую прочность.', 'stroit.ru'),
('Вспомогательные помещения', 'Применительно к промышленным предприятиям это помещения, предназначенные для бытового обслуживания занятых на предприятии рабочих и служащих, а также для размещения административно-хозяйственных и технических служб.', 'stroit.ru'),
('Втулка', 'Деталь машины, механизма или прибора, имеющая осевое отверстие, в которое входит другая деталь. В зависимости от формы различают цилиндрические и конические втулки; в зависимости от назначения – подшипниковые (изготовленные из антифрикционных материалов детали неразъемного подшипника скольжения, в отверстии которой вращается цапфа вала или оси), закрепительные (предназначены для закрепления внутренних колец подшипников качения и других деталей на цилиндрических участках валов и осей), переходные (служат для установки инструмента с коническим хвостовиком в шпиндель станка, имеющего отверстие большего размера, чем хвостовик инструмента) и проч.', 'stroit.ru'),
('Вулканит', 'Штучный теплоизоляционный материал, состоящий из асбеста, диатомита и извести.', 'stroit.ru'),
('Выветривание', 'Разрушение горных пород под воздействием различных атмосферных явлений: ветров, дождей, снеготаяния, солнечной радиации и т.п.', 'stroit.ru'),
('Выклейка', 'В общем случае – узор (рисунок) из другого материала, наклеиваемый на что-либо В полиграфии – приклеивание отделочной бумаги на основу форзаца из белой бумаги с тканевым фальцем.', 'stroit.ru'),
('Выключатель концевой', 'Устройство, предназначенное для автоматического отключения механизма подъемника при переходе его движущимися частями установленных положений.', 'stroit.ru'),
('Вынос трассы в натуру', 'Полевые геодезические работы по прокладке на местности оси здания, сооружения.', 'stroit.ru'),
('Вышка', 'Грузоподъемная машина прерывного действия, предназначенная для перемещения людей с инструментом и материалами и проведения работ в вертикальном направлении (вверх, вниз).', 'stroit.ru'),
('Вяжущие вещества', 'Вещества, выполняющие функцию цементирующего компонента. По происхождению вяжущие вещества могут быть как органическими, так и неорганическими.', 'stroit.ru'),
('Вяжущие воздушные', 'Группа вяжущих веществ (известь воздушная гашеная, известь молотая негашеная, гипсовые вяжущие, магнезиальные, растворимое стекло), которые способны твердеть, набирать прочность и сохранять ее только на воздухе.', 'stroit.ru'),
('Вяжущие гидравлические', 'Самая многочисленная группа вяжущих. Способны твердеть и сохранять твердость на воздухе и в воде. К ним относятся известь гидравлическая, романцемент, портландцемент и некоторые другие материалы.', 'stroit.ru'),
('Вяжущие материалы', 'Вяжущие материалы – цемент, глина, известь и т.п. Применяются в строительстве для изготовления бетонов и растворов, а также для скрепления отдельных элементов строительных конструкций.', 'stroit.ru'),
('Габарит', 'В общем случае – предельное очертание предмета. Применительно к транспортным машинам и движущимся частям стационарных машин определяется с учетом их безопасного перемещения среди других машин и сооружений. В архитектуре – обобщенный предельный контур архитектурного сооружения или его части.', 'stroit.ru'),
('Габбро', 'Габбро - высокопрочный стойкий к выветриванию строительный материал – магматическая горная порода черного или темно-зеленого цвета (встречается пятнистая окраска). Габбро в виде полированных плит используется для облицовки фасадов зданий.', 'stroit.ru'),
('Гажа', 'Гажа - рыхлые отложения озерно-болотных водоемов, состоящие из углекислого кальция. Гажа может содержать примесь глины. Применяется гажа для производства цемента, извести, для известкования почв.', 'stroit.ru'),
('Газобетон', 'Разновидность ячеистого бетона, получаемая из смеси вяжущего, песка и воды с газообразующими добавками. В качестве вяжущего применяют портландцемент. Газообразователем, как правило, служит алюминиевая пудра. При введении ее в смесь происходит реакция с известью или щелочью, в результате которой выделяется водород.', 'stroit.ru'),
('Газобетон сланцезольный', 'Ячеистый бетон, изготовленный на основе тонкодисперстной золы от переработки горючих сланцев в топливо.', 'stroit.ru'),
('Газосиликат', 'Газосиликат - ячеистый теплоизоляционный бетон, получаемый из смеси извести с молотым кварцевым песком путем вспучивания предварительно приготовленного шлама (теста) с помощью газообразователя и затвердевания в различных условиях (автоклавная обработка или пропаривание).', 'stroit.ru'),
('Гайка', 'Деталь резьбового соединения или винтовой передачи, имеющая отверстие с резьбой. Крепежная гайка служит для закрепления сидящих на валу деталей (подшипников качения, шкивов и т. п.) или навинченная на конец болта (шпильки) обеспечивает фиксацию элементов, скрепляемых с помощью резьбового соединения. Также гайки применяются в винтовых передачах (например, в механизмах подачи станков, домкратах и проч.). В зависимости от формы различают шестигранные, круглые, корончатые, барашковые, колпачковые и другие гайки.', 'stroit.ru'),
('Гайка барашковая (барашек)', 'Гайка с выступами, обеспечивающими ее затягивание без применения гаечного ключа.', 'stroit.ru'),
('Галерея', 'Крытый проход, расположенный по наружному периметру здания, опирающийся на несущие стены, колонны, аркады.', 'stroit.ru'),
('Галерея висячая', 'Галерея, поддерживаемая консольными выпусками из здания.', 'stroit.ru'),
('Ганч', 'Ганч - вяжущий материал, применяемый в штукатурных растворах. Ганч получается путем обжига природной смеси гипса с глиной.', 'stroit.ru'),
('Гардины', 'Гардины – занавеси из легкой прозрачной ткани, ширина которых, как правило, в два и более раза превышает ширину карниза.', 'stroit.ru'),
('Генеральный подрядчик', 'Организация, являющаяся главным исполнителем договора подряда на проведение строительно-монтажных работ.', 'stroit.ru'),
('Генеральный проектировщик', 'Проектная организация, ответственная за выполнение комплекса изыскательских и проектных работ на основании договора с Заказчиком.', 'stroit.ru'),
('Геокаркасы', 'Разновидность георешеток (другое название «объемные георешетки»). Конструкции из нетканных лент, скрепленных между собой сварными высокопрочными швами в шахматном порядке. Применяются для противоэрозионной защиты откосов, конусов путепроводов, а также при устройстве подпорных стенок и армировании слабых оснований.', 'stroit.ru'),
('Геоматы', 'Имеющие различную структуру (регулярно или хаотично расположенные волокна, конструкции из полос в виде сот и проч.) объемные рулонные водопроницаемые структуры, выполненные из полимеров и (или) других синтетических или природных элементов, соединенных между собой термическим, механическим или иным способом. Применяются для создания устойчивого растительного покрова на берегах водоемов, склонах и откосах, предотвращения эрозионных процессов, улучшения дренажа и усиления оснований', 'stroit.ru'),
('Геомембраны', 'Рулонные материалы из полимеров или битумов, характеризующиеся очень низкой водопроницаемостью. Применяются для паро-, гидро- и газоизоляции подземных строительных конструкций, устройства противофильтрационных экранов, а также для снижения величины активных сдвиговых напряжений за счет гладкого контакта с нижележащим слоем и т. д.', 'stroit.ru'),
('Георешетки', 'Геосинтетический материал, состоящий из сетки работающих на растяжение непрерывных соединительных элементов, скрепленных между собой экструзионным способом, сваркой или переплетением (при этом величина ячеек больше ширины соединительных элементов и имеет достаточные размеры для сцепления с окружающим грунтом, камнями или другими геотехническими сопрягаемыми элементами). Георешетки применяются для армирования грунтовых стен, устоев мостов, склонов и земляных насыпей, а так же в локальных местах, имеющих слабое основание.', 'stroit.ru'),
('Геосетки', 'Рулонные материалы, имеющие ячеистую структуру, образующие элементы которой (высокопрочные нити или пучки нитей) скреплены узлами, переплетены или спрессованы. Существуют композиционные геосетки, в состав которых входят нетканые материалы. Изготавливаются из полимерных нитей или лент. Характеризуются высокой прочностью и малой деформативностью. Используются для укрепления слабых грунтов, армирования автодорог, железнодорожных путей, взлетно-посадочных полос аэродромов и полигонов для захоронения отходов, а также упрочнения строительных конструкций и гидросооружений, армирования изоляции нефтяных и газовых трубопроводов.', 'stroit.ru'),
('Геосинтетические материалы', 'Материалы, полностью или частично изготовленные из синтетических или натуральных полимеров, применяемые в геотехнике для повышения технических характеристик грунтов или элементов различных строительных конструкций.', 'stroit.ru'),
('Герма', 'Используемое в садово-парковой архитектуре скульптурное изображение в виде головы или бюста на четырехгранной опоре.', 'stroit.ru'),
('Гигроскопичность', 'Способность материалов к поглощению атмосферной влаги.', 'stroit.ru'),
('Гидрант', 'Гидрант – стационарное устройство для отбора воды на пожарные нужды из наружной водопроводной сети.', 'stroit.ru'),
('Гидроизол', 'Рулонный беспокровный гидроизоляционный материал, полученный путем пропитки асбестового картона нефтяным битумом.', 'stroit.ru'),
('Гидроизол', 'Беспокровный биостойкий рулонный материал, получаемый пропиткой асбестовой бумаги нефтяными битумами. Предназначается для гидроизоляции подземных и других сооружений, устройства рулонной кровли и антикоррозионных покрытий трубопроводов.', 'stroit.ru'),
('Гидроизоляционные строительные материалы', 'Предназначенные для защиты строительных конструкций от постоянного воздействия агрессивной влажной среды, чаще всего от действия воды под давлением.', 'stroit.ru'),
('Гидроизоляционный слой пола', 'Слой (слои), препятствующий прониканию через пол сточных вод и других жидкостей, а также проникновению грунтовых вод.', 'stroit.ru'),
('Гидроизоляция проникающая', 'Гидроизоляция проникающая - надежное обеспечение водонепроницаемости бетонных и каменных конструкций путём инъекций специальных составов.', 'stroit.ru'),
('Гидропривод', 'Привод, включающий в себя гидравлический механизм, в котором рабочая жидкость находится под давлением, с одним и более объемным гидродвигателем (гидроцилиндром).', 'stroit.ru'),
('Гидрофобность', 'Способность материала не смачиваться водой.', 'stroit.ru'),
('Гидрошпонки', 'Выполненные из ПВХ эластичные ленты для уплотнения рабочих и деформационных швов в бетонных конструкциях, постоянно или временно находящихся под воздействием поверхностных грунтовых или сточных вод (резервуары, водонапорные башни, дамбы, водосливы, каналы, плавательные бассейны, очистные сооружения и т.п.). Также используется для гидроизоляции (удержания воды вне сооружения) фундаментов, подземных парковок, туннелей и др. подземных сооружений.', 'stroit.ru'),
('Гипс', 'Гипс - минерал, водный сульфат кальция, а также осадочная горная порода, состоящая в основном из этого минерала. Гипс применяется при производстве вяжущих веществ, штукатурного гипса, эстрихгипса, гипсового цемента, а также для получения серной кислоты.', 'stroit.ru'),
('Гипсокартон (сухая штукатурка)', 'Представляет собой лист, состоящий из двух слоев строительной бумаги(картона) и гипсового сердечника. Из общей массы листа примерно 93% приходится на двуводный гипс, 6% — на картон, 1% массы образован за счет влаги, крахмала и органического поверхностно-активного вещества.', 'stroit.ru'),
('Глазурь', 'Глазурь - тонкий стекловидный слой на поверхности керамических изделий, образующийся за счет расплавления и последующего застывания специально нанесенной керамической массы определенного состава. Глазурь улучшает физико-химические и декоративные качества изделия, способствует увеличению его механической прочности, химической стойкости, долговечности, повышает его эксплуатационные качества.', 'stroit.ru'),
('Глиеж', 'Глиеж - глина, обожженная в результате подземного горения угольных пластов. Глиеж применяется при производстве силикатных цементов, где ее содержание может достигать 50%.', 'stroit.ru'),
('Глины', 'Осадочные горные породы, в основном состоящие из глинистых минералов. Обладают пластичностью – при замешивании с водой образуют вязкое тесто, которое при высыхании сохраняет приданную форму. Используются для производства керамики, огнеупоров, как адсорбенты, а также для изготовления кладочных печных растворов.', 'stroit.ru'),
('Глиняный раствор', 'Глиняный раствор - кладочный раствор из глины, песка и воды. Глиняный раствор применяется при кладке печей, каминов, очагов и дымовых труб.', 'stroit.ru'),
('Глубина промерзания', 'Глубина, на которую в зимний период промерзает грунт. В условиях Северо-Запада глубина промерзания ~ 1,5 м.', 'stroit.ru'),
('Глухая резьба', 'Несквозная резьба, выполненная в массиве древесины, рассчитанная на восприятие рельефа при солнечном освещении или специальной подсветке.', 'stroit.ru'),
('ГОИ (полировальная паста)', 'Полировальная паста, широко применяемая в технике. ГОИ – аббревиатура учреждения в котором она была разработана (Государственный Оптический Институт). Основу пасты ГОИ составляет окись хрома. Помимо нее в ее состав входят: стеарин, жир, силикагель, керосин. Различают грубую, среднюю и тонкую пасты. Грубой удаляют мельчайшие царапины, оставшиеся на поверхности после обработки абразивными материалами (шлифования). С помощью средней получают ровный блеск полируемой поверхности. После полировки тонкой пастой, поверхность приобретает интенсивный зеркальный блеск.', 'stroit.ru'),
('Горбыль', 'Наружная часть бревна, удаляемая при получении бруса на лесопильной раме, циркульной или ленточной пиле. Имеет одну пропиленную поверхность. Используется для технологической щепы и при производстве деревянных изделий. Горбыль, у которого наружная поверхность частично пропилена называется дощатым горбылем.', 'stroit.ru'),
('Горизонт водоносный', 'Горизонт, насыщенный водой, залегающий между двумя водоупорными пластами.', 'stroit.ru'),
('Гравий', 'Природный или искусственный материал, представляющий собой окатанные зерна размером 5–70 мм и гладкую поверхность.', 'stroit.ru'),
('Градирня', 'Градирня - башня с железобетонным или металлическим каркасом обшитым асбоцементными/алюминиевыми панелями. Градирня предназначена для охлаждения циркуляционной воды при оборотной системе водоснабжения.', 'stroit.ru'),
('Гранит', 'Гранит - полнокристаллическая равномернозернистая или порфировидная горная порода, состоящая из кварца, полевого шпата и темноцветных минералов. Гранит обладает высокой твердостью. Используется гранит в качестве декоративного облицовочного материала, как заполнитель для бетонов, в виде кислотоупорной облицовки.', 'stroit.ru'),
('Грейдер', 'Грейдер – прицепная землеройно-планировочная машина, которая с помощью отвала вырезает или перемещает грунт.', 'stroit.ru'),
('Грохот', 'Один из основных видов технологического оборудования дробильно-сортировочных заводов и обогатительных фабрик, служащий для механической сортировки сыпучих материалов по крупности частиц (кусков). Помимо разделения материалов на фракции, может использоваться для обезвоживания материалов. Грохоты подразделяются на неподвижные (состоящие из рабочего органа неподвижной просеивающей поверхности и установки для ее крепления – колосниковые, дуговые, конические) и подвижные, состояшие из одной или нескольких просеивающих поверхностей (сит), устройств для их установки и механизма, приводящего сита в движение. Среди подвижных различают барабанные, качающиеся, вибрационные и полувибрационные грохоты.', 'stroit.ru'),
('Грунт', 'Общее название горных пород включая почвы, являющихся объектом строительной деятельности. Различают: скальные грунты - породы, залегающие в виде монолитного или трещиноватого массива и рыхлые грунты - крупнообломочные и песчаные породы.', 'stroit.ru'),
('Грунтовки', 'Составы, наносимые первым слоем на подготовленную к окраске поверхность для уменьшения ее пористости и обеспечения требуемой адгезии лакокрасочного покрытия. От окрашивающих составов отличаются меньшим содержанием пигментов.', 'stroit.ru'),
('Грунтозацеп', 'Выступ на протекторе, обеспечивающий хорошее сцепление с грунтом и самоочищение от него.', 'stroit.ru'),
('Гурт', 'Гурт – вертикальные и горизонтальные профилированные тяги на фасадах и в интерьерах.', 'stroit.ru'),
('Дальномер', 'Прибор, служащий для определения расстояний без их непосредственного измерения на местности.', 'stroit.ru'),
('Дальномер оптический', 'Дальномер, принцип действия которого основан на решении прямоугольного или равнобедренного треугольника по известной длине базы и измеренному противоположному углу.', 'stroit.ru'),
('Двери-купе', 'Двери-купе – двери, использующиеся в качестве фасада шкафов-купе или в качестве раздвижных перегородок.', 'stroit.ru'),
('Дверь ложная', 'Облицованный наличником заложенный дверной проем, образующий неглубокую нишу.', 'stroit.ru'),
('Двойня (тройня)', 'Деревянная постройка, объединяющая в одну группу два (три) сруба с самостоятельными несимметричными двускатными крышами.', 'stroit.ru'),
('ДДУ', 'Сокращение от слов «детское дошкольное учреждение» или «дезинфекционно-душевая установка».', 'stroit.ru'),
('Девелопер', 'Предприниматель, получающий прибыль от создания и развития объектов недвижимости (как финансирующий реализацию проекта самостоятельно, так и с привлечением инвестиций). Может брать на себя функции автора идеи проекта, приобретателя земельного участка под застройку, организатора проектирования объекта, нанимателя генподрядчика, управляющего недвижимостью и т. д.', 'stroit.ru'),
('Девелопмент', 'Область менеджмента. занимающаяся развитием недвижимости. Включает комплекс строительных, инженерных и иных мероприятий над недвижимым имуществом, ведущих к качественным изменениям в состоянии (и, соответственно, стоимости)земли, зданий и сооружений.', 'stroit.ru'),
('Декор', 'Система украшений (именно так это и переводится с латинского) фасадов и интерьеров зданий и сооружений или изделий. Примерами декора являются одноцветная и многоцветная покраска, художественная роспись, фактурная обработка поверхности, выполненные из различных материалов орнаменты, скульптурные изображения и т. д. Декор призван помогать акцентировать выразительность архитектурных решений. И, кроме того, это – одно из средств зрительного объединения в ансамбль отдельных зданий или предметов.', 'stroit.ru'),
('Декстрины', 'Продукты частичного расщепления полисахаридов, аморфные вещества с достаточно выраженными восстанавливающими свойствами, образуют коллоидные растворы. Декстрины из крахмала применяют в качестве клеящих веществ в текстильной, полиграфической, обувной промышленности.', 'stroit.ru'),
('Демаркация', 'Разграниченье, проведение какой-либо разделяющей черты, границы.', 'stroit.ru'),
('Демпфер', 'Устройство для гашения, ослабления (демпфирования) или предотвращения механических колебаний, возникающих в машинах и приборах при их работе (гидравлические, механические, пневматические). В электрических машинах роль демпферов, ослабляющих электромагнитные колебания, выполняют катушки индуктивности.', 'stroit.ru'),
('Деталь', 'Изделие, изготовленное из однородного по наименованию и марке материала, без применения сборочных операций. Эти же изделия, подвергнутые покрытиям (защитным или декоративным), независимо от вида, толщины и назначения покрытия, или изготовленные с применением местной сварки, пайки, склейки, сшивки и т. п.', 'stroit.ru'),
('Детандер', 'Машина для охлаждения газа, вследствие его расширения, т. е. расширительная машина, в которой поток совершает (полезную) внешнюю работу, вращая колесо или толкая поршень. Применяется главным образом не с целью совершения внешней работы, а для получения холода. Так, детандеры используются в установках сжижения газов, криогенных рефрижераторах, в некоторых системах кондиционирования воздуха и т. д.', 'stroit.ru'),
('Детериорация', 'Ухудшение состояния окружающей природной среды, происходящее в результате хозяйственной деятельности человека.', 'stroit.ru'),
('Дефлектор', 'Вытяжное устройство, устанавливаемое на конце наружной части трубы для отсоса загрязннного воздуха из различных помещений и реже для подачи воздуха в помещения. Установленный на дымовой трубе дефлектор повышает тягу, подсасывая газы из трубы. Действие дефлектора основано на использовании энергии обдувающего его потока воздуха. Выполняются дефлекторы преимущественно из стали, реже из бетона, асбестоцемента и других материалов.', 'stroit.ru'),
('Джут', 'Род растений семейства липовых. Растения высотой до 3,5 м с прямостоячим, ветвистым стеблем и стержневым корнем произрастают в тропических районах Азии, Африки, Америки, Австралии. Джутовое волокно широко используют для изготовления технических, упаковочных, мебельных и других тканей, ковровых изделий.', 'stroit.ru'),
('Диабаз', 'Диабаз - полнокристаллическая мелкозернистая вулканическая горная порода черного цвета. Диабаз обладает высокой твердостью и прочностью на сжатие. Применяется диабаз для мощения улиц и при производстве литых каменных изделий.', 'stroit.ru'),
('Диатомит', 'Диатомит - рыхлая или сцементированная кремнистая горная порода белого, светло-серого или желтоватого цвета. Диатомит более чем на 50% состоит из панцирей диатомей. Диатомиты обладают большой пористостью, малой плотностью (не тонут в воде), адсорбционными и теплоизоляционными свойствами. Диатомиты кислотостойки, огнеупорны.', 'stroit.ru'),
('Диммер', 'Диммер – устройство для плавного регулирования силы света лампы.', 'stroit.ru'),
('Динамометр', 'Прибор, предназначенный для измерения силы. Чувствительным элементом прибора – пружина, деформация которой пропорциональна модулю измеряемой силы.', 'stroit.ru'),
('Диспенсер', '1) Одна из разновидностей печатной рекламы. Подставка из жесткой бумаги, картона или пластика с рекламным текстом или изображением. 2) Устройство для поштучной подачи товара в упаковке. 3) Автомат, предназначенный для выдачи наличных денег.', 'stroit.ru'),
('Дифманометр', 'См. «манометр дифференциальный».', 'stroit.ru'),
('Диффузор', 'В аэродинамике – часть канала (трубы), в которой происходят замедление (расширение) потока и возрастание давления. Диффузоры применяется в устройствах, в которых осуществляется перемещение жидкостей и газов (водопроводах, газопроводах, воздуховодах, нефтепроводах, аэродинамических трубах, реактивных двигателях и пр.). В электроакустике диффузором называют часть механической колебательной системы громкоговорителя, предназначенной для возбуждения звуковых волн в окружающем воздухе. Кроме того, название «диффузор» носят элементы технологического оборудования, например в производстве глинозема – аппарат для проточного выщелачивания дробленого бокситового спека.', 'stroit.ru'),
('Диэлектрики', 'Вещества, обладающее низкой удельной электрической проводимостью, т. е. практически не проводящие электрический ток; способны поляризоваться в электрическом поле.', 'stroit.ru'),
('Добавки для бетона водоредуцирующие', 'Вещества, позволяющие получать бетонную смесь требуемой удобоукладываемости с пониженным расходом воды.', 'stroit.ru'),
('Добавки для бетона воздухововлекающие', 'Поверхностно-активные органические вещества, способствующие вовлечению в бетонную смесь при ее перемешивании мелкодисперсного воздуха, равномерно распределенного в бетоне.', 'stroit.ru'),
('Добавки для бетона газообразующие', 'Вещества, способные выделять газ за счет химического взаимодействия с продуктами гидратации цемента.', 'stroit.ru'),
('Добавки для бетона гидрофобизирующие', 'Вещества, придающие стенкам пор и капилляров в бетоне гидрофобные (водоотталкивающие) свойства.', 'stroit.ru'),
('Добавки для бетона кольматирующие', 'Вещества, способствующие заполнению пор в бетоне водонерастворимыми продуктами.', 'stroit.ru'),
('Добавки для бетона пенообразующие', 'Поверхностно-активные органические вещества, обеспечивающие возможность получения технической пены требуемых кратности и стойкости, которые при смешении с компонентами бетонной смеси позволяют получать бетоны ячеистой или поризованной структуры.', 'stroit.ru'),
('Добавки для бетона противоморозные', 'Вещества, понижающие температуру замерзания воды и способствующие твердению бетона при отрицательных температурах.', 'stroit.ru'),
('Добавки для бетонов', 'Природные или искусственные химические продукты, вводимые в составы бетонов при их изготовлении с целью улучшения технологических свойств бетонных смесей, физико-химических свойств бетонов, снижения их стоимости.', 'stroit.ru'),
('Добавки для бетонов водоудерживающие', 'Вещества, способствующие снижению водоотделения бетонной смеси.', 'stroit.ru'),
('Добавки для бетонов пластифицирующие', 'Вещества, увеличивающие подвижность бетонных смесей.', 'stroit.ru'),
('Добавки для бетонов поризующие', 'Вещества, способствующие целенаправленному образованию в теле бетона воздушных или других газообразных нор.', 'stroit.ru'),
('Добавки для бетонов стабилизирующие', 'Вещества, способствующие снижению расслаиваемости бетонной смеси.', 'stroit.ru'),
('Доводчик дверной', 'Доводчик дверной – устройство регулирующее скорость открытия или закрытия дверей.', 'stroit.ru'),
('Дождеприемник', 'Сооружение на канализационной сети, предназначенное для приема и отвода дождевых вод.', 'stroit.ru'),
('Дозатор', 'Дозатор – устройство для автоматического отмеривания (дозирования) заданных массы или объёма жидких и сыпучих материалов.', 'stroit.ru'),
('Дом блокированный', 'Дом блокированный – малоэтажный жилой дом, состоящий из нескольких квартир с отдельными входами.', 'stroit.ru'),
('Дорожная одежда', 'Инженерная конструкция из нескольких слоев различных дорожно-строительных материалов, предназначенная для движения автотранспорта.', 'stroit.ru'),
('Дранка, гонт', 'Дранка - деревянная щепа, применявшаяся для кровли крыш. В некоторой степени дранка - предшественница черепицы.', 'stroit.ru'),
('Дренаж', 'Система траншей, борозд, труб (дрен), колодцев, предназначенных для сбора избыточной грунтовой влаги с осваиваемой территории.', 'stroit.ru'),
('Дымник', 'Дымник - верхняя надкровельная часть печной дымовой трубы.', 'stroit.ru'),
('Дымоход', 'Дымоход – канал дымовой трубы.', 'stroit.ru'),
('Дюбель', 'Дюбель - крепежное изделие. Предназначен дюбель для закрепления в твердых сплошных стеновых или потолочных материалах. Принцип крепления дюбеля: трение, возникающее за счет распора дюбеля при установке в него шурупа или винта, создающее удерживающую силу.', 'stroit.ru'),
('Египетский треугольник', 'Египетский треугольник - прямоугольный треугольник с соотношением сторон 3:4:5. Сумма указанных чисел (3+4+5=12) с древних времен использовалась как единица кратности при построении прямых углов с помощью веревки, размеченной узлами на 3/12 и 7/12 ее длины. Применялся египетский треугольник в архитектуре средних веков для построения схем пропорциональности.', 'stroit.ru'),
('Ендова', 'Желоб, сочленение двух скатов крыши, образующее входящий угол.', 'stroit.ru'),
('Жалюзи', 'Светозащитные шторы, состоящие из горизонтальных или вертикальных непрозрачных пластиковых или металлических пластин, вращающихся на оси.', 'stroit.ru'),
('Железобетон', 'Искусственный строительный материал, состоящий из стального арматурного каркаса залитого бетоном и конструктивно объединяющий рабочие свойства стали и бетона. При этом арматура работает на растяжение, а бетон – на сжатие.', 'stroit.ru'),
('Железобетонные конструкции', 'Монолитные или сборные конструкции, выполненные из совместно работающих стального арматурного каркаса и бетона.', 'stroit.ru'),
('Жесткость', 'Жесткость – способность металлоконструкции или ее элемента не подвергаться деформации под воздействием внешних механических нагрузок.', 'stroit.ru'),
('Жесткость несущего остова здания', 'Неизменность формы несущего остова (пространственное сочетание несущих элементов здания. определяющее его форму, прочность и устойчивость) в процессе восприятия нагрузок.', 'stroit.ru'),
('Жидкие гвозди', 'Жидкие гвозди - клеи, отличающиеся высокой адгезией. Клей жидкие гвозди наносятся на склеиваемые материалы не сплошным слоем, а точечно, на места, куда обычно забивают гвозди или дюбели.', 'stroit.ru'),
('Жиклёр', 'Жиклёр – калиброванное отверстие для дозирования подачи жидкого топлива или воздуха.', 'stroit.ru'),
('Жилые помещения', 'Жилые дома, коттеджи, дачные постройки, предназначенные для постоянного проживания, а также отдельные квартиры, зарегистрированные в соответствующих государственных органах.', 'stroit.ru'),
('Жирный бетон', 'Бетон с низким содержанием заполнителей и высоким содержанием цемента.', 'stroit.ru'),
('Забор', 'Глухая ограда, как правило, выше человеческого роста.', 'stroit.ru'),
('Забор каменный', 'Возведенная на соответствующем фундаменте стена, выложенная из бутового камня, кирпича, кирпичных или бетонных блоков, усиленная на определенном расстоянии столбами из того же материала. В ряде случаев имеет цоколь по низу и карниз по верху.', 'stroit.ru'),
('Завертка оконная', 'Элемент оконной фурнитуры, служащий для запирания окон с одной стороны при помощи вращения ручки.', 'stroit.ru'),
('Завеса воздушная', 'Устройство в системе местной приточной вентиляции, служащее для предотвращения поступления наружного холодного воздуха в производственное помещение через открытые проемы (двери или ворота) промышленного здания. Состоит из воздуховодов, через которые под углом к плоскости проема и стремящемуся проникнуть в помещение потоку вентилятором нагнетается воздух. Если этот воздух предварительно подогревается; такое устройство называется воздушной тепловой завесой. Воздушной завесой называют и непосредственно подаваемый системой местной приточной вентиляции воздушный поток, препятствующий доступу в помещения наружного холодного воздуха.', 'stroit.ru'),
('Завеса тепловая', 'См. «завеса воздушная».', 'stroit.ru'),
('Заворина', 'Заворина - разновидность засова, представляющая собой перекладину, как правило, деревянную. Завориной изнутри закладываются ворота.', 'stroit.ru'),
('Заглушка', 'Деталь для временной или постоянной герметизации концевых участков трубопроводов. Может применяться для предохранения торцов труб и профилей при перевозке и хранении.', 'stroit.ru'),
('Заземление', 'Устройство для электрического соединения с Землей аппаратов, машин и приборов. Состоит из заземлителя и заземляющего проводника. Служит для защиты от опасного действия электрического тока.', 'stroit.ru'),
('Заклепка полукруглая', 'Заклепка (см «заклепка») с полукруглой головкой.', 'stroit.ru'),
('Заклепка потайная', 'Заклепка (см «заклепка») с потайной головкой.', 'stroit.ru'),
('Замок', 'Изделие, служащее для запирания дверей, имеющее сложную комбинацию запирающих устройств или рабочих штифтов, обеспечивающих блокировку.', 'stroit.ru'),
('Замок, замковый камень', 'Завершающий свод или арку верхний центральный камень. Представляет собой клиновидный блок или кирпич, несколько выступающий из плоскости.', 'stroit.ru'),
('Замочно-скобяные изделия', 'Изделия, служащие для соединения оконных створок и дверных полотен с коробками, а также для закрывания, запирания, фиксирования и украшения окон и дверей.', 'stroit.ru'),
('Заплот, заплотная ограда', 'Забор, представляющий собой сплошную деревянную ограду из бревен или теса.', 'stroit.ru'),
('Застройщик', 'Физическое/юридическое лицо или орган государственной исполнительной власти/местного самоуправления, получившее в установленном порядке земельный участок под строительство или реконструкцию комплекса недвижимого имущества.', 'stroit.ru'),
('Заступ', 'Большая металлическая лопата для земляных работ. Состоит из железной лопасти и рукоятки. На верхнем конце ручки имеется костыль. Форма лопасти может быть прямоугольной со скошенными или закругленными краями, трапецоидальной и трехугольной', 'stroit.ru'),
('Земляное полотно', 'Сооружение, служащее основанием верхнего строения железнодорожного пути или дорожной одежды автомобильной дороги. Служит для восприятия и равномерного распределения на нижележащий естественный грунт нагрузки от транспортных средств, а также рельсов, шпал и балласта (на железной дороге) и дорожной одежды (на автомобильной дороге). Один из главных элементов дороги, от состояния которого во многом зависит исправность железнодорожного пути и дорожной одежды.', 'stroit.ru'),
('Зензубель', 'Столярный инструмент для выборки и зачистки четвертей, фальцев, пазов, а также острожки перпендикулярных поверхностей. Может иметь одиночный или двойной нож по форме напоминающий лопатку.', 'stroit.ru'),
('Зенкование', 'Обработка выходной части отверстия для получения конических или цилиндрических углублений.', 'stroit.ru'),
('Зенковка', 'Зенковка – многолезвийный режущий инструмент для обработки деталей с целью получения конических или цилиндрических углублений, опорных плоскостей вокруг отверстий или снятия фасок центровых отверстий.', 'stroit.ru'),
('Зенковка (или зенковочное сверло)', 'Многолезвийный режущий инструмент для обработки деталей с целью получения конических или цилиндрических углублений, опорных плоскостей вокруг отверстий или снятия фасок центровых отверстий. По форме режущей части зенковки подразделяются на цилиндрические, конические и торцовые (цековки).', 'stroit.ru'),
('Зигмашина', 'Иногда пишется: зикмашина, зиговочная машина, зиг-машина. Машина для образования и правки местных выступов и углублений (зигов) на поверхности листовой заготовки, а также для закатки проволоки.', 'stroit.ru'),
('Золотое сечение', 'Золотое сечение издревле используется при нахождении максимально уравновешенных пропорций между архитектурными частями зданий или частями архитектурных сооружений. Принцип Золотого сечения заключается в следующем: деление целого на две неравные части пропорционально в том случае, когда меньшая часть – «минор» (примерно 38%), относится к большей части – «майор» (примерно 62%), так, как большая («майор») к целому и наоборот.', 'stroit.ru'),
('Зонт вытяжной', 'Элемент систем вытяжной вентиляции, размещаемый над тепловыделяющим оборудованием. Используется в промышленности, лабораториях, на предприятиях общественного питания и т. д. В разговорной речи весьма часто используется термин «вытяжка».', 'stroit.ru'),
('ЗРУ (закрытые распределительные устройства)', 'Электрическое распределительное устройство для приема мощности от передвижных электростанций, сети и передачи ее потребителям, оборудование которого расположено в помещении (например, изготавливаются ЗРУ на базе стандартных морских контейнеров различных типов и размеров, предназначенных для эксплуатации в условиях низких температур).', 'stroit.ru'),
('Известняки', 'Известняки - осадочные горные породы, состоящие главным образом из кальцита. Известняки могут содержать различные примеси (обломочных частиц, органических соединений и др.) Название известнякам дается в зависимости от особенностей слагающих его компонентов. Известняки широко применяются в строительстве (как облицовочный камень, для производства извести и т. д.), стекольной промышленности, металлургии (флюсы).', 'stroit.ru'),
('Известь воздушная', 'Воздушное вяжущее, получаемое путем обжига дробленых известковистых пород (известняка, мела, ракушечника и т. д.), содержащих не более 6% глинистых компонентов. Получаемая известь носит название комовой, а после измельчения – молотой.', 'stroit.ru'),
('Известь гашеная', 'Известь гидратная, известь пушонка. Получают из комовой или молотой извести путем гашения ее водой. Если количество воды составляет 60–80% от массы извести, комки распадаются на тонкодисперсные частицы и образуется известь пушонка. При дальнейшем разбавлении водой получают соответственно известковое тесто и известковое молоко. Применяется для приготовления кладочных и штукатурных растворов, а также в бетонах невысоких марок, используемых в сухих условиях. Известь пушонка используется для приготовления сухих смесей.', 'stroit.ru'),
('Известь гидравлическая', 'Гидравлическое вяжущее. Получают путем обжига мергелистых известняков (содержащих до 20% глинистых компонентов). Применяется для приготовления кладочных и штукатурных растворов и бетонов невысокой прочности, используемых во влажных условиях.', 'stroit.ru'),
('Известь кипелка', 'Молотая негашеная известь. Получается при механическом измельчении комовой извести. При взаимодействии ее с водой выделяется значительное количество тепла.', 'stroit.ru'),
('Извещатель пожарный', 'Устройство, служащее для восприятия сигнала о пожаре (возгорании) и формирования информации о нем для дальнейшей передачи извещения. Разделяются на автоматические и ручные. В зависимости от фактора, инициирующего срабатывание, пожарные извещатели делятся на несколько типов: газовые (реагируют на газы, выделяющиеся при тлении или горении материалов), извещатели пламени (реагируют на электромагнитное излучение пламени или тлеющего очага), дымовые (в них используется фотоэлектрический принцип действия, т. е. регистрация оптического излучения, отраженного от частиц дыма, попадающих в дымовую камеру) и т. д. В зависимости от наличия энергоисточника пожарные извещатели могут быть активными (получают электропитание по шлейфу от приемно-контрольного прибора или иных источников питания), и пассивными, не требующими электропитания.', 'stroit.ru'),
('Изгиб', 'Деформация, возникающая в балках, плитах перекрытий, ограждающих конструкциях под воздействием внешних нагрузок или температурных изменений.', 'stroit.ru'),
('Изол', 'Безосновный биостойкий гидро- и пароизоляционный рулонный материал, получаемый из резино-битумного вяжущего, пластификатора, наполнителя, антисептика и полимерных добавок. Предназначается для оклеечной гидроизоляции (в том числе подземных каналов для трубопроводов), изоляции конструкций зданий и сооружений, пароизоляции покрытий, а также для гидроизоляции пролетных строений железнодорожных мостов, расположенных в районах с температурой наиболее холодных суток до минус 35 град. Цельсия. Может применяться для защиты наружной поверхности стальных труб тепловых сетей от коррозии при температуре до 140 град. Цельсия, а тепловой изоляции - от увлажнения в случае бесканальной прокладки. По согласованию с проектной организацией допускается применение изола при температуре изолируемых поверхностей до 150 град. Цельсия.', 'stroit.ru'),
('Изразцы', 'Изразцы - декоративные плитки из обожженной глины, покрытые цветной глазурью. Сформованные изразцы высушивают, а затем обжигают при температуре до 1150 град. С. Изразцы могут быть гладкими или рельефными, покрытыми глазурью (майоликовые), или неглазурованными (терракотовые).', 'stroit.ru'),
('Изыскательские работы для капитального строительства', 'Комплекс экономических и технических исследований, позволяющих обосновать целесообразность и местоположение строительства и представляющих необходимые данные для проектирования.', 'stroit.ru'),
('Импост', 'Завершающая часть колонны или стены, имеющая вид полочки или фигурного карниза, являющаяся опорой для вышерасположенной арки. Перемычка над оконным или дверным проемом того же назначения.', 'stroit.ru'),
('Индуктор', 'Индуктор – прибор для получения электрического тока более высокого напряжения', 'stroit.ru'),
('Инсоляция', 'Степень освещенности солнечным светом зданий, сооружений и их внутренних помещений.', 'stroit.ru'),
('Интарсия', 'Декоративно-прикладное искусство – врезка в основной массив древесины деревянных пластинок другого цвета или пластинок из другого материала (бронзы, кости, перламутра и т. п.).', 'stroit.ru'),
('Интерьер', 'Интерьер – внутреннее пространство помещения. Функциональное назначение интерьера определяет его архитектурное решение – размер, пропорции и т.д.', 'stroit.ru'),
('Инцерт', 'Облицовка бетонных поверхностей природными или искусственными камнями неправильной формы.', 'stroit.ru'),
('Кабель греющий (нагревательный)', 'Электрический кабель, используемый для обогрева. В промышленности (главным образом на химических и нефтеперерабатывающих заводах) саморегулирующиеся греющие кабели используются для защиты от замерзания трубопроводов, промышленных емкостей (цистерн), водостоков, кровли. Будучи весьма удобными в монтаже и неприхотливыми в обслуживании они все используются в инженерных системах зданий (т. н. «теплые полы»). Регулируя сопротивление кабеля, можно гибко управлять степенью нагрева.', 'stroit.ru'),
('Кабельная галерея', 'Надземное/наземное/подземное горизонтальное/наклонное протяженное проходное сооружение, предназначенное для прокладки электрических кабелей.', 'stroit.ru'),
('Кабельная камера', 'Подземное кабельное сооружение, закрываемое глухой съемной плитой, предназначенное для укладки кабельных муфт или для протяжки кабелей в блоки.', 'stroit.ru'),
('Кабельная шахта', 'Вертикальное кабельное сооружение, у которого высота в несколько раз больше стороны сечения, снабженное скобами или лестницей для передвижения людей.', 'stroit.ru'),
('Кабельный канал', 'Закрытое и заглубленное в грунт, пол, перекрытие и т.п. непроходное сооружение, предназначенное для размещения в нем электрических кабелей.', 'stroit.ru'),
('Кабельный колодец', 'Кабельная камера, имеющая люк для входа в нее.', 'stroit.ru'),
('Кабельный лоток', 'Открытая конструкция, предназначенная для прокладки проводов и кабелей.', 'stroit.ru'),
('Калибр', 'Измерительный прибор – средство комплексного контроля, используемый при приемке готовых деталей и предназначенный для проверки размеров и формы изделий или взаимного расположения их частей. С его помощью определяют, находятся ли в допускаемых пределах размеры деталей или сборочных единиц, например, сопрягаемых винтовых поверхностей (болт и гайка) на длине свинчивания. Различают: регулируемые, нерегулируемые, непроходные, рабочие, приемные и другие калибры.', 'stroit.ru'),
('Калорифер', 'Калорифер – прибор для нагревания воздуха в помещение, состоящий из труб, по которым циркулирует горячая вода, пар или горячий воздух.', 'stroit.ru'),
('Каменные конструкции', 'Части зданий и сооружений (фундаменты, стены, перекрытия, арки, дымовые трубы и т.п.), возведенные, как правило, из местного сырья.', 'stroit.ru'),
('Камень бутовый', 'Камень бутовый - куски природного камня размером 150–500 мм. Камень бутовый может быть рваным или плитняковым. Применяют камень бутовый для устройства фундаментов, кладки стен некоторых сооружений, отсыпки или бетонирования определенных частей гидротехнических сооружений.', 'stroit.ru'),
('Камень керамический', 'Камень керамический - пустотелый строительный материал изготавливаемый из глинистого сырья с различными добавками. Камень керамический отличается от кирпича несколько большими размерами. По средней плотности камень керамический подразделяется на эффективные (не более 1450 кг/ м куб.) и условно эффективные (1450–1600 кг/ м куб.). Камень керамический различается также по марке, плотности и морозостойкости.', 'stroit.ru'),
('Камень поризованный', 'Камень поризованный – промежуточное положение между тяжёлыми и лёгкими бетоном занимает крупнопористый (беспесчаный) бетон, изготовляемый на плотном крупном заполнителе с поризованным при помощи газо- или пенообразователей цементным камнем.', 'stroit.ru'),
('Камин', 'Камин - открытый очаг, предназначенный для обогрева помещения. К основным частям камина относятся: топливник – ниша, в которой происходит сгорание топлива; под – своего рода колосник, на который укладывают топливо; дымоход – дымовая труба, отводящая продукты сгорания; зольник – выдвижная металлическая емкость для сбора золы. Камин обладает малым КПД (5–20%), поэтому имеет в основном декоративное значение. К положительным качествам камина можно отнести усиленную вытяжку комнатного воздуха, что хорошо вентилирует помещение.', 'stroit.ru'),
('Канализация', 'Канализация – система труб или подземных каналов, служащая для удаления, очистки и обезвреживания сточных вод.', 'stroit.ru'),
('Канелюры', 'Канелюры – вертикальные и горизонтальные желоба на колоннах (вертикальные) и базах колонн (горизонтальные).', 'stroit.ru'),
('Канефора', 'Канефора - органично вписанное в архитектуру здания скульптурное изображение женской фигуры, играющее конструктивную роль. Канефора выполняет функции колонны.', 'stroit.ru'),
('Каолиновая вата', 'Каолиновая вата и изделия на ее основе – высокотемпературная (температура применения до 1100-1250 град. C) теплоизоляция, применяемая в различных отраслях промышленности. Сырьем для ее производства служат технический глинозем и кварцевый песок. Средняя плотность каолиновой ваты 80 кг/куб. м. Она устойчива к вибрации, инертна к воде, водяному пару, маслам и кислотам, обладает высокими электроизоляционными свойствами, практически не меняющимися с повышением температуры до 700-800 град. C. Каолиновая вата поставляется в рулонах и в виде изделий различной формы.', 'stroit.ru'),
('Капитальное строительство', 'Строительство любых объектов, для возведения которых требуется проведение земляных и строительно-монтажных работ по устройству заглубленных фундаментов, возведению несущих и ограждающих конструкций, подводке инженерных коммуникаций.', 'stroit.ru'),
('Капитальный ремонт', 'Комплекс строительных и организационно-технических мероприятий, направленных на устранение физического износа сооружения, не связанный с изменением основных технико-экономических показателей здания и его функционального назначения.', 'stroit.ru'),
('Капитель', 'Конструктивно капитель – верхняя часть вертикальной опоры (стойки, колонны, пилястры), воспринимающая нагрузку от горизонтальных балок перекрытия. Из античной архитектуры известны четыре основных типа капитель дорическая, капитель ионическая, капитель коринфская и, являющаяся соединением последних двух – композитная капитель.', 'stroit.ru'),
('Каптаж', 'Колодец или приемная камера для сбора подземных родниковых вод в местах их выхода на поверхность.', 'stroit.ru'),
('Каре', 'Квадратное в плане здание с прямоугольным внутренним двором.', 'stroit.ru'),
('Каркасные железобетонные конструкции', 'Железобетонные конструктивные системы, состоящие из: колонн; перекрытий; устоев, образующих единый несущий каркас сооружения.', 'stroit.ru'),
('Карниз', 'Карниз – горизонтальный выступ на стене, поддерживающий крышу здания и защищающий стену от стекающей воды.', 'stroit.ru'),
('Картуш', 'Картуш – украшение в виде щита или не до конца развернутого свитка, на котором помещается герб, эмблема или надпись.', 'stroit.ru'),
('Карьер', 'Место добычи полезных ископаемых открытым способом.', 'stroit.ru'),
('Каток дорожный', 'Каток дорожный – механизм для уплотнения укатыванием грунтов, дорожных оснований и покрытий.', 'stroit.ru'),
('Кафель', 'Кафель - облицовочная керамическая плитка.', 'stroit.ru'),
('Каширование', 'Каширование – облицовка поверхности плиты (ДВП, MDF, HDF) декоративной бумагой (пленкой), пропитанной смолами, под давлением с последующим отвердением клеевого слоя различными способами, но без использования горячего процесса.', 'stroit.ru'),
('Квадр', 'Квадр - отесанный каменный блок, имеющий форму прямоугольного параллелепипеда. Квадр – элемент каменной стены.', 'stroit.ru'),
('Квартал', 'Ограниченная с четырех сторон улицами часть застроенной территории населенного пункта, предполагающая целостность архитектурного замысла и соблюдение экономических противопожарных и санитарно-гигиенических требований.', 'stroit.ru'),
('Кварциты', 'Плотные и крепкие зернистые горные породы, состоящие почти полностью из кремнезема. Используются как строительный камень, абразивный и кислотоупорный материал, в виде флюса в металлургии и для производства огнеупорного кирпича.', 'stroit.ru'),
('Кельма', 'Инструмент для выполнения каменных работ. Стальная лопатка с деревянной ручкой. Существует несколько типов кельм. КБ (кельма для бетонных и каменных работ)– применяется для разравнивания раствора и бетонной смеси, заполнения вертикальных швов и подрезки раствора, выступающего из швов. КП (кельма для печных и каменных работ)– применяется для разравнивания раствора, заполнения вертикальных швов и подрезки раствора, выступающего из швов; для разравнивания раствора при кладке вентиляционных и дымовых каналов. КШ (кельма для штукатурных работ)– применяется для набрасывания и разравнивания раствора. КО (кельма для отделочных работ)– применяется для набрасывания, разравнивания раствора и выполнения других операций при производстве отделочных работ.', 'stroit.ru'),
('Кемпас', 'Твердолиственная порода деревьев, произрастающих в Юго-Восточной Азии. Красная с золотистыми, желтыми и оранжевыми оттенками древесина имеет ровную текстуру со светлыми прожилками. Отличается твердостью, плотностью, влагоустойчивостью.', 'stroit.ru'),
('Керамзит', 'Керамзит - искусственный заполнитель для легких бетонов (керамзитобетон). Керамзит получается в результате вспучивания гранул легкоплавких глинистых пород при их обжиге. Керамзит выпускается в виде щебня или гравия диаметром 5–40 мм. Кроме того, керамзит используется в качестве теплоизоляционной засыпки.', 'stroit.ru'),
('Керамика', 'Материалы и изделия из минерального сырья (глины), полученные путем обжига при высоких температурах.', 'stroit.ru'),
('Керамическая плитка для полов, метлахская плитка', 'Обожженные до спекания плитки из глины с добавлением красителей. Лицевая сторона может быть рифленой, гладкой, со вдавленным рисунком и т. п. Укладывается на цементно-песчаную стяжку или специальные мастики. Название «метлахская плитка» произошло от наименования германского города Метлах, в котором было организовано первое промышленное производство.', 'stroit.ru'),
('Керамическая плитка облицовочная', 'Обожженные до спекания плитки, применяемые для облицовки стен. Различают фарфоровые, фаянсовые и гончарные плитки. Лицевая сторона имеет рифленую покрытую глазурью поверхность, внутренняя – насечку для лучшего сцепления с раствором или мастикой.', 'stroit.ru'),
('Керамические санитарные изделия', 'Глазурованные изделия, изготовленные из смеси белых глин и минералов, обожженные при высокой температуре и предназначенные для санитарно-гигиенического и хозяйственного применения путем приема и смыва загрязнений водой.', 'stroit.ru'),
('Кератофир', 'Эффузивная или жильная щелочная горная порода с порфировыми выделениями полевого шпата и цветных минералов', 'stroit.ru'),
('Керосин', 'Продукт перегонки тяжелых нефтепродуктов. Представляет собой смесь углеводородов с температурой кипения 110–320 град. С. Применяется как топливо и в качестве растворителя.', 'stroit.ru'),
('Киперная лента', 'Хлопчатобумажная лента саржевого или диагонального переплетения, суровая или гладкокрашенная. Применяется в электромонтажных работах (например, в концевых заделках силовых кабелей для герметизации жил и для изоляции обмоток электрических машин), для производства армейской одежды и снаряжения, окантовки краев швов верхней одежды, упаковки изделий, изготовлении канцелярских товаров (папок для бумаг), переплетении книг и т. д.', 'stroit.ru'),
('Кирпич керамический', 'Каменный штучный пустотелый или полнотелый строительный материал правильной формы, изготавливаемый из глинистого сырья путем обжига. Подразделяется на марки по прочности (от 75 до 300) и морозостойкости (от 15 до 50). По теплотехническим свойствам и плотности выделяют эффективный (1450 кг/м куб.), условно эффективный (1450–1600) и обыкновенный кирпич (свыше 1600).', 'stroit.ru'),
('Кирпич керамический двухслойный', 'Облицовочный кирпич, тонкий (3–5 мм) наружный слой которого состоит из глин приобретающих при обжиге розовато-бежевый оттенок. Тело такого кирпича состоит из обычных глин, применяемых для производства красного керамического кирпича. Имеет и другое название: кирпич ангобированный.', 'stroit.ru'),
('Кирпич клинкерный', 'Кирпич клинкерный - керамический строительный материал, изготовленный из глинистого сырья и обожженный до полного спекания. Кирпич клинкерный используется для мощения.', 'stroit.ru'),
('Кирпич лекальный', 'Изготовленный по специальным рисункам и вытесанный по лекалу кирпич, применяемый для декоративной отделки карнизов, углов, арок. На профессиональном языке каменщиков – штучный набор.', 'stroit.ru'),
('Кирпич лицевой', 'Кирпич лицевой – кирпич правильной формы, пригодный для наружных работ, имеющий четкие грани, однородную окраску, выдерживающий воздействие атмосферных осадков и пригодный для наружных работ.', 'stroit.ru'),
('Кирпич облицовочный', 'Кирпич облицовочный – фактурный или фасонный лицевой кирпич.', 'stroit.ru'),
('Кирпич огнеупорный', 'Кирпич огнеупорный – белый, ровный кирпич, изготовленный из тугоплавкой глины, обладающий большой прочностью и повышенной огнестойкостью.', 'stroit.ru'),
('Кирпич полнотелый', 'Кирпич с минимальным объемом пустот (по ГОСТ 530-95 менее 13 %). Используется для кладки внутренних и внешних стен, создания колонн, столбов и других различных конструкций, которые кроме собственного веса несут дополнительную нагрузку.', 'stroit.ru'),
('Кирпич пустотелый', 'Кирпич пустотелый – полый кирпич со сквозными каналами или полыми пространствами внутри.', 'stroit.ru'),
('Кирпич рядовой', 'Кирпич рядовой – кирпич, используемый для внутренних рядов кладки или для внешних рядов с последующей штукатуркой. Рядовой кирпич может иметь на боковой стороне вдавленный геометрический рисунок для лучшего сцепления со штукатурным раствором.', 'stroit.ru'),
('Кирпич силикатный', 'Кирпич силикатный - строительный материал, изготавливаемый из смеси кварцевого песка и извести. Кирпич силикатный применяют при возведении несущих стен многоэтажных зданий.', 'stroit.ru'),
('Кирпич строительный', 'Кирпич строительный – искусственный камень правильной формы, сформированный из минеральных материалов и приобретающий прочность, водостойкость и морозостойкость после обжига или обработки паром.', 'stroit.ru'),
('Кирпич фактурный', 'Кирпич фактурный – облицовочный кирпич с рельефным рисунком на лицевой поверхности', 'stroit.ru'),
('Кирпич фасадный', 'Кирпич фасадный – см. кирпич лицевой', 'stroit.ru'),
('Кирпич фасонный', 'Кирпич фасонный – облицовочный кирпич, предназначенный для кладки сложных форм: окон, подоконников, арок, столбов, заборов и т.п.', 'stroit.ru'),
('Кирпич цветной', 'Кирпич цветной – кирпич, окрашенный в тот или иной цвет на стадии производства.', 'stroit.ru'),
('Кирпич шамотный', 'Кирпич шамотный – огнеупорный кирпич, выдерживающий температуру до 1600 град.С.', 'stroit.ru'),
('Киянка', 'Столярный инструмент, молоток из дерева твердых пород. Используется для работы с долотами и стамесками, что предохраняет рукоятки режущих инструментов от повреждений.', 'stroit.ru'),
('Кладка каменная', 'Кладка, состоящая из уложенных в определенном порядке (с перевязкой) камней или кирпичей. Основная цель перевязки – придание конструкции монолитности путем укладки камней каждого верхнего ряда таким образом, чтобы вертикальные швы между ними не совпадали с вертикальными швами между камнями нижнего ряда. Существует несколько систем перевязки: многорядная (римская); двухрядная (цепная); крестовая; готическая (польская) и голландская.', 'stroit.ru'),
('Кладка полигональная', 'Каменная кладка цокольной части стены здания, выполненная из притесанных друг к другу многоугольных камней.', 'stroit.ru'),
('Клееный брус', 'Несущий конструктивный элемент, изготовленный из деревянных досок, склеенных вместе в параллельном направлении волокон.', 'stroit.ru'),
('Клеть', 'Клеть - прямоугольный в плане бревенчатый сруб. Также клеть - холодная (без отопления) изба, кладовая, амбар.', 'stroit.ru'),
('Клинкер', 'Продукт равномерного обжига измельченной сырьевой смеси (известняка и глины). Используется при производстве цемента.', 'stroit.ru'),
('Когезия', 'Сцепление между находящимися в контакте поверхностями двух однородных по составу тел.', 'stroit.ru'),
('Койланаглиф', 'Рельеф с углубленным контуром и выпуклой моделировкой, встречающийся в архитектуре Древнего Египта, античности и древнего Востока.', 'stroit.ru'),
('Кокора, курица', 'Кокора - в деревянном зодчестве ствол, как правило – ели, с одним ответвленным корнем, образующим крюк. Кокора использовалась в качестве стропила при устройстве безгвоздевых кровель.', 'stroit.ru'),
('Коллектор', 'В электротехнике – часть ротора электрической машины, состоящая из ряда изолированных друг от друга медных пластин, служащая электромеханическим преобразователем частоты тока. В коммунальном хозяйстве – участок канализационной сети, собирающий сточные, воды из бассейнов канализования. Подразделяются на: коллекторы бассейна канализования (принимают сточные воды из канализационной сети одного бассейна); главные коллекторы (собирают сточные воды из двух или нескольких коллекторов бассейнов канализования); загородные, или отводные, коллекторы (отводят сточные воды транзитом (без присоединений) за пределы объекта канализования к насосным станциям, очистным сооружениям или к месту выпуска в водоем).', 'stroit.ru'),
('Колонна', 'Колонна - вертикальная линейная конструкция, высота которой значительно превышает ее поперечное сечение. Колонна предназначенная для восприятия вертикальных (в меньшей степени – горизонтальных) нагрузок.', 'stroit.ru'),
('Комель', 'Нижняя, толстая часть бревна. Бревно, заготовленное из нижней части ствола называется комлевым бревном.', 'stroit.ru'),
('Конвектор', 'Конвектор – отопительный прибор, основанный на принципе рециркуляции горячего и холодного воздуха.', 'stroit.ru'),
('Конек', 'Конек - верхнее горизонтальное ребро крыши. Конек образовывается пересечением двух кровельных скатов.', 'stroit.ru'),
('Консоль', 'Консоль - часть балки или иной конструкции. Консоль выступает из плоскости стены.', 'stroit.ru'),
('Контргайка', 'Контргайка – вторая гайка, навинчиваемая вслед за основной на тот же болт и препятствующая самоотвинчиванию первой.', 'stroit.ru'),
('Контрфорс', 'Контрфорс - вертикальная опора, расположенная с наружной стороны стены и воспринимающая боковой распор. Сечение контрфорса увеличивается по мере приближения к основанию по треугольнику или ступенчато. В случае возникновения сравнительно небольших нагрузок, сечение контрфорса может быть постоянным, что внешне приближает контрфорс к пилястре.', 'stroit.ru'),
('Кора выветривания', 'Рыхлый поверхностный слой горных пород, образовавшийся в результате выветривания. Как правило, кора выветривания имеет глинистый состав.', 'stroit.ru'),
('Коррозия', 'Необратимый процесс ухудшения характеристик и свойств строительного материала в конструкции в результате химического и (или) физико-химического и (или) биологического воздействий или процессов в самом материале.', 'stroit.ru'),
('Коррозия бетона', 'Ухудшение характеристик и свойств бетона в результате вымывания (выщелачивания) из него растворимых составных частей (коррозия первого вида); образования продуктов коррозии, не обладающих вяжущими свойствами (коррозия второго вида), и накопления малорастворимых кристаллизующихся солей, увеличивающих объем его твердой фазы (коррозия третьего вида).', 'stroit.ru'),
('Коррозия железобетона', 'Разрушение железобетона в результате коррозии бетона и (или) арматуры.', 'stroit.ru'),
('Косоур', 'Косоур - наклонная несущая балка, на которую опираются ступени лестничного марша. В промышленном домостроении сборный лестничный марш изготавливается из железобетона полностью (и косоуры, и ступени) и также называется косоур.', 'stroit.ru'),
('Котлован', 'Котлован – искусственное углубление в грунте, предназначенное для устройства оснований и фундаментов зданий и других инженерных сооружений.', 'stroit.ru'),
('Коттедж', 'Коттедж – загородный индивидуальный одно- двух-этажный жилой дом с приусадебным участком.', 'stroit.ru'),
('Кошель', 'Кошель - распространенный в северных территориях России тип крестьянского дома. Кошель подразумевает параллельное расположение жилой и хозяйственной зон, находящихся под одной несимметричной крышей.', 'stroit.ru'),
('Кран', 'Водоразборное устройство, обеспечивающее получение воды из системы водоснабжения и регулирование расхода воды потребителем.', 'stroit.ru'),
('Кран грузоподъемный', 'Машина цикличного действия, предназначенная для подъема и перемещения груза в пространстве, подвешенного с помощью крюка или удерживаемого другим грузозахватным органом.', 'stroit.ru'),
('Кран кабельного типа', 'Кран, у которого грузозахватный орган подвешен к грузовой тележке, перемещающейся по несущим канатам, закрепленным на двух опорах.', 'stroit.ru'),
('Кран кабельный', 'Кран, у которого несущими элементами являются канаты, закрепленные в верхней части опорных мачт (башен).', 'stroit.ru'),
('Кран кабельный мостовой', 'Кран, у которого несущими элементами являются канаты, закрепленные на концах моста, установленного на опорных стойках.', 'stroit.ru'),
('Кран козловой', 'Кран, у которого несущие элементы конструкции опираются на крановый путь при помощи двух опорных стоек.', 'stroit.ru'),
('Кран мостовой', 'Кран, у которого несущие элементы конструкции опираются непосредственно на крановый путь.', 'stroit.ru'),
('Кран настенный', 'Кран стационарный, прикрепленный к стене либо перемещающийся по надземному крановому пути, закрепленному на стене или несущей конструкции.', 'stroit.ru'),
('Краски масляные', 'Суспензии пигментов и наполнителей в олифах. Выпускаются густотертыми или готовыми к употреблению.', 'stroit.ru'),
('Краски минеральные', 'Окрасочные составы на основе неорганических вяжущих и клея. Подразделяются на известковые, силикатные, цементные и клеевые.', 'stroit.ru'),
('Краски полимерцементные', 'Суспензия сухой пигментной части, состоящей из белого портландцемента, строительной молотой извести, светостойких, щелочестойких пигментов и наполнителей в водной дисперсии полимера - пластифицированной поливинилацетатной эмульсии или синтетических латексов, устойчивых к цементу. Предназначаются для наружной и внутренней отделки зданий по бетонным, газобетонным, кирпичным, асбестоцементным и оштукатуренным поверхностям и древесноволокнистым плитам, а также для отделки железобетонных панелей в заводских условиях. Наносят при температуре не ниже плюс 2 град. Цельсия.', 'stroit.ru'),
('Краски силикатные', 'Суспензия щелочестойких пигментов и наполнителей в виде сепарированного мела и талька, силикатизаторов в виде сухих цинковых белил или бората кальция (сухая пигментная часть) в водном растворе высокомодульного силиката калия (жидкое калийное стекло). Силикатные краски предназначаются для наружной и внутренней отделки зданий и сооружений путем окраски кирпичных, бетонных и оштукатуренных поверхностей.', 'stroit.ru'),
('Краски цементные', 'Водные суспензии, состоящие из смеси белого портландцемента со щелочестойкими пигментами и некоторыми добавками для улучшения свойств.', 'stroit.ru'),
('Красная линия', 'В градостроительстве красная линия – линия, отделяющая застроенную территорию от незастроенного пространства (площадь, улица). За красную линию не должно выходить ни одно из строений квартала.', 'stroit.ru'),
('Креномер', 'Прибор, показывающий угол наклона подъемника.', 'stroit.ru'),
('Кренты', 'Добавки, вводимые в состав цемента при помоле для улучшения его свойств.', 'stroit.ru'),
('Кровельная асбестоцементная плитка', 'Плоское асбестоцементное изделие, длина и ширина которого не более 600 мм, а толщина не более 5 мм.', 'stroit.ru'),
('Кровля', 'Верхняя часть крыши, состоящая из деревянной обрешетки и наружного покрытия. В качестве покрытия применяются самые разнообразные материалы: от дранки и черепицы до современных пластиковых материалов.', 'stroit.ru'),
('Кронциркуль', 'Измерительное средство для сравнения наружных линейных размеров деталей с размерами, взятыми по масштабной линейке, концевым мерам или калибру. Состоит из двух соединенных шарнирно дугообразных ножек. Иногда имеет шкалу. Второе значение – чертёжный инструмент для вычерчивания окружностей.', 'stroit.ru'),
('Кружала', 'Кружала - деревянная форма, поддерживающая опалубку, по которой возводятся арочные, сводчатые и купольные конструкции. Кружала также используют при возведении сводчатой части каминов.', 'stroit.ru'),
('Крупноблочные конструкции', 'Объемные блоки заводского изготовления представляющие собой части сооружения: кухня, санузел, лифтовая шахта и т.п.', 'stroit.ru'),
('Крыша', 'Верхнее покрытие сооружения, защищающее его от воздействия внешней среды. Состоит из несущей части – стропил, подстропильных балок и в ряде случаев стоек и наружного слоя (см. кровля). Наклонные плоскости крыши называются скатами; внутренние углы, образованные скатами – ендовами; наружные углы – ребрами; верхнее горизонтальное ребро – коньком.', 'stroit.ru'),
('Ксилито-волокнистый лист (КВЛ)', 'Строительно-отделочный материал. Изготавливается из древесных отходов, агломенированных минеральными связующими веществами. С обеих сторон армирован тканью из стекловолокна. Используется для монтажа стен, перегородок, колонн, арочных конструкций, отделки потолочных и стеновых поверхностей, полов и т. п.', 'stroit.ru'),
('Купол', 'Покрытие в форме полушария или опрокинутой чаши, возведенное над круглым или многоугольным в плане сооружением.', 'stroit.ru'),
('Курдонер', 'Курдонер - ограниченный главным корпусом и боковыми флигелями парадный двор перед особняком. По красной линии курдонер отделяется от наружного пространства сквозной оградой с воротами.', 'stroit.ru'),
('Курица', 'См. кокора.', 'stroit.ru'),
('Кювета (кюветка)', 'Лабораторное оборудование – ванночка для работы с химикатами.', 'stroit.ru'),
('Лаги', 'Лаги - горизонтально расположенные бревна, брусья или металлические балки. Лаги являются опорой для полов здания или помостов.', 'stroit.ru'),
('Лаки', 'Растворы пленкообразующих веществ в органических растворителях. Могут содержать пластификатор, отвердитель и другие добавки, улучшающие качество покрытия.', 'stroit.ru'),
('Ламбрекен', 'Ламбрекен - украшающая верхнюю часть оконного или дверного проема поперечная драпировка из плотной тяжелой ткани с воланами, складками, кистями. Также ламбрекен - резное деревянное украшение того же назначения.', 'stroit.ru'),
('Ламинат', 'Слоистый пластик на основе ПВХ-смолы. Наносится на поверхность ДСП под давлением, имитирует текстуру природного камня (мрамор, гранит, габбро и т.д.), древесных пород (бук, дуб, груша, орех, красное и чёрное дерево и др.). Отличается повышенной температуростойкостью, малой истираемостью.', 'stroit.ru'),
('Ламинирование', 'Ламинирование – облицовка поверхности плиты декоративной бумагой (пленкой), пропитанной смолами, методом горячего прессования, при котором волокна плиты и бумаги проникают друг в друга.', 'stroit.ru'),
('Ландшафт', 'Участок поверхности земли, в пределах которого все природные компоненты находятся во взаимосвязанном единстве.', 'stroit.ru'),
('Лапа, рубка «в лапу»', 'Один из способов рубки углов бревенчатых строений. Рубка "в лапу" или "без остатка" характерна тем, что торцы соединяемых в углах бревен (брусьев) не выходят за наружную плоскость стены. По сравнению с рубкой «в оболо» менее эффективна с точки зрения теплосбережения.', 'stroit.ru'),
('Леса строительные', 'Временные вспомогательные сооружения для размещения рабочих и материалов при выполнении строительно-монтажных, отделочных и других работ преимущественно снаружи здания. Получили свое название, когда материалом для их изготовления служил круглый или вчерне обработанный лес. В настоящее время изготавливаются металлическими. В конструктивном отношении представляют собой пространственную каркасную систему, выполненную из унифицированных элементов, что делает возможным их универсальное использование независимо от очертаний в плане зданий (сооружений) и рельефа местности. Различают инвентарные стоечные, подвесные (висячие), передвижные и иные типы строительных лесов.', 'stroit.ru'),
('Лессировка', 'Тонкие прозрачные (полупрозрачные) слои красок, наносимые на просохшие или просыхающие краски, с целью изменения, (усиления или ослабления) цветовых тонов.', 'stroit.ru'),
('Лестница', 'Конструктивный элемент, соединяющий этажи здания. Состоит из наклонных маршей, этажных (на одной отметке с этажом) и промежуточных (междуэтажных) лестничных площадок. По конфигурации лестницы разделяются на прямые, ломаные, криволинейные, винтовые.', 'stroit.ru'),
('Лестничная клетка', 'Лестничная клетка – вертикальный проем в перекрытиях здания, предназначенный для монтажа лестничных маршей.', 'stroit.ru'),
('Лестничный пролет', 'Лестничный пролет – пространство, ограниченное лестничными площадками.', 'stroit.ru'),
('Лещадь, лещадная плита', 'Лещадь - тонкие каменные плиты, применяющиеся для верхнего настила пола.', 'stroit.ru'),
('Ливнеспуск', 'Сооружение на канализационной сети, предназначенное для сброса избытков дождевых вод в приемник сточных вод.', 'stroit.ru'),
('Линолеум', 'Рулонный полимерный материал для покрытия полов. Первоначально изготавливался на джутовой основе из растительных масел и пробковой муки (глифталевый линолеум). В настоящее время выпускают линолеумы на основе синтетических смол.', 'stroit.ru'),
('Лист асбестоцементный волнистый', 'Асбестоцементный лист, форма которого основана на повторяющемся чередовании выпуклых и вогнутых участков с прямыми продольными образующими. Волной асбестоцементного листа называют его часть, ограниченную образующими, отстоящими друг от друга на величину, равную шагу волны. – Шаг волны асбестоцементного листа – расстояние между двумя ближайшими образующими лицевой поверхности асбестоцементного волнистого листа, имеющими общую касательную.', 'stroit.ru'),
('Лист асбестоцементный плоский', 'Плоское монолитное прямоугольное изделие, толщина которого, как правило, составляет от 4 до 25 мм. Может быть: - прессованным (лист, изготовленный с применением дополнительного уплотнения полуфабриката давлением после снятия с форматного барабана) или непресcованным (лист, не подвергшийся дополнительному уплотнению давлением после снятия с форматного барабана); - калиброванным (лист, калиброванный по длине и ширине путем дополнительной обработки в затвердевшем состоянии) и некалиброванным (лист, разрезанный в незатвердевшем состоянии): - огнестойким (обладающим повышенным сопротивлением теплопередаче; используется для защиты строительных конструкций от воздействия огня при пожаре); - гибким (обладающим повышенной пластичностью; используется для облицовки поверхностей различной формы); - перфорированным (плоский асбестоцементный лист, значительную площадь которого занимают отверстия).', 'stroit.ru'),
('Лифт', 'Стационарный подъемник обычно прерывного действия с вертикальным движением кабины или платформы по жестким направляющим, установленным в шахте. В зависимости от назначения, высоты подъема, расположения лебедок, планировки и конструкции зданий лифты имеют различные кинематические схемы: с непосредственной подвеской кабины и противовеса, с полиспастной подвеской кабины и противовеса, выжимные с полиспастной подвеской кабины и т. д.', 'stroit.ru'),
('Лицензирование строительной деятельности', 'Разрешение, которое выдают уполномоченные компетентные органы на ведение изыскательских, проектных, строительно-монтажных и тому подобных работ.', 'stroit.ru'),
('Лобовая доска', 'В деревянном зодчестве украшенная резьбой доска, закрывающая переход от бревен стены к доскам фронтона избы.', 'stroit.ru'),
('Лоджия', 'Лоджия – часть здания, открытая с одной или нескольких сторон (балкон или вход, углубленные в стену).', 'stroit.ru'),
('Лондонская штора', 'Лондонская штора – штора из мягкой ткани, основание которой в поднятом положении образует свободную гирлянду.', 'stroit.ru'),
('Люверс', 'Люверс – круглое отверстие в парусе, парусиновом тенте, чехле, шторе и т.п., служащее для пропуска снастей. Для предохранения от разрыва люверс обметывается ниткой.', 'stroit.ru'),
('Люверсы', 'Люверсы – отверстия в верхней части шторы, окантованные металлом, сквозь которые продевается карниз.', 'stroit.ru'),
('Люкарна', 'Люкарна - чердачное окно.', 'stroit.ru'),
('Люлька', 'Площадка, имеющая ограждение, предназначенная для размещения людей с инструментом и материалами.', 'stroit.ru'),
('Люминофор', 'Люминофор – вещество, излучающее свет при воздействии на него электромагнитного излучения. Используется в лампах дневного света, электронно-лучевых трубках и т.п.', 'stroit.ru'),
('Люнет', 'Люнет – ограниченный горизонтально снизу арочный проем в своде или стене.', 'stroit.ru'),
('Люстр', 'Люстр - тонкая прозрачная пленка. Люстр наносится на глазурованную поверхность фарфоровых и фаянсовых изделий для придания им после обжига радужного металлического блеска.', 'stroit.ru'),
('Майолика', 'Майолика - крупнопористые керамические изделия с росписью по сырой непрозрачной оловянной глазури, соединяющейся при обжиге с красками. К майолике относят испанскую и итальянскую керамику XV–XVI веков.', 'stroit.ru'),
('Малахит', 'Малахит - мелкокристаллический водный карбонат меди, один из красивейших минералов. Малахит характерен широкой гаммой зеленых тонов – от светло-зеленого с голубизной («бирюзовый») до черно-зеленого. Используется малахит для облицовки колонн, столешниц, каминов, ваз, торшеров и т. п.', 'stroit.ru'),
('Манжета', 'Уплотнение в соединениях деталей машин, служащее для предотвращения протекания жидкости или газа из области высокого в область более низкого давления. Выполняется в виде кольца из резины, кожи или иного материала.', 'stroit.ru'),
('Манометр', 'Прибор для измерения давления жидкостей и газов. Различают манометры для измерения абсолютного давления, отсчитываемого от нуля (полного вакуума) и манометры для измерений избыточного давления (то есть разности между абсолютным и атмосферным давлением, когда абсолютное давление больше атмосферного). Для измерения давления, соответствующего атмосферному, применяют барометры, а для измерения давления разреженных газов — вакуумметры. Основа измерительной системы манометра — чувствительный элемент, являющийся первичным преобразователем давления. В зависимости от принципа действия и конструкции чувствительного элемента различают жидкостные, поршневые, деформационные (пружинные) манометры. Кроме того, находят применение приборы, действие которых основано на измерении изменений физических свойств различных веществ под действием давления.', 'stroit.ru'),
('Манометр дифференциальный', 'Манометр, предназначенный для измерения разности двух давлений.', 'stroit.ru'),
('Мансарда', 'Мансарда - чердачное помещение, оборудованное для жилых нужд. Названо по фамилии французского архитектора Ф. Мансара (1646–1708). Устройство мансарды дает возможность получить дополнительную полезную площадь в здании.', 'stroit.ru'),
('Марка бетона по прочности', 'Определяется пределом прочности на сжатие образцов размером 150x150x150 мм, изготовленных из рабочего состава и испытанных через 28 суток нормального твердения.', 'stroit.ru'),
('Марка кирпича', 'Показатель прочности, определяющий нагрузку (в кг) на 1 кв.см, которую может выдержать кирпич. В промышленности выпускается кирпич марок: 75, 100, 125, 150, 200, 250, 300.', 'stroit.ru'),
('Марка цемента', 'Показатель прочности изделий из цемента на изгиб и сжатие. Различают марки 200, 300, 400, 500, 550 и 600.', 'stroit.ru'),
('Маркетри', 'Маркетри - мозаичные изображения, выполненные путем наклеивания на основу тонких фигурных пластинок фанеры из различных пород древесины. В ряде случаев в маркетри добавляются пластинки из слоновой кости, перламутра, металла. Маркетри широко используется при изготовлении мебели.', 'stroit.ru'),
('Матица', 'В деревянных постройках матица - главная несущая балка, поддерживающая потолочный настил.', 'stroit.ru'),
('Мауэрлат', 'Мауэрлат - брус, служащий опорой наслонных деревянных стропил и предназначенный для распределения нагрузки, создаваемой крышей сооружения. Мауэрлат располагается на верхнем внутреннем обрезе каменных стен.', 'stroit.ru'),
('Машина грузоподъемная', 'Подъемное устройство цикличного действия с возвратно-поступательным движением грузозахватного органа.', 'stroit.ru'),
('Мегаполис', 'Очень крупный как по занимаемой площади, так и по численности населения город, образовавшийся за счет присоединения соседних населенных пунктов.', 'stroit.ru'),
('Мезонин', 'Мезонин - надстройка над средней частью небольшого жилого дома. Часто мезонин выполняется с балконом.', 'stroit.ru'),
('Мембрана', 'Покрытие сооружения – пространственная конструкция в виде гибкой провисающей оболочки, закрепленной по верхнему периметру сооружения.', 'stroit.ru'),
('Мергель', 'Мергель представляет собой известково-глинистую породу, содержащую 35-65% глинистого вещества. Мергель широко используется как сырье для производства портландцемента. Мергель малоустойчив к атмосферным воздействиям.', 'stroit.ru'),
('Металлочерепица', 'Металлочерепица представляет собой профилированный оцинкованный стальной лист, с двух сторон покрытый полимерными защитными, декоративными составами. Внешне металлочерепица напоминает традиционную керамическую черепицу.', 'stroit.ru'),
('Метизы', 'Изделия различного назначения, изготовленные из стальной проволоки, катанки или ленты (сварочные электроды, сетки, тяжи, крепежные изделия).', 'stroit.ru'),
('Метчик', 'Инструмент для нарезания внутренней резьбы в предварительно просверленных отверстиях. Представляет собой закаленный винт с прорезанными вдоль стержня канавками, образующими режущие кромки. По назначению метчики подразделяются на ручные, машинно-ручные и машинные; по профилю нарезаемой резьбы – на метчики для метрической, дюймовой и трубной резьб; по конструкции – на цельные, сборные и специальные.', 'stroit.ru'),
('Миксбордер', 'Миксбордер - вытянутой формы цветник. Миксбордер включают набор разнообразных многолетних растений, обеспечивающих непрерывное цветение.', 'stroit.ru'),
('Мобильное (инвентарное) здание (сооружение)', 'Здание или сооружение комплектной заводской поставки, конструкция которого обеспечивает возможность его передислокации.', 'stroit.ru'),
('Модерн', 'Модерн - архитектурный стиль конца XIX – начала XX вв. Стиль модерн появился благодаря применению новых технико-конструктивных средств. Модерн характерен свободной планировкой, что создает подчеркнуто индивидуальный облик зданий.', 'stroit.ru'),
('Мозаика', 'Мозаика - изображение или узор, выполненные из цветных камней, смальты (разноцветных кусочков стеклянных сплавов), цветной керамической плитки и т. п. Используется мозаика в основном для украшения зданий.', 'stroit.ru'),
('Молдинг', 'Молдинг – переходный декоративный профиль, служащий для заделки стыков декоративных облицовочных плит, панелей и т.п.', 'stroit.ru'),
('Молибден', 'Светло-серый тугоплавкий металл, химический элемент шестой группы периодической системы Менделеева. Открыт в 1778 г. шведским химиком К. Шееле. Помимо молибденовых руд, источником молибдена служат также некоторые молибденосодержащие медные и медно-свинцово-цинковые руды. Основная часть добываемого молибдена идет на производство легированных сталей. Кроме того, металлический молибден применяется при производстве электроосветительных ламп и электровакуумных приборов; молибденовые проволока и лента широко используются в качестве нагревателей для высокотемпературных печей.', 'stroit.ru'),
('Молоток отбойный', 'См. «отбойный молоток».', 'stroit.ru'),
('Момент грузовой', 'Произведение величии вылета L и соответствующей ему грузоподъемности Q.', 'stroit.ru'),
('Монокоттура', 'Монокоттура - эмалированные обожжённые плитки с цветной или белой эмалью, сформованные путем прессования. Технология производства монокоттуры, предусматривает лишь одну процедуру обжига, которому подвергаются одновременно и основа, и эмаль.', 'stroit.ru'),
('Морозостойкость', 'Способность материала после насыщения его водой выдерживать определенное количество циклов замораживания – оттаивания без ухудшения свойств ниже установленного предела.', 'stroit.ru'),
('Мост', '1. Инженерное сооружение перекинутое через препятствие (река, овраг, дорога). Состоит из опор и пролетов. Промежуточные опоры называются быками, крайние – устоями. 2. В деревянном зодчестве – перекрытие, пол деревянного рубленного строения.', 'stroit.ru'),
('Мрамор', 'Мрамор - карбонатная горная порода, образующаяся при перекристаллизации известняков. Мрамор хорошо полируется. Применяется мрамор в качестве облицовочного строительного материала.', 'stroit.ru'),
('Мраморная крошка', 'Мраморная крошка - заполнитель для штукатурных растворов и декоративных бетонов. Мраморная крошка состоит из отходов получаемых при разработке мраморных карьеров и изготовлении мраморных изделий.', 'stroit.ru'),
('Мраморная пудра', 'Измельченный до порошкообразного состояния белый мрамор. Применяется в качестве добавки к цементу, извести или гипсу при изготовлении декоративных штукатурных растворов.', 'stroit.ru'),
('Навесная стена', 'Навесная стена – наружная стена здания, выполненная из легких панелей, опирающихся на каркас здания.', 'stroit.ru'),
('Надфиль', 'Небольшой напильник с мелкой насечкой. Изготавливается из высокоуглеродистой инструментальной стали. Применяется для зачистки и обработки поверхностей мелких точных деталей.', 'stroit.ru'),
('Накат', 'Уложенные сплошным настилом на стены и балки бревна или пластины, составляющие часть плоского покрытия.', 'stroit.ru'),
('Наличник', 'Наличник - деревянная (пластиковая) профилированная рамка. Наличник обрамляет дверной или оконный проемы.', 'stroit.ru'),
('Нащельник', 'Нащельник - узкая деревянная (пластиковая) рейка. Нащельник прикрывает щели между досками покрытия или обшивки сооружения.', 'stroit.ru'),
('Некапитальное строительство', 'Строительство объектов из легких сборных конструкций, не предусматривающих устройство заглубленных фундаментов и подземных помещений.', 'stroit.ru'),
('Нервюры', 'Нервюры - выпуклые ребра свода, образующие каркас. Нервюры упрощающие кладку свода и уменьшающие его распор.', 'stroit.ru'),
('Несущая способность', 'Максимальная нагрузка, которую могут нести строительные конструкции, их элементы, а также грунты оснований без потери их функциональных качеств.', 'stroit.ru'),
('Несущие конструкции', 'Несущие конструкции – конструкции, воспринимающие основные нагрузки и обеспечивающие прочность, жесткость, и устойчивость зданий и сооружений.', 'stroit.ru'),
('Нивелир', 'Нивелир - прибор, предназначенный для определения превышения одной точки над другой.', 'stroit.ru'),
('Нивелирная рейка', 'Деревянный брус прямоугольного или двутаврового сечения длиной 3-4 м. с нанесённой на лицевую поверхность шкалой.', 'stroit.ru'),
('Нивелирный репер', 'Геодезический знак, определяющий пункт нивелирной сети.', 'stroit.ru'),
('Нивелирование', 'Топографическая съемка рельефа местности по предварително перенесенным на местность точкам.', 'stroit.ru'),
('Нихром', 'Общее название группы жаростойких сплавов никеля (65—80%) с хромом (кроме того в состав нихрома могут входить кремний, аллюминий, микродобавки редкоземельных металлов). Впервые получен в начале XX столетия. Благодаря сочетанию высоких жаростойкости и электрического сопротивления применяется для изготовления нагревательных элементов электрических печей и бытовых приборов; а также деталей, работающих при высоких температурах.', 'stroit.ru'),
('Ниша', 'В архитектуре – углубление в стене здания или сооружения, служащее для размещения скульпт. украшений, мебели и т. д.', 'stroit.ru'),
('Нутромер', 'Измерительный инструмент для определения внутренних линейных размеров (отверстий, пазов и т. п.) деталей. Большинство нутромеров имеет устройства для установки (центрирования) линии измерения в направлении контролируемого размера, а также дополнительные механизмы для передачи перемещений от измерительных наконечников на отсчетное устройство. Для более точных измерений используют микрометрические нутромеры со сменными удлинителями и индикаторами часового типа. По конструкции различают цанговые, шариковые и другие нутромеры. Основанием для классификации также служат: тип отсчетного устройства; вид контакта с измеряемой поверхностью и т. д. Индикаторные нутромеры выпускаются обычно с рычажными и клиновыми передачами. Большинство нутромеров имеет две точки контакта с измеряемой поверхностью (двухконтактная схема измерения).', 'stroit.ru'),
('Обвалование', 'Возведение земляных дамб вокруг местности подверженной потенциальному затоплению поверхностными водами.', 'stroit.ru'),
('Обло (в обло)', 'См. рубка углов.', 'stroit.ru'),
('Обои', 'Обои - традиционный и широко известный отделочный материал. В современном определении обои — это рулонный материал, как правило, на бумажной основе, который крепится к поверхности стен с помощью клея.', 'stroit.ru'),
('Обои жидкие', 'Обои жидкие - позволяют создавать гладкие или рельефные покрытия без швов. В состав жидких обоев могут входить хлопок, целлюлоза, текстильные волокна. Обои жидкие разводятся водоэмульсионной краской и наносятся валиком или краскопультом. Колеровка производится специальными красками.', 'stroit.ru'),
('Обои велюровые', 'Бумажные полотна, на которые в процессе производства сначала наносится рисунок, а затем велюровые ворсинки.', 'stroit.ru'),
('Обои виниловые', 'Обои виниловые формируются из двух слоев: нижний слой бумаги (или ткани) покрывается слоем поливинила, а затем на поверхность наносится рисунок или тиснение.', 'stroit.ru'),
('Обои металлические', 'Обои металлические - изготавливаются путем покрытия бумажной основы тонким слоем фольги, после чего на поверхность обоев наносится тиснение или рисунок.', 'stroit.ru'),
('Обои текстильные', 'Обои текстильные представляют собой бумажное полотно, ламинированное нитями из натуральных или смешанных волокон, либо натуральной тканью.', 'stroit.ru'),
('Обрешетка', 'Обрешетка - конструкция из брусков, досок, жердей и т. п. Обрешетка располагается поперек стропил для настила по ней кровельного материала.', 'stroit.ru'),
('Огнестойкость', 'Способность конструкций и изделий в течение определенного времени выдерживать без разрушения воздействие высоких температур.', 'stroit.ru'),
('Огнеупорность', 'Способность материалов выдерживать без разрушения воздействие высоких (не ниже 1580 град. С) температур.', 'stroit.ru'),
('Ограждающие конструкции', 'Ограждающие конструкции – части здания, отделяющие его внутреннее пространство от внешней среды или разделяющие его смежные помещения (стены, перегородки)', 'stroit.ru'),
('Оконница', 'Оконница - оконный переплет в виде металлической решетки со слюдяным или стеклянным заполнением. В древних постройках оконница выполнялась в виде доски с прорезанными в ней отверстиями.', 'stroit.ru'),
('Оконный блок', 'Оконный блок – светопрозрачная часть окна, предназначенная для освещения и проветривания помещения. Оконный блок состоит из коробки, створчатых элементов и встроенных систем проветривания.', 'stroit.ru'),
('Оконный проем', 'Оконный проем – проем для монтажа одного или нескольких оконных блоков.', 'stroit.ru'),
('Октагон', 'Октагон - восьмигранное сооружение. Октагон перекрывается восьмигранным же шатровым покрытием.', 'stroit.ru'),
('Олифа натуральная', 'Олифа натуральная - пленкообразующее вещество. Олифа натуральная изготавливается из растительного масла (льняного, конопляного и т. д.) путем его термической обработки. Применяется олифа натуральная главным образом при производстве лакокрасочных материалов.', 'stroit.ru'),
('Ондулин', 'Ондулин - волнистый листовой кровельный материал с волокнистым наполнителем. Ондулин не содержит асбеста.', 'stroit.ru'),
('Опалубка', 'Съемная деревянная или металлическая форма, в которую укладывают бетонный раствор при возведении бетонных и железобетонных конструкций.', 'stroit.ru'),
('Оранжерея', 'Оранжерея - остекленное сооружение с искусственным климатом. Оранжерея предназначена для выращивания различных теплолюбивых растений.', 'stroit.ru'),
('Орнамент', 'Орнамент - декоративный элемент в строительном, изобразительном и прикладном искусстве, состоящий из повторяющихся стилизованных природных или архитектурных форм. Основное предназначение орнамента: заполнение поверхностей, обрамление, разделение частей, украшение внутренних стен, потолков и фасадов зданий.', 'stroit.ru'),
('Ортогональ', 'Ортогональ в архитектурном проектировании – система изображений плана или фасада. Ортогональ выполняется в прямых линиях, без учета перспективных сокращений.', 'stroit.ru'),
('Ортостат', 'Ортостат - вертикальные плиты нижней части каменной стены. Иногда на ортостате выполняются скульптурные рельефы.', 'stroit.ru'),
('Основания сооружений', 'Массив грунта, на который передаются нагрузки от фундаментов зданий и сооружений.', 'stroit.ru'),
('Отбойный молоток', 'Механический ручной инструмент ударного действия, служащий в горном деле для разрушения некрепких горных пород и в строительстве – для разрыхления мерзлых грунтов, разборки бетонных фундаментов, асфальтовых и бетонных покрытий и т.п. Исполнительный орган – пика, долото или лопата в зависимости от вида выполняемых работ и характеристики разрушаемого массива. Отбойные молотки бывают пневматическими, электрическими, гидравлическими и с приводом от бензинового двигателя внутреннего сгорания.', 'stroit.ru'),
('Отлив', 'Отлив – козырек, предназначенный для защиты нижний части окна и удаления влаги.', 'stroit.ru'),
('Отражающая изоляция', 'Средство, служащее для уменьшения тепловых потерь, благодаря сокращению теплообмена между средами. Обычно состоит из двух частей: подложки и отражающего слоя. В качестве подложки используются материалы, обладающие хорошими теплоизолирующими свойствами. В основном пенополиэтилены (ППЭ и НПЭ), вспененный каучук, стелоткани. Отражающий слой выполняют из алюминиевой фольги или полипропиленовой металлизированной пленки. Отражающая изоляция применяется в промышленном строительстве, автомобилестроении, судостроении, для внутренней отделки помещений (в т. ч., саун и бань), монтаже вентиляционных систем и различного вида трубопроводов.', 'stroit.ru'),
('Отрицательные формы рельефа', 'Отрицательные формы рельефа - относительно пониженные участки поверхности суши или дна водоемов: долины, впадины, овраги, строительные котлованы и др.', 'stroit.ru'),
('Охлупень (шелом)', 'Охлупень - коньковое бревно с выбранным снизу пазом. Охлупень прикрывает верхний (коньковый) стык тесин деревянной кровли. Комлевой частью охлупень, выходит на фасад дома и украшается резным изображением коня, птицы и т. п.', 'stroit.ru'),
('Оцилиндрованное бревно', 'Круглый лесоматериал, обработанный для придания ему цилиндрической формы.', 'stroit.ru'),
('Паддуга', 'Паддуга - сферическая поверхность, расположенная над карнизом в помещении. Паддуга создает переход от плоскости стены к поверхности потолка.', 'stroit.ru'),
('Паз. Желоб', 'Желоб в местах соединения элементов строительных конструкций (брусьев, бревен, досок). В паз вставляется соединительная рейка или гребень смежного элемента.', 'stroit.ru'),
('Пазик', 'Инструмент для выборки пазов в элементах деревянных конструкций.', 'stroit.ru'),
('Палисад', 'Стена из врытых в землю и соединенных между собой столбов высотой в несколько метров.', 'stroit.ru'),
('Пальметта', 'Пальметта - декоративный орнамент в виде стилизованных пальмовых листьев, соединенных в гирлянду. Орнамент пальметта был довольно популярен в Риме, позже – в искусстве классицизма.', 'stroit.ru'),
('Пандатив', 'Пандатив - скульптурное лепное украшение, расположенное (висящее) в вершине свода. См. также ПАРУСА.', 'stroit.ru'),
('Пандус', 'Пандус - пологий (около 15 градусов) подъем для въезда на верхнюю площадку крыльца. В современном строительстве пандус устраивается в многоэтажных гаражах, подземных переходах и т. п.', 'stroit.ru'),
('Панели-сэндвич', 'Панели-сэндвич - панели, выполненые из теплоизоляционного сердечника, чаще всего из минеральной ваты, пенополистирола и пенополиуретана. Наружные и внутренние поверхности панелей-сэндвич представляют собой обычно жёсткие стальные, алюминиевые или пластмассовые листы.', 'stroit.ru'),
('Панель', 'Панель - отделка нижней части стены помещения, отличная от отделки всей стены. Панель может быть деревянной, мраморной, пластиковой.', 'stroit.ru'),
('Панно', 'Панно - заполненная художественным изображением часть стены, обрамленная лентой орнамента или лепной рамкой. Также панно – деревянные резные, лепные или мозаичные композиции.', 'stroit.ru'),
('Парапет', 'Парапет - ограждение балкона, кровли здания, моста, набережной и т. п. В некоторых случаях парапет решается как декоративный художественный элемент.', 'stroit.ru'),
('Паркет', 'Материал в виде тонких строганных планок (клепок) из твердых пород древесины, служащий для покрытия полов. Различают штучный паркет, щитовой и наборный.', 'stroit.ru'),
('Паркетная доска', 'Паркетная доска – заменитель натурального паркета. Паркетная доска имеет трехслойную структуру, при этом волокна среднего слоя расположены перпендикулярно волокнам верхнего и нижнего слоев.', 'stroit.ru'),
('Пароизoляция', 'Слой материала, основным назначением которого является предотвращение попадания влаги в результате капиллярного просачивания или диффузии водяных паров в строительные конструкции.', 'stroit.ru'),
('Партер', 'В ландшафтной архитектуре – открытая часть парка, расположенная на плоской местности.', 'stroit.ru'),
('Паруса. Пандативы', 'Паруса - треугольные сферические элементы купольной конструкции. С помощью паруса возводится купол над четырехугольным в плане помещением.', 'stroit.ru'),
('Патрубок', 'Патрубок – небольшой отрезок трубы, присоединённый (ввальцованный, приклёпанный, приваренный) к трубопроводу, резервуару и др. конструкциям, служащий для подключения к ним трубопроводов и арматуры.', 'stroit.ru'),
('Пемза', 'Легкая вулканическая пористая порода светло-серого цвета, похожая на застывшую пену. Предел прочности пемзы — 0,2-1,4 МПа, средняя плотность — 300-600 кг/м3, истинная плотность 2,5 г/см3.', 'stroit.ru'),
('Пендельтюр', 'Пендельтюр - дверь на качающихся петлях, открывающаяся в обе стороны.', 'stroit.ru'),
('Пеноасбест', 'Пеноасбест - особолёгкий минеральный теплоизоляционный материал. Готовится пеноасбест из распушенного хризатил-асбеста, технической пены и химических реагентов. Средняя плотность около 20 кг/куб.м.', 'stroit.ru'),
('Пенобетон', 'Пенобетон - вид ячеистого легкого бетона, получаемый из пеномассы, которая приготавливается из цементного теста, поризованного технической пеной, образующей воздушные ячейки (поры).', 'stroit.ru'),
('Пеноизол', 'Органический ячеистый карбомидный пенопласт. Отличается низкими плотностью и теплопроводностью, высокой сопротивляемостью огню, стойкостью к действию микроорганизмов, легкостью механической обработки, относительно невысокой стоимостью. Используется в качестве теплоизоляционного материала при строительстве и ремонте жилых и производственных зданий и сооружений.', 'stroit.ru'),
('Пенопласт', 'Пенопласт - материал, имеющий пористую структуру состоящую из несообщающихся ячеек. Пенопласт имеет низкую плотность, высокие тепло- и звукоизоляционные характеристики. К недостаткам пенопласта можно отнести горючесть.', 'stroit.ru'),
('Пенополивинилхлорид', 'Пенополивинилхлорид - теплоизоляционный поропласт, получаемый поризацией поливинилхлоридных смол. Средняя плотность пенополивинилхлорида < 100 кг/м3. Пенополивинилхлорид незначительно изменяет свои свойства при изменении температуры от +60 град.С до –60 град.С.', 'stroit.ru'),
('Пенополистирол', 'Пенополистирол - теплоизоляционный материал, разновидность термопластичных пенопластов. Наилучшими характеристиками обладает пенополистирол, изготовленный методом экструзии.', 'stroit.ru'),
('Пенополиуретан', 'Пенополиуретан - теплоизоляционный материал. Пенополиуретан может быть жестким или эластичным. Относится пенополиуретан разновидности пенопластов.', 'stroit.ru'),
('Пеностекло', 'Пеностекло - ячеистый материал, получаемый при спекании тонкоизмельченного стекла. Пеностекло с сообщающимися порами используется как звукопоглощающий материал. Пеностекло с закрытыми порами – как теплоизоляционный.', 'stroit.ru'),
('Пергамин', 'Пергамин - мягкий рулонный кровельный материал, получаемый путем пропитки кровельного картона битумом. Пергамин используется в качестве армирующей части нижних слоев изоляционных покрытий в кровле.', 'stroit.ru'),
('Пергола', 'Пергола - сооружение, состоящее из установленных друг за другом арок с деревянной обрешеткой. Пергола используется для размещения на ней вьющихся декоративных растений.', 'stroit.ru'),
('Перегородка каркасная', 'Перегородка, состоящая из стоек (каркас — несущая часть), заполнения и обшивки.', 'stroit.ru'),
('Перекид', 'Перекид – полотнище ткани, перекинутое через карниз.', 'stroit.ru'),
('Перекрытие', 'Перекрытие - конструктивная часть сооружения, разделяющие его на этажи. По назначению перекрытия бывают цокольные, междуэтажные, чердачные; по форме – плоские и сводчатые. Несущими элементами плоских перекрытий являются балки и плиты.', 'stroit.ru'),
('Перелив', 'Узел водосливной арматуры, предназначенный для слива излишней воды из санитарно-технического прибора при достижении в нем максимального уровня воды.', 'stroit.ru'),
('Перемычка', 'Перемычка - небольшая балка, применяемая для перекрытия дверных, оконных и т. п. проемов.', 'stroit.ru'),
('Перемычка клинчатая', 'Перемычка клинчатая - плоская перемычка арочного типа, выложенная из радиального или клинчатого кирпича. Перемычка клинчатая работает по принципу арки. Также применяется в печном, особенно каминном строительстве.', 'stroit.ru'),
('Переруб', 'Переруб - внутренняя бревенчатая (брусовая) стена, выполненная одновременно с основным срубом, врубленная с остатком. Конструкция сруба с использованием переруба называется ПЯТИСТЕНОК.', 'stroit.ru'),
('Переувлажнение грунта', 'Полное насыщение грунта водой, ведущее к нарушению его структуры и потере несущей способности.', 'stroit.ru'),
('Перила', 'Перила - ограждения различной конфигурации. Перилами ограждают лестницы, балконы, террасы, набережные, мосты. Как правило перила имеют высоту около одного метра.', 'stroit.ru'),
('Перистиль', 'Перистиль - прямоугольные площадь, сад или двор, окруженные крытой колоннадой.', 'stroit.ru'),
('Перспектива', 'Перспектива - система изображения трехмерного пространства на двухмерной плоскости в соответствии со зрительным восприятием человека. В архитектурных чертежах перспектива используется для построения иллюзорного пространства.', 'stroit.ru'),
('Песчаники', 'Песчаники - обломочные породы, состоящие из мелких частиц различных минералов, сцементированных в прочную массу. Окраска песчаника может быть белой, серой, желтой, красной.', 'stroit.ru'),
('Пигменты', 'Пигменты - нерастворимые сухие вещества, придающие окраску лакокрасочным материалам, пластмассам и т. д. Неорганические пигменты подразделяются на природные и искусственные (синтетические). К природным пигментам относятся, в частности, различные окислы железа, марганца, хрома и другие соединения (железный сурик, охра, мумия), а также некоторые виды глин и известняков. Синтетические пигменты делятся на органические и неорганические. Основными характеристиками пигментов являются цвет, свето- и атмосферостойкость, интенсивность, укрывистость, антикоррозионность.', 'stroit.ru'),
('Пиломатериалы', 'Пиломатериалы - изготавливаются путем продольной распиловки пиловочных бревен. Бревна на пиломатериалы могут распиливаться по диаметру (пластины) или по двум взаимно перпендикулярным диаметрам (четвертина).', 'stroit.ru'),
('Пилон', 'Пилон - мощная опора, поддерживающая перекрытия, своды, арки, устои мостов.', 'stroit.ru'),
('Пилястра', 'Пилястра - прямоугольный плоский вертикальный выступ, повторяющий все части и пропорции колонны. Пилястра является декоративным элементом, служащим для вертикального членения плоскости стены. В некоторых случаях пилястра может служить конструктивным усилением стены.', 'stroit.ru'),
('Пирон', 'Пирон - короткий металлический стержень или брусок. Пирон скрепляет по вертикали блоки каменной кладки.', 'stroit.ru'),
('Планировка анфиладная', 'Размещение взаимосвязанных помещений последовательно, одно за другим, с расположенными по одной оси дверными проемами.', 'stroit.ru'),
('Планировка гибкая', 'Схема планировки, предусматривающая трансформацию размеров, взаимного размещения, конфигурации отдельных помещений как по вертикали, так и по горизонтали, обусловленную в основном функциональными требованиями. При необходимости позволяет расчленять внутреннее пространство легкими трансформирующимися элементами.', 'stroit.ru'),
('Планировка коридорная', 'Архитектурно-планировочная схема взаимного расположения помещений с одной или двух сторон коридора.', 'stroit.ru'),
('Планировка центрическая', 'Схема планировки, предусматривающая наличие большого главного помещения, вокруг которого группируются меньшие, вспомогательные, помещения.', 'stroit.ru'),
('Пластизол', 'Пластизол - металлический лист толщиной 0,5 мм. С внешней стороны на него нанесен слой полимера толщиной 200 мкм, затем слой грунтовки, пассиватора и цинка. С внутренней стороны нанесены слой цинка, пассиватора и специального покрытия на основе эпоксидной смолы. Пластизол применяется в основном как кровельное покрытие.', 'stroit.ru'),
('Пластина', 'Пластина - половина распиленного или расколотого вдоль бревна. Пластины применяются для настила полов, потолков, взвозов, деревянных мостов и т. п.', 'stroit.ru'),
('Пластичность', 'Способность материала без разрушения изменять свои размеры и форму под воздействием внешней нагрузки и сохранять эти изменения после прекращения ее действия.', 'stroit.ru'),
('Пластмассы газонаполненные', 'Пластмассы газонаполненные (ячеистые пластмассы, пенопласты, поропласты). Органические высокопористые материалы, получаемые из синтетических смол.', 'stroit.ru'),
('Плафон', 'Плафон - украшенный живописным или лепным изображением потолок.', 'stroit.ru'),
('Плашка', 'Резьбонарезной инструмент для нарезания наружной резьбы вручную или на металлорежущем станке (как правило, за один проход). В процессе резания плашка и заготовка одновременно совершают вращение вокруг продольной оси резьбы и равную шагу резьбы продольную подачу. Плашки изготавливаются из инструментальной и быстрорежущей сталей. В зависимости от наружной формы различают круглые, квадратные, шестигранные, трубчатые и иные плашки.', 'stroit.ru'),
('Плинт', 'Плинт - квадратная массивная плита, уложенная в основании колонны.', 'stroit.ru'),
('Плинтус', 'Плинтус - профилированная деревянная или пластиковая рейка. Плинтус предназначен для прикрытия щелей между полом и стеной. Плинтус можно рассматривать и как элемент архитектурного оформления помещения.', 'stroit.ru'),
('плита асбестоцементная', 'Изделие, представляющее собой плоскостной строительный элемент, имеющий асбестоцементные наружные поверхности и внутреннее пространство, заполняемое при необходимости изоляционным материалом, предназначенное для использования в горизонтальном положении.', 'stroit.ru'),
('Плита древесноволокнистая', 'Материал, получаемый горячим прессованием массы, состоящей из целлюлозных волокон, воды, синтетических полимеров и специальных добавок. Сырьем для производства ДВП служат отходы деревообрабатывающего производства, стебли тростника.', 'stroit.ru'),
('Плита древесностружечная', 'Материал, получаемый горячим прессованием стружечной массы, смешанной со связующим. Изготавливаются одно- и многослойными. Свойства плит зависят от использующегося при производстве вида связующего.', 'stroit.ru'),
('Плита камышитовая', 'Плита камышитовая (камышит). Теплоизоляционное изделие, изготовленное из стеблей тростника, камыша осенне-зимней рубки с прошивкой металлической проволокой.', 'stroit.ru'),
('Плита МДФ', 'Плита МДФ. Новый вид ДВП европейского производства. Плиты МДФ готовятся из тонкодисперсной древесной муки, спрессованные с синтетическими смолами. В отличие от традиционно применяющимся ДВП плиты МДФ фрезеруются и покрываются декоративными плёнками.', 'stroit.ru'),
('Плита цементно-стружечная', 'Конструкционный материал, состоящий из прессованных древесных стружек, смешанных с портландцементом, соответствующими добавками и водой. Морозостоек, относительно легко обрабатывается, нетоксичен. Применяется при устройстве ограждающих конструкций, перегородок, полов.', 'stroit.ru'),
('Плитка глазурованная (эмалированнная)', 'Плитка глазурованная (эмалированнная). Плитка, имеющая поверхность покрытую слоем цветного стекла, придающего ей важные эстетические характеристики (цвет, блеск, рисунок, оттенки и т.д.), а также технические свойства (твердость, непроницаемость и т.д.).', 'stroit.ru'),
('Плотность', 'Отношение массы тела к занимаемому объему. Выражается в кг/куб. м. Различают истинную и насыпную плотность. Истинная плотность - предел отношения массы к объему, т. е. плотность тела или вещества без учета имеющихся в них пустот и пор. Насыпная плотность - отношение массы зернистых материалов ко всему занимаемому или объему, включая пространства между частицами.', 'stroit.ru'),
('Площадь', 'Архитектурно организованная, незастроенная часть территории населенного пункта, органично включенная в уличную сеть.', 'stroit.ru'),
('Плывун', 'Плывун - насыщенный водой рыхлый грунт. В результате давления вышележащих толщ и других механических воздействий плывун переходит в текучее состояние.', 'stroit.ru'),
('Повал', 'Повал - в деревянном зодчестве верхняя расширенная часть сруба. Повал служит основанием для шатровых и скатных крыш.', 'stroit.ru'),
('Погрузчик', 'Погрузчик – самоходный подъёмно-транспортный механизм, предназначенный для погрузки, разгрузки и штабелирования грузов.', 'stroit.ru'),
('Подбалка', 'Подбалка - отрезкок бруса, расположенный между несущей балкой и опорными стойками. Подбалки предназначены для распределения вертикальной нагрузки.', 'stroit.ru'),
('Подволока', 'Подволока в деревянном зодчестве - чердачное помещение.', 'stroit.ru'),
('Подклеть', 'Подклеть - нижний нежилой этаж деревянного или каменного дома. Подклеть предназначена для хозяйственных нужд.', 'stroit.ru'),
('Подкос', 'Подкос - работающий на сжатие наклонный брус в строительных конструкциях. Подкос поддерживает горизонтальные элементы конструкции (балки, перекрыртия). Подкос опирается на вертикальные элементы (стойки, колонны).', 'stroit.ru'),
('Подмости', 'Подмости - конструкция, предназначенная для выполнения работ, производимых на небольшой высоте.', 'stroit.ru'),
('Подпорная стенка', 'Подпорная стенка - железобетонная, бутовая, металлическая или деревянная конструкция. Подпорная стенка удерживает от обрушения находящийся за ней массив грунта.', 'stroit.ru'),
('Подсос капиллярный', 'Подсос капиллярный – перемещение жидкости внутри пористых материалов.', 'stroit.ru'),
('Подстилающий слой пола', 'Подстилающий слой пола - слой пола, распределяющий нагрузки на грунт. Там, где пол находится над неотапливаемым подпольем (проездом), устраивается теплоизоляционная прослойка.', 'stroit.ru'),
('Подступенок', 'Подступенок – конструктивная деталь лестницы: вертикальный элемент ступени.', 'stroit.ru'),
('Подъемник', 'Грузоподъемная машина прерывного действия, предназначенная для перемещения людей с инструментом и материалами и проведения работ в пределах зоны обслуживания.', 'stroit.ru'),
('Подъемник передвижной несамоходный', 'Подъемник, передвигаемый вручную по рабочей площадке и перевозимый на механизированном транспортном средстве по дорогам.', 'stroit.ru'),
('Подъемник передвижной самоходный', 'Подъемник, оборудованный механизмом передвижения по рабочей площадке и транспортируемый на механизированном транспортном средстве по дорогам.', 'stroit.ru'),
('Подъемники передвижные', 'Подъемники, транспортируемые на механизирован ном транспортном средстве.', 'stroit.ru'),
('Подъемники самоходные', 'Подъемники, оборудованные механизмом для передвижения по рабочей площадке и по дорогам. Различают подъемники автомобильные, подъемники на спецшасси, подъемники пневмоколесные, подъемники железнодорожные, подъемники прицепные, подъемники прицепные пневмоколесные, подъемник прицепные железнодорожные.', 'stroit.ru'),
('Покрытие гонтовое битумное (шинглс)', 'Покрытие гонтовое (шинглс) выполняется в виде листов (кровельной плитки) «под черепицу». Покрытие гонтовое изготавливается из основных битуминозных материалов с декоративной посыпкой.', 'stroit.ru'),
('Покрытие здания', 'Покрытие здания – верхнее ограждение здания для защиты помещений от внешних климатических факторов и воздействий.', 'stroit.ru'),
('Покрытие КПЦР', 'Покрытие КПЦР – КПЦР — коллоидный полимерцементный раствор. Покрытие КПЦР, предназначено для усиленной гидроизоляции полов и стен подвальных помещений промышленных и гражданских зданий, резервуаров для питьевой воды и пожарного водоснабжения, аэротенков, объектов химической водоочистки, канализационных и насосных станций, ванн плавательных бассейнов, мазутохранилищ, гидротехнических и других сооружений.', 'stroit.ru'),
('Покрытие чердачное', 'Покрытие чердачное образует пространство (проходное или полупроходное) над перекрытием верхнего этажа.', 'stroit.ru'),
('Поле фильтрации', 'Территория, предназначенная для биологической очистки сточных вод.', 'stroit.ru'),
('Полиакрилаты', 'Полимеры сложных эфиров акриловой или метакриловой кислот, образующиеся при полимеризации эфиров акриловой и метакриловой кислот (соответственно, акрилатов и метакрилатов). Представляют собой прозрачные, термопластичные, физиологически безвредные, хорошо растворяющиеся в органических растворителях и характеризующиеся низкой масло-и бензостойкостью материалы. Применяются для производства стекла органического, полимерных пленок, лакокрасочных материалов, клеев и пропиточных составов для бумаги, кожи, дерева, ткани и проч.', 'stroit.ru'),
('Полимербетон', 'Полимербетон – материал на основе рационально подобранной смеси полиэфирных смол и различных минеральных заполнителей.', 'stroit.ru'),
('Полимербетоны', 'Специальные бетоны на основе полимерного вяжущего, химически стойких минеральных заполнителей, наполнителей и добавок.', 'stroit.ru'),
('Полица', 'Полица - пологая нижняя часть шатровой или двускатной кровли. Полица предназначена для отвода дождевой воды.', 'stroit.ru'),
('Половняк', 'Половняк - разбитый пополам (или на более мелкие части) кирпич.', 'stroit.ru'),
('Полуколонна', 'Полуколонна - колонна выступающая из плоскости стены на половину своего диаметра. Конструктивно полуколонна – контрфорс, увеличивающий устойчивость сооружения.', 'stroit.ru'),
('Полы наливные', 'Полы наливные - монолитные покрытия полов, выполняемые из подвижных полимерсодержащих составов по предварительно подготовленному основанию или стяжке.', 'stroit.ru'),
('Пористость', 'Пористость - степень заполнения объема материала порами (ячейками воздуха или другого газа). Пористость существенно влияет на технические свойства материалов теплопроводность, прочность, водопоглощение и др.).', 'stroit.ru'),
('Портал', 'Архитектурно оформленный и богато украшенный резьбой и т. п. дверной проем монументального здания.', 'stroit.ru'),
('Портик', 'Портик - образованная несущими колоннами галерея. Портик располагается перед входом в здание.', 'stroit.ru'),
('Портландцемент', 'Гидравлическое вяжущее, получаемое при тонком измельчении клинкера и гипса. Может содержать различного рода добавки. Патент на него был получен в Англии Д. С. Аспдиным в 1824 году.', 'stroit.ru'),
('Портландцементный клинкер', 'Клинкер, состоящий преимущественно из высокоосновных силикатов кальция, а также алюминатов и алюмоферритов кальция', 'stroit.ru'),
('Портьера', 'Портьера – тканевой занавес дверного или оконного проема, состоящий из одного или двух вертикальных полотнищ.', 'stroit.ru'),
('Поручни', 'Поручни – перила ограждений надстроек, мостиков, трапов, машинных площадок и др. Поручни изготавливаются из дерева или металла.', 'stroit.ru'),
('Порфир', 'Порфир - мелкокристаллическая изверженная горная порода с крупными включениями. По химическому составу порфир близок к граниту.', 'stroit.ru'),
('Потайной зуб', 'Потайной зуб - прямоугольный выступ в верхнем бревне, входящий в соответствующий паз нижнего бревна венца сруба.', 'stroit.ru'),
('Потерна', 'Потерна - коридор (галерея) внутри массивного сооружения. Потерна устраивается, например, в теле плотин для наблюдения за состоянием их внутренних частей.', 'stroit.ru'),
('Поток', 'Поток - бревно с выдолбленным желобом, служащее для отвода воды с кровли. Одновременно поток является опорой для нижних концов кровельного теса.', 'stroit.ru'),
('Потолок натяжной', 'Потолок натяжной – прочное полотно из термопластичного ПВХ, котрое монтируется ниже "черного" потолка и скрывает его неровности.', 'stroit.ru'),
('Потолок перфорированный', 'Потолок перфорированный – потолок, состоящий из панелей с отверстиями различного диаметра, расположенными на определенном расстоянии друг от друга.', 'stroit.ru'),
('Потолок подвесной', 'Потолок подвесной – совокупность конструктивно-отделочных элементов, образующих дополнительное укрытие, подвешиваемое к основному потолку.', 'stroit.ru'),
('Потребитель электроэнергии', 'Электроприемник или группа электроприемников, объединенных технологическим процессом.', 'stroit.ru'),
('Предел огнестойкости', 'Предел огнестойкости - время в минутах (часах) с момента начала пожара до выхода конструкции из строя (до потери несущей способности, обрушения, достижения необратимых деформаций или до образования сквозных трещин), или прогрева до повышения температуры на противоположной от огня поверхности порядка 220 град.С, выше которой возможно самовоспламенение органических материалов.', 'stroit.ru'),
('Предохранительный пояс', 'Средство индивидуальной защиты, закрепляемое на теле человека и применяемое автономно или совместно с другими средствами защиты для предотвращения падения человека с высоты или эвакуации его из опасных зон.', 'stroit.ru'),
('Прируб', 'Прируб - органически включенная в общую композицию второстепенная и более низкая часть срубного здания.', 'stroit.ru'),
('Причелина', 'Причелина - украшенные резьбой фронтонные доски. Причелина прикрывает торцы слег тесовой кровли.', 'stroit.ru'),
('Прогон', 'Прогон - балка в системе несущих конструкций здания. Прогон опирается непосредственно на опорные части сооружения – стены, колонны, пилоны.', 'stroit.ru'),
('Продух', 'Продух - небольшое отверстие в цоколях, стенах, перекрытиях здания. Продухи предназначены для естественной вентиляции замкнутых пространств сооружения.', 'stroit.ru'),
('Проем', 'Отверстие для окон, дверей и т. п. в стенах или перегородках.', 'stroit.ru'),
('Пролет', 'Пролет - расстояние между смежными опорами, перекрываемое балкой, плитой, аркой и пр. Расчетный пролет – расстояние между осями опор. Пролет в свету – расстояние между внутренними гранями опор.', 'stroit.ru'),
('Пропилеи', 'Пропилеи - торжественно оформленный подъезд к монументальному зданию. Например, пропилеи афинского Акрополя, парные галереи при подъезде к Смольному в Санкт- Петербурге.', 'stroit.ru'),
('Пропиловка. Пропильная резьба', 'Ажурная сквозная резьба по дереву, выполненная специальной пилой. Пропиловкой оформляются наличники, карнизы, причелины и пр. в деревянном зодчестве.', 'stroit.ru'),
('Проступь', 'Проступь – верхний горизонтальный элемент ступени.', 'stroit.ru'),
('Профнастил', 'Профнастил - металлический профильный лист, на который сверху нанесен слой полимера, затем последовательно слой грунтовки, пассиватора и цинка.', 'stroit.ru'),
('Прочность', 'Свойство материала сопротивляться разрушению под действием внутренних напряжений, вызываемых внешними силами или другими факторами. Физическая природа прочности твёрдых тел обусловлена в конечном счёте силами взаимодействия между атомами и ионами, составляющими тело. Оценивается пределом прочности – нагрузкой, при которой происходит разрушение материала. Обозначается R, измеряется в МПа.', 'stroit.ru'),
('Прясло', 'В русском оборонительном строительстве часть крепостной стены между двумя башнями.', 'stroit.ru'),
('ПСУЛ (предварительно сжатая уплотнительная лента)', 'Ленточный герметик общестроительного назначения. Основная сфера применения – защита швов (примыкания) от сквозного продувания и промерзания и попадания в него пыли, грязи, листьев, снега, водяных брызг, шума. Кроме того, может применяться: для уплотнения элементов кровли в малоэтажном строительстве; соединений в фасадных конструкциях и конструкциях сендвич-панелей; в качестве уплотнителя и утеплителя в деревянном домостроении.', 'stroit.ru'),
('Пудра алюминиевая', 'Тонко измельченный порошок алюминия, не растворимый в воде и в органических растворителях. Применяется в качестве пигмента, при производстве строительных материалов (газобетона), в пиротехнике. Состоит из чешуйчатых или каплеобразных частиц. Первые получают путем раздавливания, а вторые – распыления расплавленного алюминия.', 'stroit.ru'),
('Пульвины', 'Пульвины - каменная плита (подушка), установленная между пятОй арки и капителью опоры (колонны).', 'stroit.ru'),
('Путепровод', 'Конструктивно – аналог моста, эстакады, служащий для организации непрерывного движения в местах пересечения транспортных коммуникаций.', 'stroit.ru'),
('Пухто', 'Ставшее общеупотребительным название мусорных контейнеров. Сокращение от выражения «пункт утилизации и хранения твердых отходов».', 'stroit.ru'),
('Пуццоланы', 'Пуццоланы - слабо сцементированные отложения вулканических материалов (пеплы и т. д.), разновидность вулканических туфов. Светлые разновидности пуццоланов применяются при изготовлении гидравлической извести и пуццоланового цемента. Такой цемент имеет повышенные водо- и сульфатостойкость, но отличается пониженными воздухо- и морозостойкостью по сравнению с портландцементом.', 'stroit.ru'),
('Пьедестал', 'Художественно оформленное основание для скульптуры, вазы, обелиска, колонны.', 'stroit.ru'),
('Пятистенок', 'См. ПЕРЕРУБ.', 'stroit.ru'),
('Рабатка', 'Рабатка - цветник в виде узкой (1–2,5 м) полосы. Рабатка окаймляет парковые дорожки, цветочные партеры.', 'stroit.ru'),
('Равелин', 'Равелин - оборонительное сооружение впереди крепостного вала. Как правило, равелин треугольный в плане.', 'stroit.ru'),
('Радиодальномер', 'Дальномер, действие которого основано на измерении расстояний с помощью электромагнитных волны радиодиапазона. Различают импульсные радиодальномеры, действие которых основано на измерении времени распространения импульсов от радиодальномера до объекта и обратно, и непрерывные, чье действие основано на определении разности фаз излучаемых и отраженных от объекта волн.', 'stroit.ru'),
('Разбавители', 'Жидкости, служащие для уменьшения вязкости составов или разведения сухих минеральных красок. В качестве разбавителей в лакокрасочных составах используют олифы и различные эмульсии.', 'stroit.ru'),
('Развертка', 'Режущий инструмент, применяемый для окончательной (чистовой) обработки просверленных отверстий. Различают ручные развертки для работы с помощью воротка, надеваемого на квадрат развертки и машинные развертки для работы на станке. По форме различают цилиндрические и конические развертки.', 'stroit.ru'),
('Разжелобок', 'Разжелобок - стык двух скатов кровли с желобом между ними.', 'stroit.ru'),
('Размалковка', 'Изменение формы поперечного сечения уголкового профиля, вследствие изгибания его полок наружу. Изменение формы поперечного сечения уголкового профиля, вследствие изгибания его полок внутрь называется смалковка.', 'stroit.ru'),
('Разрез архитектурный', 'Разрез архитектурный – фронтальная проекция здания или архитектурной детали, условно рассеченная плоскостью.', 'stroit.ru'),
('Разрядник', 'Электротехническое устройство в виде двух или нескольких электродов, разделенных диэлектрическими (разрядным) промежутками, проводимость которых резко меняется, когда разность потенциалов между электродами становится равной некоторой конкретной величине (т. н. напряжение пробоя). В соответствии с функциональным назначением выделяют два основных типа разрядников — защитные (вследствие пробоя разрядника позволяют предотвращать чрезмерное возрастание напряжения на линии или на той установке, к которой они подсоединены) и управляющие (применяются для соединения в определенной последовательности различных элементов генераторов импульсного напряжения, для подсоединения нагрузки к мощным импульсным источникам тока, а также для соединения элементов электрических схем испытательной аппаратуры высокого напряжения). Применяются в электротехнике, радиоэлектроники, автоматике.', 'stroit.ru'),
('Райв', 'Райв – теплоизоляционный материал, изготовленный на основе распушенных особым способом древесных волокон хвойных пород. Райв используется в виде ленточного или блочного утеплителя.', 'stroit.ru'),
('Ракурс', 'Перспективное сокращение архитектурных форм, изображенных фигур и предметов.', 'stroit.ru'),
('Рама железобетонная', 'Железобетонная конструкция, состоящая из колонн жестко закрепленных в фундаментах и балок.', 'stroit.ru'),
('Рантовая лента', 'Рантовая лента – полоса из вспененного полиэтилена толщиной не менее 5 мм и шириной 120–180 мм, служащая для компенсации температурного расширения стяжки.', 'stroit.ru'),
('Раскос', 'Раскос - строительный элемент, соединяющий два узла каркаса, фермы и т. п. Раскос располагается по диагонали замкнутого контура и обеспечивает жесткость конструкции.', 'stroit.ru'),
('Раскреповка', 'Раскреповка - вертикальное членение объема, проходящее по всей его высоте. Создавая небольшие выступы-утолщения в стене, раскреповка членит все пересекаемые элементы: карниз, фронтон, цоколь и т. п.', 'stroit.ru'),
('Распор', 'Горизонтальная составляющая вертикальной нагрузки, возникающая в конструкциях работающих на распор (арках, сводах и т. п.).', 'stroit.ru'),
('Раствор теплый', 'Раствор теплый. Для приготовлении такого раствора в качестве заполнителя используется пористый заполнитель (например, вспученный перлит, вермикулит). Раствор теплый имеет среднюю плотность, как правило, не более 1200 кг/куб.м и теплопроводность до 0,3 Вт/мК. Применение раствора теплого. исключает появление мостиков холода в кладке. Раствор теплый используется также для приготовления «теплой» штукатурки.', 'stroit.ru'),
('Раствор холодный', 'Раствор холодный. Для приготовлении такого раствора в качестве заполнителя используется природный, чаще всего кварцевый, песок с максимальной крупностью до 5 мм. При использовании раствора холодного толщина швов кирпичной кладки должна находиться в пределах 10-12 мм.', 'stroit.ru'),
('Растворители', 'Жидкости, служащие для придания составам необходимой консистенции. В качестве растворителей для масляных красок используются бензин, уайт-спирит, скипидар, для перхлорвиниловых – ацетон, для клеевых и водоэмульсионных – вода. Большинство растворителей – токсичные, горючие и взрывоопасные вещества.', 'stroit.ru'),
('Расшивка', '1. Расшивка (гл.) - придание определенной формы лицевым швам кирпичной или каменной кладки. 2. Расшивка (сущ.) - инструмент для производства указанных работ.', 'stroit.ru'),
('Расщебенка', 'Расщебенка – заполнение щебнем пустого пространства, как правило, между элементами фундаментных конструкций.', 'stroit.ru'),
('Редуктор', 'Редуктор – механизм, входящий в приводы машин и служащий для снижения угловых скоростей ведомого вала с целью повышения крутящих моментов.', 'stroit.ru'),
('Резьбомер', 'Резьбоизмерительный прибор – средство измерения и контроля резьбы. Различают резьбомеры: для комплексного контроля и для измерения отдельных параметров; для наружной и внутренней резьб; для цилиндрической и конической резьб; для ходовых винтов и т. д. Наибольшим разнообразием отличаются резьбомеры для измерения наружных резьб.', 'stroit.ru'),
('Реконструкция', 'Обновление, модернизация, перестройка зданий, улиц, площадей, городов.', 'stroit.ru'),
('Реконструкция объекта', 'Строительные работы в отдельных помещениях объекта, проводимые в целях частичного изменения фасадной части и/или несущих конструкций здания, предусматривающие один из видов работ (или их комплекс): изменение формы оконных и дверных проемов; создание, ликвидация оконных и дверных проемов; изменение входов; устройство входов, тамбуров входов; устройство лоджий, балконов; остекление лоджий, балконов; замена столярных элементов фасада.', 'stroit.ru'),
('Реле', 'Реле – элемент автоматики, в котором воздействие механических, тепловых, оптических и иных физических величин вызывает скачкообразное изменение физических величин в управляемой цепи.', 'stroit.ru'),
('Рельеф', '1. Рельеф - скульптурное изображение на плоскости. Рельеф может быть углубленным (койланогриф) и выступающим (барельеф, горельеф). 2. Рельеф - конфигурация поверхности земельного участка ( рельеф местности).', 'stroit.ru'),
('Ремонт фасада объекта', 'Проведение строительных работ, предусматривающих один из видов работ (или их комплекс): ремонтно-восстановительные работ по фасаду здания (включая замену отделочного материала); ремонт или частичное восстановление архитектурных элементов; проведение штукатурных и окрасочных работ; ремонт, замену столярных изделий (кроме объектов историко-градостроительной среды); покраску столярных изделий; ремонт, покраску кровли.', 'stroit.ru'),
('Ремонтный загон', 'Место, в которое устанавливается кран на время его ремонта.', 'stroit.ru'),
('Ремонтопригодность', 'Свойство изделия, характеризующее его приспособленность к восстановлению исправности и сохранению заданной технической характеристики в результате предупреждения, выявления и устранения отказов. Показатель ремонтопригодности – среднее время ремонта на один отказ данного вида, а также трудоемкость и стоимость устранения отказов.', 'stroit.ru'),
('Ригель', 'Ригель - горизонтальный элемент строительной конструкции (балка, прогон). В рамах ригель соединяет стойки, в каркасах – опоры, в крышах – стропила.', 'stroit.ru'),
('Ритм', 'Ритм - повторяемость, чередование архитектурных элементов здания. Ритм задается расположением колонн, аркад, проемов, скульптур и т. п.', 'stroit.ru'),
('Розетка. Розетта', 'Стилизованный орнамент в виде распустившегося цветка.', 'stroit.ru'),
('Рольставни', 'Рольставни – современная альтернатива традиционным железным решеткам. Основным назначением рольставен является защита от проникновения в помещение.', 'stroit.ru'),
('Романцемент', 'Романцемент - гидравлическое вяжущее. Романцемент получают посредством тонкого помола известковых и магнезиальных мергелей обожженных при температуре 850–900 град. С. Романцемент может содержать гипс и различные добавки. Выпускается романцемент трех марок: 2,5; 5 и 10.', 'stroit.ru'),
('Ростверк', 'Ростверк - часть фундамента сооружения. Ростверк распределяет нагрузку на основание, в том числе свайное.', 'stroit.ru'),
('Ростральная колонна', 'Отдельная, самостоятельная колонна, ствол которой украшен рострами, – скульптурными изображениями носовой части корабля.', 'stroit.ru'),
('Ротонда', 'Ротонда - круглая в плане постройка (зал, беседка, павильон), окруженная колоннами и покрытая куполом.', 'stroit.ru'),
('Рубанок', 'Столярный инструмент, предназначенный для ручного строгания древесины. Состоит из деревянной или металлической колодки, резца (ножа) и зажимного клина. Имеет несколько разновидностей, например, горбач (рубанок со съемными колодками, использующийся для ручного строгания выпуклых и вогнутых деталей), шлифтик (рубанок с укороченным корпусом, применяемый для зачистки древесины, имеющей задиры и свилеватость, а также для зачистки торцов).', 'stroit.ru'),
('Рубероид', 'Рубероид - мягкий рулонный кровельный материал. Изготавливается рубероид путем пропитки кровельного картона нефтяными битумами и последующего нанесения на обе стороны слоев тугоплавкого битума с наполнителем и посыпкой. Рубероид подразделяется на кровельный и подкладочный.', 'stroit.ru'),
('Рубероид наплавляемый', 'Рубероид с утолщенным слоем вяжущего, расплавляемого с помощью специальных горелок при проведении кровельных работ. Его использование заметно снижает трудоемкость работ, повышает их безопасность.', 'stroit.ru'),
('Рубитэкс', 'Рулонный кровельный и гидроизоляционный материал. Предназначен для устройства кровельного ковра зданий и сооружений различного назначения, а также гидроизоляции мостов, фундаментов, тоннелей. Сохраняет эластичность (а значит, возможность укладки) при температуре до минус 25 град. C, теплостойкость – до +100 град. C. Состоит из армирующей основы (стеклохолст, комбинированная ткань, каркасная стеклоткань РАТЛ, полиэстер – полиэфирное нетканое волокно), покрытой с обеих сторон СБС-модифицированным полимерно-битумным вяжущим. Имеет непревзойденную адгезию к основанию (стяжка, бетон, металл и т. д.), высокую теплостойкость и устойчивость к циклическим перепадам температур. Нижняя сторона покрывается легкооплавляемой антиадгезионной пленкой, (марка «П»), верхняя – пленкой либо крупнозернистой минеральной посыпкой (марка «К»).', 'stroit.ru'),
('Рубка углов', '1. Рубка углов "в обло" ("в чашу") – снизу вдоль вышележащего бревна вырубается полукруглая выемка по диаметру нижележащего бревна. В углах торцы бревен выходят за плоскость перпендикулярной стены. Чаша выбирается в половину бревна. 2. Рубка углов "в лапу" – в углах концы бревен соединяются потайным зубом и их торцы не выходят за плоскость перпендикулярной стены. 3. Рубка углов "в иглу" – один конец бревна соединяется "в простую чашу", другой – затесывается на два канта и вставляется в паз, выбранный в боку другого бревна. 4. Рубка углов "в реж" – чаша вырубается не в половину, а в четверть бревна таким образом, что между бревнами остается просвет. Такой способ применяется при рубке неотапливаемых помещений в целях экономии леса (хоз. постройки и т. п.).', 'stroit.ru'),
('Румпа', 'См. ИЗРАЗЦЫ.', 'stroit.ru'),
('Рундук', 'Крытая площадка наружной деревянной лестницы.', 'stroit.ru'),
('Руст (рустик)', 'Руст – рельефная кладка или облицовка стен камнями с грубо отесанной или выпуклой лицевой поверхностью.', 'stroit.ru'),
('Рыбий пузырь', 'В поздней готике – оконный проем сложной криволинейной формы.', 'stroit.ru'),
('Ряжи', 'Ряжи - заполненные камнем и песком прямоугольные бревенчатые конструкции. Ряжи устраиваются при строительстве гидротехнических сооружений (шлюзов, плотин, мостов).', 'stroit.ru'),
('Сажень', 'Сажень - русская мера длины, определяемая средними размерами человеческого тела. Малая сажень – от поднятой на уровень плеча руки, до пола. Косая сажень – расстояние от подошвы левой ноги до конца пальцев поднятой вверх правой руки. 1 сажень = 48 вершкам = 7 футам = 84 дюймам = 2,13360 м.', 'stroit.ru'),
('Саман', 'Высушенный на воздухе кирпич, приготовленный из смеси глины, песка и соломы.', 'stroit.ru'),
('Самовольная застройка', 'Строительство здания или иного сооружения, на земельном участке, не выделенном в порядке, установленном законом. Лицо, осуществившее такую застройку, не приобретает права собственности на возведенные объекты.', 'stroit.ru'),
('Самцовая (безгвоздевая) крыша', 'Конструкция крыши, в которой тес укладывается на горизонтальные бревна – слеги. Концы слег врубаются в поперечные бревна сруба, образующие фронтон.', 'stroit.ru'),
('Самцовый фронтон', 'Бревенчатый фронтон.', 'stroit.ru'),
('Сандрик', 'Сандрик - декоративный архитектурный элемент в виде небольшого карниза. Сандрик располагается над окном или дверью.', 'stroit.ru'),
('Саркофаг', 'Саркофаг - первоначально название породы известняка, способствующего разложению тела и употреблявшегося для изготовления гробов. Отсюда переносное значение саркофага – гроб, небольшая гробница.', 'stroit.ru'),
('Сборные железобетонные конструкции', 'Строительные конструкции заводского изготовления, которые монтируются непосредственно на строительной площадке.', 'stroit.ru'),
('Сваи', 'Сваи - деревянные, металлические или железобетонные "стержни", которые заглубляют в основание зданий и сооружений. Сваи передают нагрузку от фундамента на плотные (материковые) грунты.', 'stroit.ru'),
('Свод', 'Свод - строительная конструкция криволинейной формы, служащая для перекрытия помещения. Различают части свода: ПЯТА – опорная часть свода. ЗАМОК – верхняя часть свода. ШЕЛЫГА – линия, проходящая в замковой части свода и соединяющая его верхние точки. ПРОЛЕТ – расстояние между пятами свода. СТРЕЛА ПОДЪЕМА – отвесная линия из замковой части до прямой, соединяющей пяты. ЩЕКА, или ЛЮНЕТ – торей, срез свода.', 'stroit.ru'),
('Связи', 'Связи - деревянные, металлические или железобетонные элементы, стягивающие пяты арок, сводов и др. строительных конструкций, в которых возникают распорные усилия. Связи гасят распор, передающийся на столбы и стены сооружения.', 'stroit.ru'),
('Сграффито. Граффито', 'Способ декоративной отделки фасадов, заключающийся в нанесении на поверхность стены двух тонких разноцветных слоев штукатурки и последующем процарапывании металлическим инструментом высохшего верхнего слоя до нижнего – фонового.', 'stroit.ru'),
('Сейф', 'Устройство с площадью основания изнутри не более 2 кв.м (если более – это уже хранилище ценностей согласно ГОСТ Р 50862-96), предназначенное для хранения ценностей, документов и носителей информации, устойчивое к взлому.', 'stroit.ru'),
('Сейф встроенный', 'Сейф, предназначенный для установки в полу, стене, а также внутри другого сейфа.', 'stroit.ru'),
('Сейф огнестойкий', 'Устройство с площадью основания изнутри не более 2 кв.м, предназначенное для хранения ценностей, документов и носителей информации, устойчивое к взлому и воздействию опасных факторов пожара.', 'stroit.ru'),
('Секретность замка', 'Количество различных комбинаций его запирающего устройства, каждая из которых соответствует только определенному ключу или коду.', 'stroit.ru'),
('Сени', 'Сени - нежилая часть, как правило, деревенского дома, примыкающая к жилой части дома. Используется сени для хозяйственных нужд, летом – для ночлега.', 'stroit.ru'),
('Септик', 'Септик – сооружение для очистки небольших количеств (до 25 м3, реже до 50 м3 в сутки) бытовых сточных вод.', 'stroit.ru'),
('Сетки стеклянные', 'Сетки стеклянные – армирующие элементы штукатурок и стяжек полов. Основа сеток стеклянных — стекловолокнистые нити.', 'stroit.ru'),
('Сеть ТN', 'Питающая сеть, которая имеет точку, непосредственно связанную с землей, а открытые проводящие части электроустановки присоединяются к этой точке посредством нулевых защитных проводников.', 'stroit.ru'),
('Сеть ТN-S', 'Сеть ТN, в которой нулевой рабочий и нулевой защитный проводники работают раздельно по всей сети.', 'stroit.ru'),
('Сеть ТТ', 'Питающая сеть, которая имеет точку, непосредственно связанную с землей, а открытые проводящие части электроустановки присоединены к заземлителю, электрически независимому от заземлителя нейтрали источника питания.', 'stroit.ru'),
('Сиккативы', 'Растворы металлических солей жировых кислот в органических растворителях, служащие для ускорения высыхания лаков и красок.', 'stroit.ru'),
('Силовой кабель', 'Силовой кабель – кабель, подключающийся напрямую к источнику питания.', 'stroit.ru'),
('Сифон', 'Сифон – гидравлическое устройство, представляет собой стеклянную или резиновую трубку в форме перевернутого U с коленами разной высоты.', 'stroit.ru'),
('Скарпель', 'Скарпель – круглый или граненый стальной стержень. Один его конец расширяется в виде лопаточки с острозаточенным краем, по другому – тупому – концу наносят удары молотком. Используется скульпторами, а также при производстве ремонтно-строительных работ.', 'stroit.ru'),
('Сквер', 'Благоустроенная и озелененная территория внутри жилой застройки.', 'stroit.ru'),
('Слеги', 'Слеги - горизонтальные бревна, брусья. На слеги настилается пол.', 'stroit.ru'),
('Слобода', 'Расположенное вдоль дороги, ведущей в город, пригородное поселение.', 'stroit.ru'),
('Смалковка', 'Изменение формы поперечного сечения уголкового профиля, вследствие изгибания его полок внутрь. Изменение формы поперечного сечения уголкового профиля, вследствие изгибания его полок наружу называется размалковка.', 'stroit.ru'),
('Смеситель', 'Водоразборное устройство, обеспечивающее смешение холодной и горячей воды, а также регулирование ее расхода и температуру потребителем.', 'stroit.ru'),
('Смесь бетонная', 'Смесь вяжущих, заполнителей, затворителей и, при необходимости, добавок до ее укладки', 'stroit.ru'),
('Смесь сухая бетонная', 'Бетонная смесь без затворителя', 'stroit.ru'),
('СНиП (Строительные нормы и правила)', 'Содержащий обязательные к выполнению требования нормативный документ в области строительства.', 'stroit.ru'),
('Спейсер', 'Спейсер – идущая по периметру стеклопакета между стеклами тонкая полая рамка. Внутри спейсера находится влагопоглотитель.', 'stroit.ru'),
('Сплит-система', 'Сплит-система – кондиционер раздельного типа, состоящий из 2-х и более блоков, наружного и одного или нескольких внутренних.', 'stroit.ru'),
('Сруб. Стопа', 'Стены рубленого деревянного сооружения, собранные из соответствующим образом обработанных бревен. Бревна укладываются "в клеть" – одно на другое и в углах соединяются "в обло" ("в чашу"), "в лапу" и т. д. См. РУБКА УГЛОВ.', 'stroit.ru'),
('Сталь арматурная периодического профиля', 'Стальные стержни с равномерно расположенными на их поверхности под углом к продольной оси стержня поперечными выступами (рифлением) для улучшения сцепления с бетоном.', 'stroit.ru'),
('Стамеска', 'Столярный инструмент, предназначенный для резания материала небольшой толщины. Например, для зачистки гнезд и пазов, прирезки и подгонки соединений; снятия фасок, выдалбливания гнезд. В зависимости от формы и заточки рабочей части различают плоские и полукруглые стамески. У полукруглых стамесок полотно имеет изгиб. В зависимости от степени изогнутости полотна полукруглые стамески подразделяются на отлогие, средние, крутые и церазики. Полукруглые стамески используются при выполнении резьбы. В зависимости от характера работы они затачиваются как снаружи, так и изнутри.', 'stroit.ru'),
('Стекло армированное', 'Стекло армированное. В армированное стекло запрессовывается отожженная, хромированная или никелированная стальная проволока, которая служит каркасом, удерживающим мелкие осколки стекла при его повреждении.', 'stroit.ru'),
('Стекло жидкое', 'Стекло жидкое - воздушное вяжущее, изготавливаемое путем обжига смеси, состоящей из кварцевого песка, и соды. Полученное стекло после дробления растворяют в воде. Натриевое жидкое стекло применяется при производстве бетонов со специальными свойствами (кислотоупорных, жаростойких), огнезащитных красок и других материалов.', 'stroit.ru'),
('Стекло закаленное (сталинит)', 'Стекло закаленное (сталинит). Стекло, многократно нагретое до температуры закалки (540-560 град.С) с последующим быстрым равномерным охлаждением воздухом.', 'stroit.ru'),
('Стекло пулестойкое', 'Защитное стекло, выдерживающее воздействие огнестрельного оружия и препятствующее сквозному проникновению поражающего элемента.', 'stroit.ru'),
('Стекло ячеистое (пеностекло)', 'Стекло ячеистое (пеностекло). Представляет собой ячеистый теплоизоляциионный материал, получаемый спеканием в печи стеклянного порошка с одновременным вспучиванием его под действием газообразователя. Стекло ячеистое – один из самых прочных теплоизоляционных материалов (средняя плотность — 1200 кг/м3).', 'stroit.ru'),
('Стеклоизол', 'Модифицированный битумный кровельный и гидроизоляционный наплавляемый материал. Предназначен для устройства кровельного ковра зданий и сооружений, а также различных гидроизоляционных работ. Сохраняет эластичность (т. е. возможность укладки) при температуре до минус 10 градусов C, теплостойкость – до +85°C. Состоит из прочной стекловолокнистой основы с нанесенными с двух сторон слоями высококачественного вяжущего на основе окисленного битума.', 'stroit.ru'),
('Стеклообои', 'Стеклообои – рулонный материал на основе тисненного стекловолокна. Стеклообои огнестойки.', 'stroit.ru'),
('Стеклопакет', 'Стеклопакет – пакет, состоящий из двух или трех листов стекла (одно- и двухкамерные пакеты соответственно), герметично закрепленных на металлической рамке. Внутри стеклопакета находится разреженный воздух или инертный газ. Стеклопакет отличается хорошей теплоизоляцией, звукоизоляцией и герметичностью, не запотевает и не загрязняется изнутри.', 'stroit.ru'),
('Стеклопор', 'Стеклопор – гранулы, получаемые вспучиванием растворимого жидкого стекла или так называемой силикатглыбы — продукта охлаждения расплава натриевого или калиевого стекла. Стеклопор имеет среднюю плотность 200-300 кг/куб.м.', 'stroit.ru'),
('Стеклопрофилит', 'Стеклопрофилит (профильное стекло) – длинноразмерное стеклянное изделие, получаемое методом проката.', 'stroit.ru'),
('Стеклорубероид', 'Стеклорубероид – рулонный материал, получаемый путем двухстороннего нанесения битумного (битуморезинового или битумополимерного) вяжущего на стекловолокнистый холст. Стеклорубероид покрывается с одной или двух сторон сплошным слоем посыпки.', 'stroit.ru'),
('Стеклоэласт', 'Рулонный наплавляемый кровельный и гидроизоляционный материал. Предназначается для устройства кровельного ковра зданий различного типа и назначения, а также гидроизоляции фундаментов, мостов, тоннелей. Сохраняет эластичность при температуре до минус 25 град. C, теплостойкость – до +100 град. C. Состоит из не гниющей основы (стеклохолст, комбинированная ткань, каркасная стеклоткань РАТЛ, полиэстер) и нанесенного с двух сторон СБС-модифицированного полимерно-битумного вяжущего (полимер СБС – стирол-бутадиен-стирол). Марка «П» применяется для нижних слоев кровельного ковра и гидроизоляции. Марка «К» – для верхних слоев кровельного покрытия. Имеет недосыпанную кромку для улучшенной наклейки рулонных полотен внахлест. Наплавляется с помощью пропановой или дизельной горелки, приклеивается мастикой, либо закрепляется механически. Высокие адгезионные свойства СБС битумов позволяют применять Стеклоэласт практически на любые горизонтальные, наклонные, и вертикальные поверхности, изготовленные из негорючих материалов (цементно-песчаная стяжка, минераловатный утеплитель и т. п.).', 'stroit.ru'),
('Стеллаж', 'Стеллаж – многоярусная конструкция для складирования и хранения различных предметов и материалов, состоящее из ряда вертикальных стоек или стенок с полками, ящиками и т.п.', 'stroit.ru'),
('Стойка', 'Столб, колонна и т. п., служащие опорой балкам, перекрытию и работающие на центральное и внецентренное сжатие.', 'stroit.ru'),
('Столп', 'В архитектуре – столб, колонна.', 'stroit.ru'),
('Стопа', 'См. сруб.', 'stroit.ru'),
('Стрельня', 'Стрельня - башня в древнерусском крепостном зодчестве.', 'stroit.ru'),
('Стретчинг', 'Стретчинг – способ натяжения коврового покрытия, при котором ковролин укладывается на укрепленные вдоль стен узкие рейки с двумя рядами вбитых под углом гвоздей и натягивается с помощью специальных инструментов.', 'stroit.ru'),
('Строительные изделия', 'Железобетонные панели, плиты, балки, дверные и оконные блоки и тому подобные детали и элементы заводского изготовления, монтируемые на месте строительства.', 'stroit.ru'),
('Строительные леса', 'См. «леса строительные».', 'stroit.ru'),
('Строительные работы', 'Производственная и хозяйственная деятельность, направленная на возведение, ремонт, реконструкцию зданий и сооружений.', 'stroit.ru'),
('Строительный материал', 'Материал, предназначенный для создания строительных конструкций зданий и сооружений, а также изготовления строительных изделий.', 'stroit.ru'),
('Строительный раствор', 'Строительный раствор - смешанные в определенной пропорции цемент/известь/гипс, песок и вода. Строительный раствор используется при возведении каменной (кирпичной) кладки, отделочных работах в качестве связующего. Строительный раствор подразделяется: по виду вяжущих на цементный, известковый, гипсовый и сложный; по назначению – на кладочный, отделочный и специальный.', 'stroit.ru'),
('Строительство', 'Отрасль материального производства, в которой создаются основные фонды производственного и непроизводственного назначения: готовые к эксплуатации здания, сооружения и их комплексы.', 'stroit.ru'),
('Стропила', 'Стропила - несущие конструкции скатной кровли. Стропила состоят из наклонных стропильных ног, вертикальных стоек и наклонных подкосов. При необходимости стропила связываются понизу горизонтальными подстропильными балками.', 'stroit.ru'),
('Строчная застройка', 'Строчная застройка - расположение домов с разрывом между зданиями. При строчной застройке дома располагаются не по одной линии, а уступами.', 'stroit.ru'),
('Струбцина', 'Струбцина – зажимное приспособление для крепления деталей, используемое при склеивании и монтаже деревянных конструкций.', 'stroit.ru'),
('Стук. Стюк. Стукко.', 'Высший сорт штукатурки, в состав которой входят тонко просеянный гипс с мраморной пудрой, квасцы, клей. При застывании приобретает очень высокую прочность.', 'stroit.ru'),
('Стусло', 'Стусло – столярное приспособление, предназначенное для распиливания пиломатериалов под заданным углом.', 'stroit.ru'),
('Стяжка', 'Стяжка – основание под покрытие. Стяжка – слой пола, служащий для выравнивания поверхности нижележащего слоя пола или перекрытия. Стяжка придает покрытию пола на перекрытии заданного уклона. С помощью стяжки укрываются различные трубопроводы, распределяются нагрузки по нежестким нижележащим слоям пола на перекрытии.', 'stroit.ru'),
('Суглинок', 'Рыхлая осадочная горная порода, содержащая 10–30% глинистых частиц (размером менее 0,005 мм). По содержанию глинистых частиц выделяют тяжелые (20–30%), средние (15–20%) и легкие (10–15%) суглинки. Используются как сырье для производства кирпича, черепицы, реже – керамической плитки.', 'stroit.ru'),
('Супесь', 'Рыхлая осадочная горная порода, с содержанием глинистых частиц менее 10%. Применяется в качестве сырья при производстве строительной керамики.', 'stroit.ru'),
('Суппорт', 'Узел, предназначенный для крепления и перемещения (ручного или механического) инструмента. Точность перемещения и жесткость суппорта в значительной степени определяют качество станка. Обычно состоит из резцедержателя и т. н. «салазок», обеспечивающих заданное направление движения инструмента.', 'stroit.ru'),
('Сухая кладка', 'Каменная кладка, выполняемая с перевязкой швов, но без применения кладочного раствора. Для скрепления блоков применяются металлические анкеры.', 'stroit.ru'),
('Сухие смеси', 'Сухие смеси – сыпучие, рационально подобранные смеси вяжущего, заполнителя, наполнителей и специальными добавками (регуляторы схватывания и твердения, адгезивы, пластификаторы и другие). Сухие смеси предназначены для приготовления строительных растворов, смесей для выравнивания бетонных полов, приклеивания строительных плиток, приготовления грунтовок, шпаклевок, штукатурок и затирок. Сухие смеси затворяются водой на месте производства работ.', 'stroit.ru'),
('Таль', 'Таль – подвесное грузоподъемное устройство с ручным или механическим приводом, состоящее из подвижного и неподвижного блоков и троса.', 'stroit.ru'),
('Тамбур', 'Тамбур - помещение небольшой площади внутри здания или наружная пристройка у входных дверей. Тамбур препятствует переохлаждению основных помещений.', 'stroit.ru'),
('Таун-Хаус', 'Таун-Хаус – рассчитанный на несколько семей элитный коттедж.', 'stroit.ru'),
('Тахеометр', 'Прибор, предназначенный для тахеометрической съемки с целью получения плана с изображением ситуации и рельефа.', 'stroit.ru'),
('Твердость', 'Твердость. Свойство материала сопротивляться проникновению в него другого, более твердого тела. Например, по шкале МООСа все природные минералы делятся на 10 групп по твёрдости.', 'stroit.ru'),
('Твердость', 'Свойство материала сопротивляться местной пластической деформации, возникающей при внедрении в него более твердого тела. Не является физической постоянной, а представляет собой сложное свойство, зависящее как от прочности и пластичности материала, так и от метода измерения (методы Бринелля, Роквелла, Виккерса, Шора).', 'stroit.ru'),
('Тегула', 'Тегула - кровельная черепица. Также тегула крыша; кров; кровля.', 'stroit.ru'),
('Текстолит', 'Материал, состоящий из нескольких слоев ткани (бязь, миткаль, бельтинг, шифон, ткани из искусственных и синтетических органических волокон – вискозных, полиамидных, полиэфирных и т. д.), пропитанных синтетической смолой (смолы феноло-формальдегидные, крезоло- и ксиленолоальдегидные, мочевино- и меламино-формальдегидные, а также полиамиды, полиолефины, ацетил- и этилцеллюлоза). Текстолиты на основе стеклянных тканей называются стеклотекстолитами, на основе асбестовых — асботекстолитами. Собственно текстолиты значительно легче (плотность 1,3—1,4 г/см3) асбо- и стеклотекстолитов, характеризуются меньшей теплопроводностью, лучше подвергаются механической обработке и склеиванию, но при этом менее прочны, обладают более низкой тепло- и химической стойкостью. Текстолиты производят в виде листов, пластин и плит. Используют для изготовления подшипников скольжения, электрораспределительных панелей, шестерен, втулок, труб, цилиндров, профилей.', 'stroit.ru'),
('Тектоника', 'Тектоника - соотношение несущих и несомых частей сооружения, выраженное в пластических формах. Тектоника - художественное выражение закономерностей, присущих конструктивной системе здания.', 'stroit.ru'),
('Теодолит', 'Прибор, предназначенный для измерения горизонтальных и вертикальных углов.', 'stroit.ru'),
('Тепловая завеса', 'Тепловая завеса – аэродинамический агрегат, экономящий тепло в помещении путем образования на входе заслонов из теплого воздуха.', 'stroit.ru'),
('Теплоемкость', 'Теплоемкость – способность материала аккумулировать тепловую энергию, Удельная теплоёмкость — это количество тепла, которое необходимо передать 1 кг данного материала, чтобы повысить его температуру на 1 град.С.', 'stroit.ru'),
('Теплопроводность', 'Теплопроводность – способность материала передавать тепло от одной своей части к другой в силу теплового движения молекул. Передача тепла в материале осуществляется кондукцией (путем контакта частиц материала), конвекцией (движением воздуха или другого газа в порах материала) и лучеиспусканием. Размерность теплопроводности Вт/мК.', 'stroit.ru'),
('Теплоусвоение', 'Теплоусвоение – способность материала поглощать тепло в контакте с другими материалами.', 'stroit.ru'),
('Термостат', 'Термостат – прибор, поддерживающий заданную температуру.', 'stroit.ru'),
('Терракота', 'Терракота - неглазурованные керамические изделия строительного, бытового и художественного назначения. Терракота известна со времен неолита.', 'stroit.ru'),
('Терраса', '1. Терраса - естественная или искусственно устроенная на склоне местности горизонтальная площадка для возведения зданий, прокладки дорожек и т. д. 2. Терраса - открытая с трех сторон неотапливаемая пристройка к зданию, соединенная с ним дверью.', 'stroit.ru'),
('Тёс. Тесина', 'Тёс - доски, получаемые путем раскалывания бревна вдоль с помощью клиньев с последующей обтеской. Тёс использовался для обшивки стен сооружений, реже – для покрытия крыш.', 'stroit.ru'),
('Тетива', 'Тетива - наклонная несущая конструкция лестницы. Доски, образующие ступени, врезаются в боковые стороны тетивы. В железобетонных лестницах ступени составляют единое целое с тетивами.', 'stroit.ru'),
('Тетрапилон', 'Тетрапилон - триумфальная арка с двумя взаимно пересекающимися проездами.', 'stroit.ru'),
('Тиксотропность', 'Тиксотропность – способность краски уменьшать вязкость (разжижаться) от механического воздействия и увеличивать вязкость (сгущаться) в состоянии покоя.', 'stroit.ru'),
('Тимпан', 'Тимпан - внутреннее поле фронтона. Треугольный или полуциркульный участок над окном или дверью, выделенный рельефом.', 'stroit.ru'),
('Тонкость помола', 'Тонкость помола – характеристика дисперсности вяжущих, пигментов, наполнителей. Тонкость помола определяется остатком на стандартном сите в процентах по отношению к начальной навеске материала.', 'stroit.ru'),
('Торец', 'Прямоугольный или шестиугольный короткий брусок (торцовая шашка), предназначенный для устройства полов или мощения дорожных покрытий.', 'stroit.ru'),
('Торкрет - бетон (торкретирование)', 'Способ бетонирования при котором бетонная смесь послойно наносится на бетонируемую поверхность под давлением сжатого воздуха.', 'stroit.ru'),
('Торкретирование', 'Процесс бетонирования или оштукатуривания под действием сжатого воздуха с использованием цемент-пушки.', 'stroit.ru'),
('Тормоз дисковый', 'Тормоз, у которого торможение осуществляется прижатием вращающихся дисков к неподвижным.', 'stroit.ru'),
('Тормоз колодочный', 'Тормоз, у которого торможение осуществляется прижатием колодок к тормозному шкиву.', 'stroit.ru'),
('Тормоз ленточный', 'Тормоз, у которого торможение осуществляется прижатием ленты к тормозному шкиву.', 'stroit.ru'),
('Торфоизоляционные материалы', 'Торфоизоляционные материалы – теплоизоляционные материалы, полученные из молодого торфа — неперегнившего моха-сфагнума путём приготовления пульпы (формовочной массы, состоящей из воды и 5-10% торфа) с последующей формовкой и тепловой обработкой. Средняя плотность торфоизоляционных материалов — 200-300 кг/куб.м.', 'stroit.ru'),
('Точка росы', 'Точка росы – температура, до которой должен охладиться воздух, чтобы достичь состояния насыщения водяным паром при данном влагосодержании и неизменном давлении. При достижении точки росы в воздухе или на предметах, с которыми он соприкасается, начинается конденсация водяного пара.', 'stroit.ru'),
('Тощий бетон', 'Бетон с высоким содержанием заполнителей и низким содержанием цемента.', 'stroit.ru'),
('Трап', 'Трап – воронкообразный элемент системы водоотвода из влажных помещений.', 'stroit.ru'),
('Трельяж', 'Легкая решетка для вьющихся растений.', 'stroit.ru'),
('Трехчетвертная колонна', 'Трехчетвертная колонна -колонна выступающая из плоскости стены на 3/4 своего диаметра.', 'stroit.ru'),
('Трюмо', 'Украшенный орнаментом простенок между окнами. Высокое зеркало, расположенное в простенке.', 'stroit.ru'),
('Уайт-спирит', 'Смесь жидких углеводородов, один из растворителей нефтяных. Применяется как растворитель в лакокрасочной промышленности.', 'stroit.ru'),
('Углепластик', 'Пластмасса, содержащая углеродные волокна.', 'stroit.ru'),
('Углерод', 'Химический элемент, важнейшая составляющая часть органических веществ.', 'stroit.ru'),
('Угодье', 'Угодье - участок земли, пригодный для хозяйственного использования.', 'stroit.ru'),
('Ударостойкое стекло', 'Защитное стекло, выдерживающее многократный удар свободно падающего тела с нормируемыми показателями.', 'stroit.ru'),
('Узорит', 'Узорит - облицовочный материал из отходов производства стекла.', 'stroit.ru'),
('Укосина', 'Укосина - раскос, наклонно стоящая к стойке подпорка.', 'stroit.ru'),
('Укрывистость', 'Способность лакокрасочного состава при равномерном нанесении на одноцветную поверхность скрыть ее первоначальный цвет.', 'stroit.ru'),
('Улица', 'Пространство между двумя рядами домов в населенном пункте, предназначенное для прохода и проезда.', 'stroit.ru'),
('Ультрамарин', 'Яркая, сочная синяя краска.', 'stroit.ru'),
('Умбра', 'Темная зеленовато-коричневая минеральная краска.', 'stroit.ru'),
('Упругость', 'Свойство физических тел восстанавливать свою форму после прекращения воздействия на них внешних сил.', 'stroit.ru'),
('Уровень. Ватерпас', 'Прибор для проверки горизонтальности плоскости.', 'stroit.ru'),
('Усадка', 'Усадка – уменьшение линейных размеров и объёма материалов вследствие потери ими влаги, уплотнения, затвердевания и подобных процессов.', 'stroit.ru'),
('Фальц', 'Фальц - вид шва при соединении листов металлической кровли. Наиболее герметичным и влагонепроницаемым является двойной стоячий фальц. Это продольное соединение, выступающее над плоскостью кровли между двумя прилегающими кровельными картинами, кромки которых имеют двойной загиб.', 'stroit.ru'),
('Фальцевая кровля', 'Кровля из листовой и рулонной оцинкованной стали, а также из стали с полимерным покрытием, в которой соединение отдельных элементов покрытия выполнены с помощью фальцев.', 'stroit.ru'),
('Фальш-пол', 'Фальш-пол – сборно-разборные полы промышленных зданий с вентилируемым подпольем. В фальш-поле прокладываются электротехнические и прочие коммуникации.', 'stroit.ru'),
('Фанера', 'Фанера - слоистый древесный материал, склеенный из нечетного (три и более) числа листов лущеного шпона. Волокна шпона смежных слоев фанеры ориентированы взаимно перпендикулярно. Подразделяется на фанеру общего и специального назначения.', 'stroit.ru'),
('Фарфор', 'Изделия тонкой керамики белого цвета с температурой обжига 1250–1450 град. С. Имеют низкое водопоглощение (до 1%) и достаточно высокую твердость (6,57–7,5 по шкале Мооса). Подразделяются на изделия из твердого и мягкого фарфора, что определяется температурой обжига. Существуют технические разновидности фарфора, обладающие рядом специфических качеств.', 'stroit.ru'),
('Фасад', 'Фасад - лицевая сторона здания. Различают фасады: главный, боковой, задний, уличный, дворовый, садовый.', 'stroit.ru'),
('Фасад вентилируемый', 'Фасад вентилируемый – в настоящее время все большее распространение получает конструкция фасада вентилируемого с применением панелей и дополнительного утеплителя. Такой фасад вентилируемый сочетает быстроту монтажа, высокое качество теплозащиты, долговечность, привлекательный внешний вид.', 'stroit.ru'),
('Фасонное отверстие', 'Отверстие, форма которого отлична от круглой.', 'stroit.ru'),
('Фахверк', 'Фахверк - каркасная система, состоящая из связанных между собой стоек, балок и раскосов. Фахверк играет не только конструктивную, но и декоративную роль, визуально расчленяя фасад.', 'stroit.ru'),
('Фаянс', 'Изделия тонкой керамики, покрытые прозрачной или глухой глазурью. Водопоглощение составляет 9–12%. В отличие от фарфора содержат больше глины, а также мел или известняк. Применяется для изготовления плитки, сантехнических изделий.', 'stroit.ru'),
('Ферма стропильная', 'Ферма стропильная - решетчатая несущая конструкция, служащая для перекрытия больших пролетов.', 'stroit.ru'),
('Фехраль', 'Общее название жаростойких сплавов на железной основе, содержащих хром (Cr) и алюминий (Al), сочетающих жаростойкость с высоким удельным электрическим сопротивлением. Фехраль выпускается преимущественно в виде проволоки и ленты. Применяется как заменитель нихрома для изготовления элементов сопротивлений, работающих с нагревом не выше 1000 град. C.', 'stroit.ru'),
('Фибра', 'Фибра - материал в виде волокон или узких полос, применяемый для дисперсного армирования бетонных конструкций. При этом повышается сопротивление растяжению, истиранию, ударным нагрузкам. Фибра может быть стальной, стеклянной, базальтовой, полимерной.', 'stroit.ru'),
('Фибробетон', 'Фибробетон – конструкционный материал, получаемый на основе мелкозернистого бетона, армированного тонкодисперсным синтетическим или стеклянным волокном, а также металлической сечкой-фиброй.', 'stroit.ru'),
('Фибролит', 'Фибролит – плитный материал, изготавливаемый обычно из специальных древесных стружек (древесной шерсти) и неорганического вяжущего вещества. Древесную шерсть получают в виде тонкой и узкой стружки длиной до 30 см на специальных станках. В качестве вяжущего в фибролите используют портландцемент, реже магнезиальное вяжущее. Теплоизоляционный фибролит имеет среднюю плотность 300-500 кг/м3.', 'stroit.ru'),
('Фибролит акустический', 'Фибролит акустический – материал, получаемый из древесной шерсти или синтетического волокна и минерального вяжущего (портландцемента или гипса). Коэффициент звукопоглощения фибролита акустического не менее 0,4.', 'stroit.ru'),
('Фибролит магнезиальный', 'Фибролит магнезиальный – фибролит, изготавливаемый без специальной минерализации, поскольку каустический магнезит затворяется водными растворами магнезиальных солей, которые связывают содержащиеся в древесине водорастворимые вещества.', 'stroit.ru'),
('Фиброцемент', 'Материал, состоящий из цемента (80-90%), армирующих волокон и минеральных заполнителей. Используется для изготовления стеновых панелей, перегородок и облицовочных плит (листов). Изначально (в самом начале XX века) в качестве фибры применялся асбест, сегодня это – синтетические и стеклянные волокна.', 'stroit.ru'),
('Филёнка', 'Филёнка - выделенные тонкими профилированными рамками участки стены. Также филёнка - щиток из тонких досок, фанеры или пластика, закрывающий просвет в каркасе полотна двери.', 'stroit.ru'),
('Фитинг', 'Фитинг – соединительная часть трубопровода, устанавливаемая в местах его разветвлений, поворотов и переходов на другой диаметр.', 'stroit.ru'),
('Фланец', 'Фланец – металлический плоский диск на концах труб, служащий для скрепления их между собой при помощи болтов или заклепок.', 'stroit.ru'),
('Флеп', 'Флеп диск – шлифовальное устройство, представляющее собой абразивный материал в виде листьев прикрепленных к армированному стеклотканью кругу. Флеп диски обладают целым рядом преимуществ перед фибровыми, особенно при полировке тонких металлических листов. Они не требуют применения жесткой подложки и могут надеваться непосредственно на станок; не оставляют нагара, работают без шума и вибрации; имеют большую производительность и износостойкость. Флеп диски бывают двух видов: прямого (обеспечивают высокий результат при обработке ровных поверхностей) и конического (при выполнении шлифовальных операций легко достигают самых труднодоступных мест обрабатываемой поверхности).', 'stroit.ru'),
('Флигель', 'Боковая (или отдельно стоящая) пристройка к основному зданию.', 'stroit.ru'),
('Флюгарка', 'Устройство, предназначенное для измерения направления ветра, в виде одной или двух расположенных под углом пластинок, при вращении вокруг вертикальной оси устанавливающихся противовесом навстречу ветру.', 'stroit.ru'),
('Флюгер', 'Прибор для определения направления и скорости ветра в виде поворачивающегося под воздействием ветра металлического флажка, установленного на вертикальной оси. Противовес флажка направлен в сторону, откуда дует ветер. Сила ветра определяется по углу отклонения от вертикали укрепленной на флюгере перпендикулярно направлению ветра свободно качающейся металлической дощечке.', 'stroit.ru'),
('Флюорит', 'Другое название – плавиковый шпат, минерал класса фторидов; формула химического состава CaF2. Твердость по минералогической шкале 4, плотность 3180–3200 кг/м3, температура плавления – 1360 град. C. Окраска разнообразная: желтая, зеленая, фиолетовая и др. Используется в металлургии в качестве флюса; в химической промышленности – для получения плавиковой кислоты, а также – эмалей и глазурей для керамического производства. Широко применяется в оптике для изготовления линз, объективов и т. д.', 'stroit.ru'),
('Фольгоизол', 'Рулонный материал, состоящий из тонкой рифленой алюминиевой фольги, покрытой с нижней стороны слоем битумно-резинового или битумно-полимерного вяжущего, состоящего из битума и резины или каучука с минеральным наполнителем и антисептиком.', 'stroit.ru'),
('Фонарь здания', 'Выпуклая (различной формы) часть покрытия сооружения, имеющая проемы для освещения и вентиляции.', 'stroit.ru'),
('Форсунка', 'Форсунка – устройство для распыления жидкостей. Подача жидкости осуществляется под давлением или при помощи сжатого воздуха.', 'stroit.ru'),
('Фосфогипс', 'Фосфогипс – побочный продукт промышленного производства, в основном содержащий двуводный гипс. Фосфогипс – ценное сырьё для получения строительного гипса.', 'stroit.ru'),
('Фрамуга', 'Фрамуга - верхняя застекленная часть оконного переплета. Иногда фрамуга устанавливается над створками дверей.', 'stroit.ru'),
('Фреза', 'Многозубый инструмент в виде тела вращения для обработки различных материалов (металлов, древесины, пластмасс) резанием (фрезерованием). Различают цилиндрические, торцовые, дисковые пазовые, отрезные, концевые, шпоночные, угловые, фасонные, червячные и др. фрезы. Кроме того, фрезы отличают по форме зубьев; по направлению винтовых канавок (с правыми и левыми канавками); по конструкции (цельные, составные, сборные и т. д.): по способу крепления; по материалу режущей части.', 'stroit.ru'),
('Фреза концевая', 'Фреза, предназначенная для одновременной обработки двух взаимно перпендикулярных поверхностей.', 'stroit.ru'),
('Фреза торцовая', 'Фреза, предназначенная для обработки плоских поверхностей, ось которых перпендикулярна обрабатываемой поверхности. Бывает сборной с пластинками из твердого сплава и со вставными ножами. Режущая часть каждого ножа имеет режущие кромки, расположение которых определяется проекцией на осевую плоскость, проходящую через вершину зуба фрезы.', 'stroit.ru'),
('Фреза фасонная', 'Фреза, предназначенная для обработки поверхностей, повторяющих по форме ее саму.', 'stroit.ru'),
('Фреза цилиндрическая', 'Фреза, предназначенная для обработки плоских поверхностей, ось которых параллельна обрабатываемой поверхности. Цилиндрическая фреза имеет зубья только на цилиндрической части.', 'stroit.ru'),
('Фреза червячная', 'Фреза, предназначенная для нарезания зубьев звездочек к однорядным и многорядным приводным роликовым и втулочным цепям и для изготовления цилиндрических зубчатых колес.', 'stroit.ru'),
('Фреза шпоночная', 'Фреза, служащая для обработки шпоночных пазов.', 'stroit.ru'),
('Фреска', 'Фреска - живопись водяными красками по свеженанесенной, сырой штукатурке.', 'stroit.ru'),
('Фронтон', 'Ограниченное по бокам скатами крыши, а снизу карнизом завершение фасада здания, портика, колоннады, как правило, треугольной формы.', 'stroit.ru'),
('Фуганок', 'Ручной столярный инструмент, предназначенный для окончательного чистового строгания; выравнивания больших поверхностей; прифуговки (пригонки) отдельных деталей. Конструктивно устроен как длинный рубанок с двойным ножом.', 'stroit.ru'),
('Фундамент', 'Преимущественно подземная часть сооружения, служащая его опорой и передающая нагрузку на основание.', 'stroit.ru'),
('Футеровка', 'Защитная внутренняя облицовка. Термин используется применительно к тепловым агрегатам, химическим аппаратам, дробильному оборудованию. Футеровка выполняется из материала в большей степени устойчивого к воздействию соответствующего фактора (может быть огнеупорной, кислотоупорной, теплоизоляционной, защищающей от механических воздействий). Иногда футеровкой называют наружную защитную облицовку элементов агрегатов.', 'stroit.ru'),
('Хозяйственный способ строительства', 'Форма организации строительных работ, при которой работы выполняются собственными силами застройщика, без привлечения сторонних подрядных организаций.', 'stroit.ru'),
('Хоры', 'Верхняя открытая галерея или балкон внутри парадного зала здания.', 'stroit.ru'),
('Царга', 'Царга - рама, соединяющая ножки стола или стула.', 'stroit.ru'),
('Цековка', 'Зенковка для зачистки торцовых поверхностей (например, снятие бобышек). Как правило, выполняется в виде насадных головок, имеющих торцовые зубцы.', 'stroit.ru'),
('Цемент', 'Порошкообразный строительный вяжущий материал, который обладает гидравлическими свойствами, состоит из клинкера и, при необходимости, гипса или его производных и добавок.', 'stroit.ru'),
('Цемент общестроительный', 'Цемент, основным требованием к которому является обеспечение прочности и долговечности бетонов или растворов.', 'stroit.ru'),
('Цементно-стружечная плита (ЦСП)', 'Древесностружечная плита на цементном вяжущем. Изготавливается из деревянной стружки, портландцемента и специальных примесей, применяемых для ускорения гидратационных процессов. Плиты ЦСП монолитны, не расслаиваются, их кромки не чувствительны к атмосферному воздействию. Они не горят, устойчивы к воздействию низких температур, обладают хорошими звукопоглощающими свойствами, не содержат асбеста и формальдегида, устойчивы к поражению грибками, плесенью и насекомыми, являются экологически чистым и гигиенически чистым строительным материалом. Применяются в сборных конструкциях различного назначения: фасадах, полах, подвесных потолках, противопожарных дверях и перегородках, заборах, мощеных дорожках.', 'stroit.ru'),
('Цементный клинкер', 'Продукт, получаемый обжигом до спекания или плавления сырьевой смеси надлежащего состава и содержащий, главным образом, высокоосновные силикаты и (или) высоко- или низкоосновные алюминаты кальция.', 'stroit.ru'),
('Цементный раствор', 'Смесь цемента, песка и воды.', 'stroit.ru'),
('Цемянка', 'Цемянка - мелкотолченый красный кирпич или керамика. Цемянка добавляется в известковый раствор для придания ему розового оттенка.', 'stroit.ru'),
('Цепная (двухрядная) кладка', 'Кирпичная кладка, перевязка в которой осуществляется чередованием тычковых и ложковых рядов с перекрытием всех вертикальных швов.', 'stroit.ru'),
('Циклопическая кладка', 'Кладка стен сооружений из больших отесанных каменных глыб без применения связующего раствора.', 'stroit.ru'),
('Цоколь', 'Нижняя часть наружной стены здания, расположенная непосредственно на фундаменте, или верхняя, надземная, часть ленточного фундамента.', 'stroit.ru'),
('Чердак', 'Как правило, неотапливаемое помещение, ограниченное крышей и верхним (чердачным) перекрытием здания.', 'stroit.ru'),
('Черепица', 'Черепица - штучный кровельный материал из обожженной глины. Черепица также изготавливается из металла или пластика.', 'stroit.ru'),
('Черепица глиняная', 'Керамический кровельный материал, изготавливаемый из глинистого сырья с добавками. Один из древнейших кровельных материалов. Долговечен, огнестоек.', 'stroit.ru'),
('Черный пол. Накат', 'Черный пол - настил по балкам перекрытия, на который укладывается утеплитель.', 'stroit.ru'),
('Черный потолок', 'Черный потолок - настил по потолочным балкам, закрытый снизу слоем облицовки.', 'stroit.ru'),
('Четверик', 'Четверик - четырехугольный в плане бревенчатый сруб.', 'stroit.ru'),
('Чиллер', 'Холодильная машина, используемая в центральных системах кондиционирования. Чиллеры применяются в различных комбинациях как часть системы «чиллер – фанкойл», позволяющей обеспечить независимое регулирование температуры одновременно в большом количестве помещений. Некоторые модели чиллеров могут работать в режиме теплового насоса.', 'stroit.ru'),
('Чистый пол', 'Верхняя видимая поверхность пола.', 'stroit.ru'),
('Чистый потолок', 'Нижняя видимая поверхность потолка.', 'stroit.ru'),
('Чугун', 'Сплав железа с большим количеством углерода (св. 2,14%) и другими элементами. Основная масса чугуна перерабатывается в сталь.', 'stroit.ru'),
('Чугун белый', 'Чугун, в котором весь углерод находится в виде карбида железа или цементита.', 'stroit.ru'),
('Чугун высокопрочный', 'Материал, в котором углерод присутствует в виде шарообразного графита. Маркировка – ВЧ.', 'stroit.ru'),
('Чугун ковкий', 'Ковкий чугун получают путем отжига отливок белого чугуна. При этом углерод приобретает вид хлопьевидного графита. Маркировка – КЧ.', 'stroit.ru'),
('Чугун серый', 'Углерод в сером чугуне присутствует в виде пластинчатого или волокнистого графита. Маркировка – СЧ.', 'stroit.ru'),
('Шамот', 'Шамот – обожженная до спекания огнеупорная глина, подвергнутая затем измельчению.', 'stroit.ru'),
('Шанец', 'Шанец - колодец, оставляемый в бетонном фундаменте или полу. Шанец служит для установки анкерных болтов или оборудования.', 'stroit.ru'),
('ШАОН', 'Шнур асбестовый общего назначения (ШАОН). Представляет собой волокна хризотилового асбеста с некоторой долей хлопка и других волокон химического происхождения. Применяется для уплотнения разнообразных соединений в теплопроводящих системах и тепловых агрегатов. Сохраняет работоспособность вплоть до температуры +400 град. Цельсия.', 'stroit.ru'),
('Шарнир', 'Подвижное соединение двух тел, при котором их относительное движение является вращением вокруг оси (в случае цилиндрического шарнира; другое название – вращательная кинематическая пара) или точки (шаровой шарнир; другое название – сферическая кинематическая пара). Шарниры используются не только в механизмах машин, но и в строительных конструкциях с целью разгрузки элементов от изгибающих усилий и уменьшения температурных напряжений.', 'stroit.ru'),
('Шахта лифта', 'Сооружение, в котором движутся кабина и (или) противовес.', 'stroit.ru'),
('Шашка', 'См. торец.', 'stroit.ru'),
('Швеллер асбестоцементный', 'Изделие, аналогичное по форме стальному прокатному швеллеру, предназначенное для изготовления каркаса строительных конструкций.', 'stroit.ru'),
('Шеллак', 'Природная смола животного происхождения, вырабатываемая насекомыми, паразитирующими на некоторых древесных тропических и субтропических растениях. Хорошо растворим в щелочах и в низших алифатических спиртах, нерастворим в бензине, жирах и маслах. По окраске различают темный, оранжевый и бледный шеллак. Применяется для приготовления спиртовых лаков и политур.', 'stroit.ru'),
('Шелыга', 'Шелыга - линия, соединяющая верхние точки арки или свода.', 'stroit.ru'),
('Шерхебель', 'Столярный инструмент, предназначенный для первоначального грубого строгания вдоль, поперек и под углом к волокнам древесины. Нож шерхебеля имеет закругленный конец, который оставляет после себя неглубокие желобки.', 'stroit.ru'),
('Шестерик', 'Шестерик - шестиугольный в плане бревенчатый сруб.', 'stroit.ru'),
('Шефмонтаж', 'Организационно-техническое руководство монтажом оборудования поставщиком этого оборудования при выполнении монтажных работ специалистами заказчика.', 'stroit.ru'),
('Шиногиб', 'Устройство, предназначенное для изгиба медных и алюминиевых токоведущих шин. Основные технические параметры: усилие (в тоннах); максимальная ширина шины (в мм); максимальная толщина шины (в мм); диапазон угла изгиба (в град.); масса (в кг). Шиногибы различаются типом привода, например, гидравлический с электроприводом, ручной механический. Компактность и мобильность позволяет работать с шиногибом непосредственно на месте монтажа шин.', 'stroit.ru'),
('Шинорез', 'Устройство для резки медных и алюминиевых токоведущих шин.', 'stroit.ru'),
('Шифер', 'Шифер – материал, полученный армированием цементного камня тонкими волокнами асбеста.', 'stroit.ru'),
('Шихта', 'Смесь материалов в определенной пропорции, подлежащих переработке в металлургических, химических и иных агрегатах. Так, например, в состав металлургической шихты входят руды, рудные концентраты и агломераты, шлаки, пыль из уловительных устройств, металлический лома, флюсы и т. д.', 'stroit.ru'),
('Шкант', 'Шкант - шип, вставляемый на клею в соответствующие гнезда деревянных деталей. Шкант скрепляет детали между собой.', 'stroit.ru'),
('Шкаф-купе', 'Шкаф-купе – пристенный или встроенный в нишу шкаф с раздвижными дверями.', 'stroit.ru'),
('Шлаки вулканические', 'Выброшенные из кратера и застывшие частицы сильно насыщенного газом вулканического расплава. Используются в качестве заполнителей для легких бетонов.', 'stroit.ru'),
('Шпаклевки', 'Отделочные составы для выравнивания поверхностей перед окраской. Изготавливаются гипсовые, клеевые, масляные, полимерные и лаковые.', 'stroit.ru'),
('Шпилька', 'В машиностроении – крепежная деталь в виде металлического стержня с резьбой на обоих концах.', 'stroit.ru'),
('Шпингалет', 'Задвижка для запирания окон и дверей. Как правило, выполняется из металла. Один из видов оконной (дверной) фурнитуры.', 'stroit.ru'),
('Шплинт', 'Изготовленный из углеродистой стали согнутый пополам (для удобства разведения выступающих концов одна половинка делается чуть длиннее другой) проволочный стержень полукруглого сечения. Используется для предотвращения самоотвинчивания гаек и в качестве фиксирующего элемента слабо нагруженных сопряженных деталей.', 'stroit.ru'),
('Шпон', 'Шпон - облицовочный материал в виде тонких листов древесины, получаемый строганием брусьев ценных пород (строганый), или лущением коротких пропаренных бревен из березы, ольхи, сосны на шпонострогательных станках (лущеный). Лущеный шпон используется для изготовления слоистой древесины, фанеры. Пиленый шпон изготавливают из древесины ели, сибирского кедра, пихты. Он является наиболее высококачественным и применяется при изготовлении музыкальных инструментов.', 'stroit.ru'),
('Шпонка', 'В машиностроении – деталь машины или механизма, представляющая собой призматический или клинообразный брусок для соединения вала с надетой на него деталью. В деревянных конструкциях – деталь для соединения элементов деревянной конструкции, воспринимающая сдвигающие усилия. В гидроизоляционных работах – уплотнение из гидроизоляционного материала (например, битума), которым заполнены пазы в швах гидротехнических сооружений.', 'stroit.ru'),
('Шпунт', 'В столярном деле – продольный вырез в ребре доски для плотного соединения досок между собой. Инструмент для обработки камня – стальной четырехгранный или круглый стержень с острым концом. Еще одно значение, получившее распространение в строительной практике – обобщенное название шпунтовых свай, применяемых при устройстве ограждений гидротехнических сооружений, котлованов и траншей.', 'stroit.ru'),
('Шпунтовое ограждение', 'Шпунтовое ограждение – водонепроницаемая сплошная стена, образованная забитыми в грунт деревянными, железобетонными или стальными шпунтовыми сваями.', 'stroit.ru'),
('Штамп', 'Инструмент, предназначенный для придания детали заданной конфигурации посредством штамповки – технологической операции, состоящей в пластической деформации заготовки, как с разделением ее на части, так и без него. Конструкция штампа зависит от нескольких факторов: типа заготовки, используемых для штамповки машин, характера выполняемых операций, объемов производства.', 'stroit.ru'),
('Штангензубомер', 'Вид штангенинструмента. Представляет собой сочетание штангенглубиномера и штангенциркуля. Предназначаен для измерения толщины зуба. Вертикальная линейка служит для установки высоты от вершины зуба, на которой производится измерение толщины зуба, а горизонтальное устройство – для непосредственного измерения толщины зуба.', 'stroit.ru'),
('Штангенинструмент', 'Обобщенное название средств измерения и разметки внешних и внутренних размеров (штангенциркули, штангенрейсмасы, штангенглубиномеры и штангензубомеры). Представляет собой две измерительные поверхности (т. н. «губки»), между которыми устанавливается размер. Одна из губок составляет единое целое с линейкой (штангой), а другая – соединена с двигающейся по линейке рамкой. В целях повышения надежности штангенинструмент, как правило, изготовляется из материалов с высокой износостойкостью.', 'stroit.ru'),
('Штепсель', 'Приспособление, имеющее два гнезда, в которые втыкается вилка с проводом для включения в электрическую сеть переносных аппаратов (ламп, нагревательных приборов и т. д.).', 'stroit.ru'),
('Штраба', 'То же, что и штроба. Канавка в бетоне или кирпиче, предназначенная для прокладки элементов инженерных систем зданий (труб, кабеля и проч.). Делается с помощью зубил, перфораторов или штраборезов.', 'stroit.ru'),
('Штрипс', 'Стальная полоса (шириной 400 мм и толщиной 1,75—10 мм), используемая в качестве заготовки при производстве сварных труб.', 'stroit.ru'),
('Штроба', 'То же, что и штраба. Канавка в бетоне или кирпиче, предназначенная для пpокладки элементов инженерных систем зданий (труб, кабеля и проч.). Делается с помощью зубил, перфораторов или штраборезов.', 'stroit.ru'),
('Штукатурка', 'Отделочный или защитный слой, образованный при затвердевании строительных (штукатурных) растворов на поверхностях конструктивных элементов и частей зданий и сооружений. Различают монолитную (мокрую) штукатурку, выполняемую нанесением на поверхность штукатурного раствора, и сухую, когда поверхность облицовывается готовыми крупноразмерными листами (плитами) заводского изготовления. Штукатурка служит: - для выравнивания поверхностей ограждающих конструкций, с целью подготовки их к последующей отделке; - защиты от атмосферных воздействий (например, при отделке фасадов зданий); - защиты от иных вредных факторов (огнезащитные, звукопоглощающие, рентгенозащитные и др. штукатурки).', 'stroit.ru'),
('Штукатурка', 'Отделочный материал, получаемый путем смешения в определенной пропорции вяжущих веществ (цемент, известь, гипс и т. п.), песка и воды.', 'stroit.ru'),
('Штукатурка венецианская', 'Штукатурка венецианская – прозрачная штукатурка из мраморной муки. При нанесении создает эффект мрамора. Ее основой являются тонкодисперсные частицы (пудра) естественных минералов (мрамора, извести, гипса) и связующее вещество (чаще всего на полимерной или акриловой основе).', 'stroit.ru'),
('Штуковый рельеф', 'Штуковый рельеф - гипсовые лепные украшения на поверхности стен.', 'stroit.ru'),
('Штульп', 'Пластиковый профиль подобно импосту, служащий для соединения двух оконных створок. Но, в отличие от импоста, в штульповом соединении одна створка, не имея оконной ручки, при открывании зависима от другой (т. е. ее можно открыть, лишь открыв основную створку с ручкой). Преимущества штульпового соединения перед импостом заключается в том, что при полностью открытых двух створках, проем полностью свободен, поскольку в отличие от импоста штульп крепится не к раме, а к створке.', 'stroit.ru'),
('Штуцер', 'Деталь трубопровода. Втулка, один из концов которой имеет внутреннюю или наружную резьбу для крепления к различным емкостям или другим трубопроводам. Также штуцером называют отрезок трубы небольшого диаметра для выпуска воды или воздуха, а также отбора жидкости из трубопровода с целью измерения её давления.', 'stroit.ru'),
('Шунгизит', 'Шунгизит - искусственный пористый материал, получаемый при обжиге шунгитсодержащих пород. Шунгизит используется в качестве заполнителя для легких бетонов (шунгизитобетон) и в качестве теплоизоляционной засыпки.', 'stroit.ru'),
('Шунгит', 'Шунгит - горные породы докембрийского возраста, содержащие большое количество метаморфизованного органического вещества. Иногда шунгит называют «аспидные сланцы». Шунгит используются для получения шунгизита, отличаются высокой химической стойкостью, достаточно высоким сопротивлением истиранию, морозостойкостью.', 'stroit.ru'),
('Шунт', 'Электрический проводник (или магнитопровод), присоединяемый параллельно электрической (магнитной) цепи для ответвления части электрического тока (магнитного потока), при нежелательности или невозможности пропустить весь ток (поток) через данную цепь. Например, шунты используются для расширения пределов измерений амперметров. Шунты. изготовляют в виде пластин, лент, проволоки.', 'stroit.ru'),
('Шуруп', 'Металлический стержень цилиндрической или конической формы с винтовой нарезкой и заборным конусом в нижней части для лучшего ввинчивания. При ввертывании в дерево или иной мягкий материал, деформируя его, он сам формирует резьбу. Головку шурупа обычно выполняют сферической или плоской (потайной); шлиц на головке — прямым или крестообразным .', 'stroit.ru'),
('Шуруп потайной', 'Шуруп (см. шуруп) с потайной головкой', 'stroit.ru'),
('Шурф', 'Вертикальная (реже наклонная) горная выработка, проходимая с земной поверхности для разведки полезных ископаемых, организации вентиляции и водоотлива, а также транспортирования материалов, спуска и подъема людей и т.д.', 'stroit.ru'),
('Щебень', 'Щебень - рыхлая обломочная порода из неокатанных обломков горных пород, шлаков и т. д. размером от 10 до 100 мм. Щебень может иметь как природное, так и искусственное происхождение.', 'stroit.ru'),
('Щека', 'Щека - передняя и задняя плоскости арки.', 'stroit.ru'),
('Щепа', 'Щепа - полуфабрикат, получаемый путем измельчения древесного сырья. Различают щепу технологическую, зеленую (содержит примесь листьев и коры) и топливную. Технологическая щепа используется для производства древесноволокнистых и древесностружечных плит.', 'stroit.ru'),
('Щипец', 'Щипец - верхняя часть торцовой стены здания, ограниченная скатами крыши. В отличие от фронтона щипец не отделяется карнизом от плоскости всей стены.', 'stroit.ru'),
('Эбониты', 'Продукты вулканизации каучука большими количествами серы (до 50% от массы каучука). Образующийся твердый материал хорошо поддается механической обработке, негигроскопичен, газонепроницаем, стоек к действию растворов кислот, оснований, солей, растительных и животных жиров, но в отличие от имеющей сходную технологию изготовдения резины не проявляет высокоэластичных свойств. Эбониты применяют в производстве электроизоляционных деталей приборов.', 'stroit.ru'),
('Эклектизм', 'Эклектизм - формальное, механическое использование в композиции и художественной отделке зданий элементов стилей прошлых эпох.', 'stroit.ru'),
('Экседра', 'Экседра – полукруглая глубокая ниша в здании или отдельное полуоткрытое полукруглое сооружение.', 'stroit.ru'),
('Экстерьер', 'Внешний облик здания.', 'stroit.ru'),
('Экструдированный', 'То же что экструзионный (см. «экструзионный»). Определение «экструдированный» обычно применяется к пенополистеролу. Технология производства экструдированного пенополистерола состоит из следующих этапов: приготовление сухой вспенивающейся композиции, состоящей из полистирола и технологических добавок; ее дозирование в зону загрузки экструдера; непрерывная переработка в экструдере; выдавливание и вспенивание материала на выходе из экструзионной головки; калибрование и термостатирование плиты; охлаждение и стабилизация размеров плиты; отрезка плиты по длине на резательном устройстве. Плиты из экструдированного пенополистирола обладают одним из самых низких коэффициентов теплопроводности среди широко применяемых в строительстве утеплителей. Кроме того, преимуществами плит из экструдированного пенополистирола являются высокая механическая прочность и практически нулевое водопоглощение, что позволяет эффективно использовать их для утепления фундаментов, подвалов, стен, плоских кровель, устройства теплых полов (в том числе промышленных), теплоизоляции авто- и железнодорожного полотна и т. д.', 'stroit.ru'),
('Экструзионный', 'Полученный методом экструзии. Т. е. изделия (пленки, листы, трубы и т.д.) из полимерных или иных материалов, произведенные посредством выдавливания материала через канал головки экструдера (матрица с отверстием).', 'stroit.ru'),
('Экструзия', 'Формование изделий путем выдавливания материала через матрицу с отверстием соответствующего сечения.', 'stroit.ru'),
('Эластобит', 'Рулонный наплавляемый кровельный и гидроизоляционный материал широкого применения. Предназначается для устройства кровельного ковра зданий различного типа и назначения, а также гидроизоляции фундаментов, мостов, тоннелей. Сохраняет эластичность (т. е. возможность укладки) при температуре до минус 15 град. C, теплостойкость – до +85 град. C. Эластобит получают путем двухстороннего нанесения на негниющую основу (стеклохолст, комбинированная ткань, каркасная стеклоткань РАТЛ, полиэстер) СБС-модифицированного полимерно-битумного вяжущего. Марка «П» применяется для устройства нижних слоев кровельного ковра и гидроизоляции. Марка «К» – для устройства верхних слоев кровельного покрытия. Эластобит наплавляется с помощью пропановой или дизельной горелки, приклеивается мастикой, либо закрепляется механически. В зависимости от технических решений материал может быть закреплен полностью, частично или оставлен свободно лежащим.', 'stroit.ru'),
('Электрическая сеть', 'Совокупность электроустановок для передачи и распределения электроэнергии, состоящая из подстанций, распределительных устройств, токопроводов, воздушных и кабельных линий электропередачи.', 'stroit.ru'),
('Электрическая цепь', 'Совокупность электрооборудования, соединенного проводами и кабелями, через которое может протекать электрический ток.', 'stroit.ru'),
('Электрокорунд', 'Синтетический корунд электродуговой плавки, искусственный абразивный материал. Состав: окись алюминия, окислы кремния, титана, кальция и железа. Технология производства: плавка глиноземсодержащего сырья в дуговых печах с последующей кристаллизацией расплава. В зависимости от содержания глинозема и особенностей технологии изготовления различают несколько разновидностей: нормальный, белый, легированный (хромистый, титанистый, циркониевый). Электрокорундовые зерна, порошки и микропорошки широко используются при производстве абразивных материалов. Кроме того, электрокорунд применяется для изготовления огнеупорных, химически инертных изделий, керамических деталей электровакуумных приборов, изоляторов, в технологических процессах черной металлургии.', 'stroit.ru'),
('Электрооборудование', 'Любое оборудование, предназначенное для производства, преобразования, передачи, распределения или потребления электрической энергии. Например: машины, трансформаторы, аппараты, измерительные приборы, устройства защиты, кабельная продукция, электроприемники.', 'stroit.ru'),
('Электропроводка', 'Совокупность электрических проводов и кабелей с относящимися к ним креплениями.', 'stroit.ru'),
('Электропроводка открытая', 'Электропроводка, проложенная по поверхностям стен, потолков, по фермам и по другим строительным конструкциям зданий и сооружений.', 'stroit.ru'),
('Электропроводка скрытая', 'Электропроводка, проложенная внутри конструктивных элементов зданий и сооружений.', 'stroit.ru'),
('Электрорубанок', 'Электроинструмент, предназначенный для строгания древесины различных пород вдоль и поперек волокон. Состоит из корпуса с ручкой-упором; передней опоры с механизмом регулировки глубины строгания; электродвигателя; редуктора и кабеля со штепсельной вилкой. Рабочим органом электрорубанка является ножевой барабан с двумя съемными ножами, расположенный внутри корпуса на опорной плите (лыже).', 'stroit.ru'),
('Электроустановка', 'Любое сочетание взаимосвязанного электрооборудования в пределах данного пространства или помещения.', 'stroit.ru'),
('ЭЛУР', 'Высокотехнологичный двухкомпонентный полиуретановый герметик (основная паста и отверждающей компонент), предназначенный для долговременной герметизации стыков и швов строящихся и эксплуатируемых зданий и сооружений, а также для текущего и капитального ремонта межпанельных швов. Полимеризуется при естественной температуре окружающей среды, превращаясь в резиноподобный, эластичный, водостойкий, безусадочный материал.', 'stroit.ru'),
('Элювий', 'Элювий - продукт выветривания горных пород, остающийся на месте своего образования.', 'stroit.ru'),
('Эмали', 'Эмали - суспензии пигментов с перхлорвиниловыми, поливинилхлоридными и прочими смолами, а также другими добавками. Время высыхания эмали 1–3 часа.', 'stroit.ru'),
('Эмаль-провод', 'Провода круглого и прямоугольного сечения с медной жилой и изоляцией на основе эмальлаков, предназначенные для обмоток электрических машин, аппаратов, измерительных и прочих приборов.', 'stroit.ru'),
('Эмульсии', 'Эмульсии - группа связующих и разбавителей для водных и лакокрасочных составов, улучшающих их качество и способствующих экономии олифы. Применяются эмульсии вместо олифы для приготовления шпаклевок, грунтовок. Битумные и дегтевые эмульсии используют для огрунтовки оснований под гидроизоляцию, для приклеивания рулонных кровельных материалов, при изготовлении асфальтовых растворов.', 'stroit.ru'),
('Эмульсия', 'Эмульсия – двухфазная дисперсионная система, в который чаще всего дисперсионной средой является вода, а дисперсионной фазой — органические жидкости, в том числе битумы, полимерные смолы. Эмульсия применяется в производстве водоэмульсионных красок.', 'stroit.ru'),
('Эмульсолы', 'Многокомпонентные составы на основе минеральных масел и поверхностно-активных веществ (сульфонатов, оксиэтилированных алкилфенолов, алифатических кислот и др.). При смешении с водой образуют устойчивые коллоидно-дисперсные системы. Могут содержать спирты, полиэтиленгликоли, присадки, бактерициды, воду. Используются в качестве разделительной эмульсии для смазывания форм и опалубки при производстве бетонных изделий, а также при изготовлении многофункциональных смазочно-охлаждающих жидкостей.', 'stroit.ru'),
('Эпистиль', 'Эпистиль - нижняя деталь балочной конструкции. Эпистиль опирается непосредственно на опору.', 'stroit.ru'),
('Эркер', 'Полукруглый, треугольный или многогранный остекленный выступ в стене здания.', 'stroit.ru'),
('Эстакада', 'Надземное или надводное сооружение мостового типа, служащее для пропуска транспортных средств и пешеходов, обеспечения погрузочно-разгрузочных работ, прокладки инженерных коммуникаций. Обычно состоит из значительного числа пролетов однотипной конструкции. Материалами для строительства эстакад служат железобетон, сталь или дерево. На строительных объектах их используют для транспортировки строительных материалов и изделий и перемещения подъемных и монтажных кранов.', 'stroit.ru'),
('Этилацетат', 'Уксусноэтиловый эфир CH3COOC2H5. Бесцветная легколетучая жидкость с фруктовым запахом. Хорошо растворим в спирте, эфире, хлороформе; мало растворим в воде. Горюч, в определенных концентрациях в воздухе взрывоопасен (3-9 %). Применяется как растворитель в лакокрасочной промышленности.', 'stroit.ru'),
('Этиленгликоль', 'Простейший гликоль, бесцветная вязкая жидкость со сладким вкусом. Смешивается во всех соотношениях с водой, спиртом, ацетоном, плохо растворим в эфире, не растворим в хлороформе, алифатических и ароматических углеводородах. Обладает способностью существенно понижать температуру замерзания воды (например, при 40%-ном содержании – до минус 25O C. что широко используется для приготовления антифризов. Производные этиленгликоля используются при производстве растворителей, взрывчатых веществ, синтетических волокон.', 'stroit.ru'),
('Янтарь', 'Янтарь - окаменевшая ископаемая смола древних хвойных деревьев.', 'stroit.ru'),
('Ярус', 'В архитектуре – одна из частей (секций) сооружения, планировочно или конструктивно повторяющихся и расположенных друг над другом.', 'stroit.ru'),
('Яшма', 'Яшма - плотная осадочная порода, состоящая из зерен кварца и различных примесей.', 'stroit.ru'),
('Яшмит', 'Минерал, в виде валунов применяется как элемент ландшафтного дизайна, для создания композиций из цветных камней в сочетании с альпийскими растениями, а также для создания альпийских горок.', 'stroit.ru')
